import{_ as o,o as l,c as e,aj as r}from"./chunks/framework.C7QJCfd1.js";const h=JSON.parse('{"title":"Houdini 常用节点和技术概述","description":"","frontmatter":{},"headers":[],"relativePath":"全流程美术/Houdini/Houdini总览/Houdini总览.md","filePath":"全流程美术/Houdini/Houdini总览/Houdini总览.md"}'),t={name:"全流程美术/Houdini/Houdini总览/Houdini总览.md"};function a(n,i,s,d,c,u){return l(),e("div",null,[...i[0]||(i[0]=[r('<h1 id="houdini-常用节点和技术概述" tabindex="-1">Houdini 常用节点和技术概述 <a class="header-anchor" href="#houdini-常用节点和技术概述" aria-label="Permalink to &quot;Houdini 常用节点和技术概述&quot;">​</a></h1><p>Houdini 提供了多种节点和技术用于处理不同类型的模拟和操作，以下是对常用节点和技术的总结：</p><h2 id="_1-sop-surface-operators" tabindex="-1">1. <strong>SOP（Surface Operators）</strong> <a class="header-anchor" href="#_1-sop-surface-operators" aria-label="Permalink to &quot;1. **SOP（Surface Operators）**&quot;">​</a></h2><ul><li><strong>作用</strong>：操作几何体，用于创建、修改和处理3D物体的几何形状。</li><li><strong>应用</strong>：建模、变形、切割、合并、布尔运算等。</li><li><strong>常见节点</strong>： <ul><li><code>Transform SOP</code></li><li><code>PolyExtrude SOP</code></li><li><code>Subdivide SOP</code></li></ul></li></ul><h2 id="_2-pop-particle-operators" tabindex="-1">2. <strong>POP（Particle Operators）</strong> <a class="header-anchor" href="#_2-pop-particle-operators" aria-label="Permalink to &quot;2. **POP（Particle Operators）**&quot;">​</a></h2><ul><li><strong>作用</strong>：用于粒子系统的创建和控制，处理粒子的生命周期、行为、速度等。</li><li><strong>应用</strong>：烟雾、火焰、尘土、雨水等效果的模拟。</li><li><strong>常见节点</strong>： <ul><li><code>POP Source</code></li><li><code>POP Force</code></li><li><code>POP Wind</code></li><li><code>POP Drag</code></li></ul></li></ul><h2 id="_3-vex-vector-expression" tabindex="-1">3. <strong>VEX（Vector Expression）</strong> <a class="header-anchor" href="#_3-vex-vector-expression" aria-label="Permalink to &quot;3. **VEX（Vector Expression）**&quot;">​</a></h2><ul><li><strong>作用</strong>：一种编程语言，用于编写自定义代码，控制几何体和模拟的行为。</li><li><strong>应用</strong>：自定义着色器、粒子行为、变形等。</li><li><strong>常见用途</strong>： <ul><li>自定义着色器（<code>Shader</code>）</li><li>粒子运动（<code>POP</code>）</li><li>变换（<code>Wrangle</code>）</li></ul></li></ul><h2 id="_4-vellum" tabindex="-1">4. <strong>Vellum</strong> <a class="header-anchor" href="#_4-vellum" aria-label="Permalink to &quot;4. **Vellum**&quot;">​</a></h2><ul><li><strong>作用</strong>：用于柔体物理模拟，处理布料、弹性物体、软体、皮肤等。</li><li><strong>应用</strong>：模拟布料、绳索、皮肤等柔性物体的动态行为。</li><li><strong>常见节点</strong>： <ul><li><code>Vellum Cloth</code></li><li><code>Vellum Solver</code></li><li><code>Vellum Constraints</code></li><li><code>Vellum Pin</code></li></ul></li></ul><h2 id="_5-rbd-rigid-body-dynamics" tabindex="-1">5. <strong>RBD（Rigid Body Dynamics）</strong> <a class="header-anchor" href="#_5-rbd-rigid-body-dynamics" aria-label="Permalink to &quot;5. **RBD（Rigid Body Dynamics）**&quot;">​</a></h2><ul><li><strong>作用</strong>：用于刚体物理模拟，处理物体间的碰撞、破碎等交互。</li><li><strong>应用</strong>：破碎物体（如玻璃、石块）、碰撞、堆叠等效果。</li><li><strong>常见节点</strong>： <ul><li><code>RBD Object</code></li><li><code>RBD Glue</code></li><li><code>RBD Solver</code></li><li><code>RBD Configure</code></li></ul></li></ul><h2 id="_6-flip-fluid-implicit-particle" tabindex="-1">6. <strong>FLIP（Fluid Implicit Particle）</strong> <a class="header-anchor" href="#_6-flip-fluid-implicit-particle" aria-label="Permalink to &quot;6. **FLIP（Fluid Implicit Particle）**&quot;">​</a></h2><ul><li><strong>作用</strong>：流体模拟技术，结合粒子和流体网格的优点，用于液体的模拟。</li><li><strong>应用</strong>：模拟水、液体溅射、波浪等效果。</li><li><strong>常见节点</strong>： <ul><li><code>Flip Fluid</code></li><li><code>Flip Solver</code></li><li><code>Particle Fluid</code></li><li><code>Flip Object</code></li></ul></li></ul><h2 id="_7-pyro-fire-smoke" tabindex="-1">7. <strong>Pyro（Fire &amp; Smoke）</strong> <a class="header-anchor" href="#_7-pyro-fire-smoke" aria-label="Permalink to &quot;7. **Pyro（Fire &amp; Smoke）**&quot;">​</a></h2><ul><li><strong>作用</strong>：用于火焰、烟雾、爆炸等气体模拟。</li><li><strong>应用</strong>：模拟火焰、烟雾、爆炸、枪击火花等。</li><li><strong>常见节点</strong>： <ul><li><code>Pyro Solver</code></li><li><code>Gas Field VOP</code></li><li><code>Smoke</code></li><li><code>Fire</code></li><li><code>Explosion</code></li></ul></li></ul><hr><h2 id="总结" tabindex="-1">总结 <a class="header-anchor" href="#总结" aria-label="Permalink to &quot;总结&quot;">​</a></h2><ul><li><strong>SOP</strong>：几何操作。</li><li><strong>POP</strong>：粒子系统。</li><li><strong>VEX</strong>：自定义编程语言，提供高效定制。</li><li><strong>Vellum</strong>：柔性物体的模拟。</li><li><strong>RBD</strong>：刚体物理模拟。</li><li><strong>FLIP</strong>：液体模拟。</li><li><strong>Pyro</strong>：火焰、烟雾和爆炸等气体模拟。</li></ul>',19)])])}const P=o(t,[["render",a]]);export{h as __pageData,P as default};
