import{_ as e,o as r,c as t,a5 as l}from"./chunks/framework.DpSpZ5OU.js";const g=JSON.parse('{"title":"Unreal Engine PCG 节点功能详解","description":"","frontmatter":{},"headers":[],"relativePath":"全流程美术/Houdini/PCGforUnreal/PCGforUnreal.md","filePath":"全流程美术/Houdini/PCGforUnreal/PCGforUnreal.md"}'),o={name:"全流程美术/Houdini/PCGforUnreal/PCGforUnreal.md"};function i(n,a,s,h,u,d){return r(),t("div",null,[...a[0]||(a[0]=[l('<h1 id="unreal-engine-pcg-节点功能详解" tabindex="-1">Unreal Engine PCG 节点功能详解 <a class="header-anchor" href="#unreal-engine-pcg-节点功能详解" aria-label="Permalink to &quot;Unreal Engine PCG 节点功能详解&quot;">​</a></h1><h2 id="一、sampler-采样器" tabindex="-1">一、Sampler（采样器） <a class="header-anchor" href="#一、sampler-采样器" aria-label="Permalink to &quot;一、Sampler（采样器）&quot;">​</a></h2><blockquote><p>用于从不同源数据中采样点或网格</p></blockquote><h3 id="_1-surfacesampler" tabindex="-1">1. SurfaceSampler <a class="header-anchor" href="#_1-surfacesampler" aria-label="Permalink to &quot;1. SurfaceSampler&quot;">​</a></h3><ul><li>​<strong>功能</strong>：在表面（如地形表面）上生成采样点</li><li>​<strong>用法</strong>：配合地形/静态网格使用，控制采样密度和分布模式</li></ul><h3 id="_2-meshsampler" tabindex="-1">2. MeshSampler <a class="header-anchor" href="#_2-meshsampler" aria-label="Permalink to &quot;2. MeshSampler&quot;">​</a></h3><ul><li>​<strong>功能</strong>：从静态网格表面提取点数据</li><li>​<strong>用法</strong>：通过网格顶点或三角面生成采样点</li></ul><h3 id="_3-pointsampler" tabindex="-1">3. PointSampler <a class="header-anchor" href="#_3-pointsampler" aria-label="Permalink to &quot;3. PointSampler&quot;">​</a></h3><ul><li>​<strong>功能</strong>：生成随机分布的点数据</li><li>​<strong>用法</strong>：设置生成范围、密度和随机种子</li></ul><h3 id="_4-splinesampler" tabindex="-1">4. SplineSampler <a class="header-anchor" href="#_4-splinesampler" aria-label="Permalink to &quot;4. SplineSampler&quot;">​</a></h3><ul><li>​<strong>功能</strong>：沿样条线路径生成点</li><li>​<strong>用法</strong>：控制沿样条的分布间隔/密度</li></ul><h3 id="_5-volumesampler" tabindex="-1">5. VolumeSampler <a class="header-anchor" href="#_5-volumesampler" aria-label="Permalink to &quot;5. VolumeSampler&quot;">​</a></h3><ul><li>​<strong>功能</strong>：在体积空间内生成三维点阵</li><li>​<strong>用法</strong>：定义体积边界和三维分布参数</li></ul><h2 id="二、spawner-生成器" tabindex="-1">二、Spawner（生成器） <a class="header-anchor" href="#二、spawner-生成器" aria-label="Permalink to &quot;二、Spawner（生成器）&quot;">​</a></h2><blockquote><p>用于生成和初始化PCG元素</p></blockquote><h3 id="_1-createpoints" tabindex="-1">1. CreatePoints <a class="header-anchor" href="#_1-createpoints" aria-label="Permalink to &quot;1. CreatePoints&quot;">​</a></h3><ul><li>​<strong>功能</strong>：在指定位置创建基础点数据</li><li>​<strong>用法</strong>：设置初始生成位置和属性</li></ul><h3 id="_2-createpointsgrid" tabindex="-1">2. CreatePointsGrid <a class="header-anchor" href="#_2-createpointsgrid" aria-label="Permalink to &quot;2. CreatePointsGrid&quot;">​</a></h3><ul><li>​<strong>功能</strong>：创建规则网格状点阵</li><li>​<strong>用法</strong>：定义网格尺寸、间距和排列方式</li></ul><h3 id="_3-meshsockettopoints" tabindex="-1">3. MeshSocketToPoints <a class="header-anchor" href="#_3-meshsockettopoints" aria-label="Permalink to &quot;3. MeshSocketToPoints&quot;">​</a></h3><ul><li>​<strong>功能</strong>：将网格插槽转换为生成点</li><li>​<strong>用法</strong>：自动提取静态网格体上的Socket位置</li></ul><h3 id="_4-staticmeshspawner" tabindex="-1">4. StaticMeshSpawner <a class="header-anchor" href="#_4-staticmeshspawner" aria-label="Permalink to &quot;4. StaticMeshSpawner&quot;">​</a></h3><ul><li>​<strong>功能</strong>：基于点数据生成静态网格实例</li><li>​<strong>用法</strong>：关联静态网格资产和生成规则</li></ul><h3 id="_5-spawnactor" tabindex="-1">5. SpawnActor <a class="header-anchor" href="#_5-spawnactor" aria-label="Permalink to &quot;5. SpawnActor&quot;">​</a></h3><ul><li>​<strong>功能</strong>：生成带有Actor的实例</li><li>​<strong>用法</strong>：指定Actor蓝图和生成参数</li></ul><h2 id="三、density-密度控制" tabindex="-1">三、Density（密度控制） <a class="header-anchor" href="#三、density-密度控制" aria-label="Permalink to &quot;三、Density（密度控制）&quot;">​</a></h2><blockquote><p>处理点密度计算和转换</p></blockquote><h3 id="_1-densitynoise" tabindex="-1">1. DensityNoise <a class="header-anchor" href="#_1-densitynoise" aria-label="Permalink to &quot;1. DensityNoise&quot;">​</a></h3><ul><li>​<strong>功能</strong>：应用噪声图影响密度分布</li><li>​<strong>用法</strong>：控制噪声类型、缩放和强度</li></ul><h3 id="_2-densityremap" tabindex="-1">2. DensityRemap <a class="header-anchor" href="#_2-densityremap" aria-label="Permalink to &quot;2. DensityRemap&quot;">​</a></h3><ul><li>​<strong>功能</strong>：重新映射密度值范围</li><li>​<strong>用法</strong>：定义输入/输出曲线关系</li></ul><h3 id="_3-distancetodensity" tabindex="-1">3. DistanceToDensity <a class="header-anchor" href="#_3-distancetodensity" aria-label="Permalink to &quot;3. DistanceToDensity&quot;">​</a></h3><ul><li>​<strong>功能</strong>：将距离数据转换为密度值</li><li>​<strong>用法</strong>：常用于边缘衰减效果</li></ul><h3 id="_4-normaltodensity" tabindex="-1">4. NormalToDensity <a class="header-anchor" href="#_4-normaltodensity" aria-label="Permalink to &quot;4. NormalToDensity&quot;">​</a></h3><ul><li>​<strong>功能</strong>：根据表面法线调整密度</li><li>​<strong>用法</strong>：控制坡度相关的生成规则</li></ul><h2 id="四、filter-过滤器" tabindex="-1">四、Filter（过滤器） <a class="header-anchor" href="#四、filter-过滤器" aria-label="Permalink to &quot;四、Filter（过滤器）&quot;">​</a></h2><blockquote><p>点数据筛选与修剪</p></blockquote><h3 id="_1-densityfilter" tabindex="-1">1. DensityFilter <a class="header-anchor" href="#_1-densityfilter" aria-label="Permalink to &quot;1. DensityFilter&quot;">​</a></h3><ul><li>​<strong>功能</strong>：基于密度阈值过滤点</li><li>​<strong>用法</strong>：设置最小/最大密度范围</li></ul><h3 id="_2-filterbytag" tabindex="-1">2. FilterByTag <a class="header-anchor" href="#_2-filterbytag" aria-label="Permalink to &quot;2. FilterByTag&quot;">​</a></h3><ul><li>​<strong>功能</strong>：根据Actor标签筛选数据</li><li>​<strong>用法</strong>：匹配指定标签进行过滤</li></ul><h3 id="_3-selfpruning" tabindex="-1">3. SelfPruning <a class="header-anchor" href="#_3-selfpruning" aria-label="Permalink to &quot;3. SelfPruning&quot;">​</a></h3><ul><li>​<strong>功能</strong>：自动修剪过密点数据</li><li>​<strong>用法</strong>：设置最小间距阈值</li></ul><h3 id="_4-pointfilter" tabindex="-1">4. PointFilter <a class="header-anchor" href="#_4-pointfilter" aria-label="Permalink to &quot;4. PointFilter&quot;">​</a></h3><ul><li>​<strong>功能</strong>：基于自定义属性筛选点</li><li>​<strong>用法</strong>：通过表达式定义过滤条件</li></ul><h2 id="五、spatial-空间操作" tabindex="-1">五、Spatial（空间操作） <a class="header-anchor" href="#五、spatial-空间操作" aria-label="Permalink to &quot;五、Spatial（空间操作）&quot;">​</a></h2><blockquote><p>空间关系计算与数据处理</p></blockquote><h3 id="_1-boundsmodifier" tabindex="-1">1. BoundsModifier <a class="header-anchor" href="#_1-boundsmodifier" aria-label="Permalink to &quot;1. BoundsModifier&quot;">​</a></h3><ul><li>​<strong>功能</strong>：修改点数据的边界范围</li><li>​<strong>用法</strong>：扩展/收缩生成区域边界</li></ul><h3 id="_2-createspline" tabindex="-1">2. CreateSpline <a class="header-anchor" href="#_2-createspline" aria-label="Permalink to &quot;2. CreateSpline&quot;">​</a></h3><ul><li>​<strong>功能</strong>：生成样条曲线数据</li><li>​<strong>用法</strong>：创建路径控制的基础曲线</li></ul><h3 id="_3-difference" tabindex="-1">3. Difference <a class="header-anchor" href="#_3-difference" aria-label="Permalink to &quot;3. Difference&quot;">​</a></h3><ul><li>​<strong>功能</strong>：执行空间差集运算</li><li>​<strong>用法</strong>：移除与其他体积重叠的部分</li></ul><h3 id="_4-getlandscapedata" tabindex="-1">4. GetLandscapeData <a class="header-anchor" href="#_4-getlandscapedata" aria-label="Permalink to &quot;4. GetLandscapeData&quot;">​</a></h3><ul><li>​<strong>功能</strong>：提取地形数据</li><li>​<strong>用法</strong>：获取地形高度/图层信息</li></ul><h3 id="_5-worldrayhitquery" tabindex="-1">5. WorldRayHitQuery <a class="header-anchor" href="#_5-worldrayhitquery" aria-label="Permalink to &quot;5. WorldRayHitQuery&quot;">​</a></h3><ul><li>​<strong>功能</strong>：执行射线检测查询</li><li>​<strong>用法</strong>：检测点与场景的碰撞关系</li></ul><h2 id="高级功能节点" tabindex="-1">高级功能节点 <a class="header-anchor" href="#高级功能节点" aria-label="Permalink to &quot;高级功能节点&quot;">​</a></h2><h3 id="_1-transformpoints" tabindex="-1">1. TransformPoints <a class="header-anchor" href="#_1-transformpoints" aria-label="Permalink to &quot;1. TransformPoints&quot;">​</a></h3><ul><li>​<strong>功能</strong>：对点数据进行空间变换</li><li>​<strong>用法</strong>：应用位移/旋转/缩放变换</li></ul><h3 id="_2-gather-merge-numon" tabindex="-1">2. Gather/Merge/Numon <a class="header-anchor" href="#_2-gather-merge-numon" aria-label="Permalink to &quot;2. Gather/Merge/Numon&quot;">​</a></h3><ul><li>​<strong>功能</strong>：数据聚合与合并操作</li><li>​<strong>用法</strong>：合并多个数据流并统计数量</li></ul><h3 id="_3-distancetoneighbors" tabindex="-1">3. DistanceToNeighbors <a class="header-anchor" href="#_3-distancetoneighbors" aria-label="Permalink to &quot;3. DistanceToNeighbors&quot;">​</a></h3><ul><li>​<strong>功能</strong>：计算邻近点距离</li><li>​<strong>用法</strong>：实现基于邻近关系的分布控制</li></ul><blockquote><p>提示：节点名称前带<code>*</code>表示需要配合特定数据类型使用（如<code>*GetActorData</code>）</p></blockquote>',65)])])}const m=e(o,[["render",i]]);export{g as __pageData,m as default};
