import{_ as r,o as t,c as o,aj as i}from"./chunks/framework.C7QJCfd1.js";const H=JSON.parse('{"title":"REBELWAY - PYTHON FOR HOUDINI ARTIST","description":"","frontmatter":{},"headers":[],"relativePath":"全流程美术/Houdini/PYTHON_FOR_HOUDINI_ARTIST/PYTHON_FOR_HOUDINI_ARTIST.md","filePath":"全流程美术/Houdini/PYTHON_FOR_HOUDINI_ARTIST/PYTHON_FOR_HOUDINI_ARTIST.md"}'),e={name:"全流程美术/Houdini/PYTHON_FOR_HOUDINI_ARTIST/PYTHON_FOR_HOUDINI_ARTIST.md"};function n(s,a,l,_,T,I){return t(),o("div",null,[...a[0]||(a[0]=[i('<h1 id="rebelway-python-for-houdini-artist" tabindex="-1">REBELWAY - PYTHON FOR HOUDINI ARTIST <a class="header-anchor" href="#rebelway-python-for-houdini-artist" aria-label="Permalink to &quot;REBELWAY - PYTHON FOR HOUDINI ARTIST&quot;">​</a></h1><p><a href="https://www.rebelway.net/houdini-for-3d-artists" target="_blank" rel="noreferrer">HOUDINI FOR 3D ARTISTS</a></p><h3 id="课程核心目标" tabindex="-1">课程核心目标 <a class="header-anchor" href="#课程核心目标" aria-label="Permalink to &quot;课程核心目标&quot;">​</a></h3><p>学会用HDAs/程序化工具做环境资产（如货架、步道），掌握Solaris的USD导出+Karma渲染，结合Copernicus（程序化框架）、Megascans纹理实现高效材质控制。 这是一门基于Houdini的环境创建课程，核心是用HDAs（ houdini数字资产）和程序化工具搭建、修改3D场景，适配Solaris渲染和Karma引擎，课程分8周逐步推进：</p><h3 id="每周内容总结" tabindex="-1">每周内容总结 <a class="header-anchor" href="#每周内容总结" aria-label="Permalink to &quot;每周内容总结&quot;">​</a></h3><ol><li><strong>第1周</strong>：项目概述+技术思路，完成项目初始化，搭建工作流基础。</li><li><strong>第2周</strong>：用程序化建模做平台主体，制作可复用的货架HDA；为Solaris渲染做场景准备，用HDA+Megascans生成材质。</li><li><strong>第3周</strong>：步道系统搭建，用曲线生成基础步道，建立自动生成楼梯的工作流。</li><li><strong>第4周</strong>：完善步道HDA，学习“几何体附着到原始元素”的实例化方式，替代传统“点复制”，实现步道元素的随机化。</li><li><strong>第5周</strong>：基于距离/角度生成楼梯，扩展HDA以生成桥梁、拱门，完成步道系统的USD导出准备。</li><li><strong>第6周</strong>：制作其他环境资产，利用已有工作流快速搭建高细节、可渲染的几何体。</li><li><strong>第7周</strong>：用程序化纹理工具，学习纹理分层、操作组合，导入模型/属性并配置材质参数以强化细节。</li><li><strong>第8周</strong>：导入动画角色+相机到Solaris，完成环境的材质分配、分镜头灯光与渲染设置。</li></ol>',6)])])}const d=r(e,[["render",n]]);export{H as __pageData,d as default};
