import{_ as i,o as a,c as t,a6 as r}from"./chunks/framework.DZKVy2Mg.js";const e="/Art_web_public/assets/image.DZCpv9mB.png",_=JSON.parse('{"title":"OverRig 用户手册","description":"","frontmatter":{"readingTime":"6 min read"},"headers":[],"relativePath":"全流程美术/动画/OverRig/OverRig.md","filePath":"全流程美术/动画/OverRig/OverRig.md"}'),o={name:"全流程美术/动画/OverRig/OverRig.md"};function h(n,l,u,s,d,c){return a(),t("div",null,[...l[0]||(l[0]=[r('<h1 id="overrig-用户手册" tabindex="-1">OverRig 用户手册 <a class="header-anchor" href="#overrig-用户手册" aria-label="Permalink to &quot;OverRig 用户手册&quot;">​</a></h1><p>OverRig 是一套用于打磨动画（包括手动制作和动作捕捉获得的动画）的工具集</p><h2 id="相关视频链接" tabindex="-1">相关视频链接 <a class="header-anchor" href="#相关视频链接" aria-label="Permalink to &quot;相关视频链接&quot;">​</a></h2><ul><li><a href="https://youtu.be/YlxmJX4KJm8" target="_blank" rel="noreferrer">完整版指南（英语）</a></li><li><a href="https://youtu.be/PgtqwtHpjiQ" target="_blank" rel="noreferrer">补充内容（版本6.0）</a></li></ul><hr><h2 id="版本6-0新增功能" tabindex="-1">版本6.0新增功能 <a class="header-anchor" href="#版本6-0新增功能" aria-label="Permalink to &quot;版本6.0新增功能&quot;">​</a></h2><ul><li><strong>剑动画润色工具（Sword animation polishing tool）</strong><ul><li>通过手工或运动捕捉（mocap）数据制作</li><li>简化动画制作流程</li><li>解决动画中的复杂技术问题</li><li>可不依赖Rigger添加特定功能</li><li>消除IK-FK切换</li><li>其他多种功能</li></ul></li></ul><hr><h2 id="动画脚本总览" tabindex="-1">动画脚本总览 <a class="header-anchor" href="#动画脚本总览" aria-label="Permalink to &quot;动画脚本总览&quot;">​</a></h2><ul><li><strong>动画设计思路：</strong><ul><li>草绘动画，重点突出良好的动态效果</li><li>跳过互动或粗略标记</li><li>使用脚本进行润色</li></ul></li></ul><hr><h2 id="基础脚本-overrig动画方法" tabindex="-1">基础脚本：OverRig动画方法 <a class="header-anchor" href="#基础脚本-overrig动画方法" aria-label="Permalink to &quot;基础脚本：OverRig动画方法&quot;">​</a></h2><h3 id="_1-knot-结点" tabindex="-1">1. KNOT（结点） <a class="header-anchor" href="#_1-knot-结点" aria-label="Permalink to &quot;1. KNOT（结点）&quot;">​</a></h3><ul><li>为选中对象创建locator，将其在世界坐标中的动画转移到locator上，后将对象“悬挂”到locator。</li><li>支持多对象同时操作。</li><li>在专用时间线上应用时，locator会有“attach”通道，动画效果通过调节权重控制。</li><li>常用用途： <ul><li>固定控件抖动</li><li>脚步滑动修正</li><li>转换到渐进循环</li><li>重置或移动控制器</li><li>层级调整</li></ul></li></ul><hr><h3 id="_2-knotoffset" tabindex="-1">2. KNOTOFFSET <a class="header-anchor" href="#_2-knotoffset" aria-label="Permalink to &quot;2. KNOTOFFSET&quot;">​</a></h3><ul><li>创建locator，移动或旋转到目标位置，点击空白区域解除选择。</li><li>将相对动画转移到locator，改变pivot点。</li><li>支持多对象操作。</li><li>在时间线上应用时，locator含“attach”通道，调节权重控制动画。</li><li>常用用途： <ul><li>武器绕轴旋转</li><li>避免gimbal锁</li><li>脚部旋转与脚尖点保持一致</li><li>环境互动</li></ul></li></ul><hr><h3 id="_3-knotorient" tabindex="-1">3. KNOTORIENT <a class="header-anchor" href="#_3-knotorient" aria-label="Permalink to &quot;3. KNOTORIENT&quot;">​</a></h3><ul><li>创建locator，控制对象的旋转动画，locator追随对象。</li><li>支持多对象操作。</li><li>时间线上应用后，locator含“attach”通道。</li><li>常用用途： <ul><li>手指旋转独立</li><li>头部旋转不影响躯干</li><li>独立尾巴动画</li></ul></li></ul><hr><h3 id="_4-smart-object-智能对象" tabindex="-1">4. Smart Object（智能对象） <a class="header-anchor" href="#_4-smart-object-智能对象" aria-label="Permalink to &quot;4. Smart Object（智能对象）&quot;">​</a></h3><ul><li>一组基础节点，能根据原始对象逐帧烘焙动画。</li><li>适合动画初期草绘阶段。</li><li>作用： <ul><li>改变控件空间</li><li>在不烘焙的情况下进行草图制作</li><li>保持原动画关键帧</li></ul></li></ul><hr><h3 id="_5-parent-父子关系" tabindex="-1">5. Parent（父子关系） <a class="header-anchor" href="#_5-parent-父子关系" aria-label="Permalink to &quot;5. Parent（父子关系）&quot;">​</a></h3><ul><li>强大脚本集，用于对象及角色互动。</li></ul><h4 id="_5-1-inside-内部" tabindex="-1">5.1 INSIDE（内部） <a class="header-anchor" href="#_5-1-inside-内部" aria-label="Permalink to &quot;5.1 INSIDE（内部）&quot;">​</a></h4><ul><li>让选中对象嵌套在最后选择的对象内（位置、旋转动画同时保存）</li><li>控制： <ul><li>将物体挂在手上（如苹果）</li><li>让角色抓取敌人（如喉咙）</li></ul></li></ul><h4 id="_5-2-world-世界" tabindex="-1">5.2 WORLD（世界） <a class="header-anchor" href="#_5-2-world-世界" aria-label="Permalink to &quot;5.2 WORLD（世界）&quot;">​</a></h4><ul><li>将对象变换到世界坐标系，保持动画</li><li>控制： <ul><li>放下苹果</li><li>转换动画</li></ul></li></ul><h4 id="_5-3-swap-交换" tabindex="-1">5.3 SWAP（交换） <a class="header-anchor" href="#_5-3-swap-交换" aria-label="Permalink to &quot;5.3 SWAP（交换）&quot;">​</a></h4><ul><li>交换两个对象在层级中的位置</li><li>支持嵌套交换</li><li>使用： <ul><li>交换手势与物体位置</li></ul></li></ul><h4 id="_5-4-across-跨越" tabindex="-1">5.4 Across（跨越） <a class="header-anchor" href="#_5-4-across-跨越" aria-label="Permalink to &quot;5.4 Across（跨越）&quot;">​</a></h4><ul><li>将对象烘焙到KNOT并放入最后选择的对象中</li><li>快速替代KNOT+PARENT_INSIDE</li><li>支持时间范围操作</li></ul><hr><h3 id="_6-attach-附着" tabindex="-1">6. ATTACH（附着） <a class="header-anchor" href="#_6-attach-附着" aria-label="Permalink to &quot;6. ATTACH（附着）&quot;">​</a></h3><ul><li>在KNOT上添加“attach”通道控制</li><li>支持多KNOT控制单个对象</li><li>作用： <ul><li>复杂动画调控</li></ul></li></ul><hr><h3 id="_7-aim-瞄准" tabindex="-1">7. AIM（瞄准） <a class="header-anchor" href="#_7-aim-瞄准" aria-label="Permalink to &quot;7. AIM（瞄准）&quot;">​</a></h3><ul><li>为选中对象创建两个定位器（TOP和BOX）</li><li>移动定位器后，转动对象的动画会被传输到定位器</li><li>支持多对象</li><li>使用场景： <ul><li>尾巴、翅膀、衣服动画</li><li>操作管理</li><li>攻角动画</li></ul></li></ul><hr><h2 id="关键工具和快捷键" tabindex="-1">关键工具和快捷键 <a class="header-anchor" href="#关键工具和快捷键" aria-label="Permalink to &quot;关键工具和快捷键&quot;">​</a></h2><table tabindex="0"><thead><tr><th>快捷键</th><th>功能描述</th></tr></thead><tbody><tr><td><strong>C</strong></td><td>制作预设/后续无限循环动画</td></tr><tr><td><strong>O</strong></td><td>制作预设/后续无限震荡动画</td></tr><tr><td><strong>L</strong></td><td>制作预设/后续无限线性动画</td></tr><tr><td><strong>D</strong></td><td>恢复默认无限动画</td></tr></tbody></table><ul><li><p><strong>循环动画：</strong></p><ul><li>复制第一帧到最后一帧，制作循环</li><li>支持动画层操作</li><li>通过sinusoid（正弦）波实现自然循环</li></ul></li><li><p><strong>帧偏移：</strong></p><ul><li>向左或右偏移动画（1帧或0.1帧）</li><li>支持逐步偏移，适合多控制器同步动画</li></ul></li><li><p><strong>镜像：</strong></p><ul><li>镜像关键帧（对X轴反射）</li><li>支持已选择的关键帧或整个曲线</li><li>适合 gait（步态）动画润色</li></ul></li></ul><hr><h2 id="结语" tabindex="-1">结语 <a class="header-anchor" href="#结语" aria-label="Permalink to &quot;结语&quot;">​</a></h2><p>该工具集和脚本极大提升动画制作效率，帮助动画师解决复杂技术难题，实现高效、自然的动画效果。</p><p><img src="'+e+'" alt="alt text"></p>',48)])])}const g=i(o,[["render",h]]);export{_ as __pageData,g as default};
