import{_ as t,o,c as r,a6 as a}from"./chunks/framework.BB0md0jN.js";const s="/Art_web_public/assets/image-4.rMRD1dvX.png",i="/Art_web_public/assets/image-2.WGlqURz3.png",n="/Art_web_public/assets/image.7dCus6Yy.png",l="/Art_web_public/assets/image-1.DaRnMdaF.png",c="/Art_web_public/assets/image-3.Eu-nbL5y.png",C=JSON.parse('{"title":"色彩空间","description":"","frontmatter":{"readingTime":"2 min read"},"headers":[],"relativePath":"全流程美术/动画电影/色彩空间/色彩空间.md","filePath":"全流程美术/动画电影/色彩空间/色彩空间.md"}'),p={name:"全流程美术/动画电影/色彩空间/色彩空间.md"};function _(m,e,b,u,d,g){return o(),r("div",null,[...e[0]||(e[0]=[a('<h1 id="色彩空间" tabindex="-1">色彩空间 <a class="header-anchor" href="#色彩空间" aria-label="Permalink to &quot;色彩空间&quot;">​</a></h1><p><img src="'+s+'" alt="alt text"> ACES（Academy Color Encoding System）是由美国电影艺术与科学学院开发的一套电影级色彩管理系统，可以统一整个制作流程（拍摄、合成、调色、输出）中的色彩空间，从而保障最终画面质量、统一性和跨平台适应性。<br> ACES 1.2 是其较成熟和广泛应用的版本，支持HDR、高位深渲染、线性工作流程等，是工业级动画和影视制作的标准之一。</p><p><a href="https://opencolorio.org/" target="_blank" rel="noreferrer">https://opencolorio.org/</a></p><p>这个仓库 OpenColorIO-Config-ACES 主要用于生成基于 ACES（Academy Color Encoding System）的 OpenColorIO（OCIO）配置文件</p><ul><li><a href="https://github.com/colour-science/OpenColorIO-Config-ACES" target="_blank" rel="noreferrer">https://github.com/colour-science/OpenColorIO-Config-ACES</a></li></ul><p>迪士尼在《冰雪奇缘2》等项目中使用的开源颜色管理工具为 ​​OpenColorIO（OCIO）​​，这是由索尼图形图像（Sony Pictures Imageworks）开发并由学院软件基金会（ASWF）维护的行业标准解决方案。该仓库提供了完整的代码、文档及配置案例，支持电影级色彩管线的搭建。</p><ul><li>​GitHub仓库<br><a href="https://github.com/AcademySoftwareFoundation/OpenColorIO" target="_blank" rel="noreferrer">https://github.com/AcademySoftwareFoundation/OpenColorIO</a></li></ul><h2 id="aces配置流程" tabindex="-1">ACES配置流程 <a class="header-anchor" href="#aces配置流程" aria-label="Permalink to &quot;ACES配置流程&quot;">​</a></h2><p><img src="'+i+'" alt="alt text"><br> 添加环境变量，指定ocio文件路径，重启电脑</p><p><img src="'+n+'" alt="alt text"><br> SP里，新建文件选择色彩管理项。然后找到路径下的ocio文件</p><blockquote><p>C:\\Program Files\\Adobe\\Adobe Substance 3D Painter\\resources\\ocio\\aces_1.2</p></blockquote><p><img src="'+l+'" alt="alt text"><br> maya中选择对应项<br><img src="'+c+'" alt="alt text"><br> houdini中配置</p>',12)])])}const A=t(p,[["render",_]]);export{C as __pageData,A as default};
