import{_ as t,o as i,c as a,aa as l}from"./chunks/framework.D4-DbJe5.js";const r="/Art_web_public/assets/image.Doci4fJw.png",d=JSON.parse('{"title":"GBWPowerfulHit","description":"","frontmatter":{"readingTime":"2 min read"},"headers":[],"relativePath":"游戏引擎/Unreal_Engine/动画/GBWPowerfulHit/GBWPowerfulHit.md","filePath":"游戏引擎/Unreal_Engine/动画/GBWPowerfulHit/GBWPowerfulHit.md"}'),o={name:"游戏引擎/Unreal_Engine/动画/GBWPowerfulHit/GBWPowerfulHit.md"};function s(n,e,u,c,f,h){return i(),a("div",null,[...e[0]||(e[0]=[l('<h1 id="gbwpowerfulhit" tabindex="-1">GBWPowerfulHit <a class="header-anchor" href="#gbwpowerfulhit" aria-label="Permalink to &quot;GBWPowerfulHit&quot;">​</a></h1><p><img src="'+r+'" alt="alt text"><br><a href="https://xqrkyxv79bf.feishu.cn/wiki/UUpqwZ7adiuhP7kGZfAcc6IrnAc" target="_blank" rel="noreferrer">中文官方文档</a></p><h4 id="features" tabindex="-1">Features: <a class="header-anchor" href="#features" aria-label="Permalink to &quot;Features:&quot;">​</a></h4><ul><li>A complete combat system.</li><li>High-performance network replication, can be used for multiplayer games.</li><li>The impact effect of Out Of The Box.</li><li>Built-in Movement system, unified 3C and combat.</li><li>Attribute system that supports mathematical expressions.</li><li>Weapon equipment system.</li><li>Asymmetric camp system.</li><li>State System.</li><li>And More Coming Soon...</li></ul><h4 id="code-modules" tabindex="-1">Code Modules: <a class="header-anchor" href="#code-modules" aria-label="Permalink to &quot;Code Modules:&quot;">​</a></h4><ul><li>GBWPowerfulHit</li><li>GBWPowerfulEffect</li><li>GBWPowerfulEffectEditor</li><li>GBWFlowMove</li><li>GBWRMS</li><li>GBWAnim</li><li>GBWAnimEditor</li><li>GBWFootIK</li><li>GBWFootIKEditor</li><li>GBWPowerfulPerception</li><li>GBWPowerfulState</li><li>GBWPowerfulTools</li><li>GBWPowerfulToolsEditor</li></ul><hr><h3 id="理解后摇与前摇" tabindex="-1">理解后摇与前摇 <a class="header-anchor" href="#理解后摇与前摇" aria-label="Permalink to &quot;理解后摇与前摇&quot;">​</a></h3><p>在动作游戏中，让Dash（冲刺或瞬移等快速移动技能）能够取消Attack（攻击）的后摇，是实现更流畅、更动感游戏手感的有效手段之一。</p><ul><li>后摇：是指英雄在施法或攻击完成后，进行的一段无意义的后续动作。这段时间内，英雄无法进行其他操作，导致游戏节奏变慢。</li><li>前摇：则是施法或攻击开始前的准备动作。虽然前摇不能通过Dash直接取消，但理解其概念有助于整体设计的平衡。</li></ul><h4 id="实现取消后摇" tabindex="-1">实现取消后摇 <a class="header-anchor" href="#实现取消后摇" aria-label="Permalink to &quot;实现取消后摇&quot;">​</a></h4><p>技能优先级：在游戏引擎中设置Dash技能的优先级高于Attack的后摇，确保当Dash技能被触发时，能够立即中断Attack的后摇。</p><h4 id="案例分析" tabindex="-1">案例分析 <a class="header-anchor" href="#案例分析" aria-label="Permalink to &quot;案例分析&quot;">​</a></h4><p>在许多成功的动作游戏中，如《鬼泣》、《猎天使魔女》等，玩家可以通过快速移动技能（如闪避、瞬移等）来取消攻击后摇，实现更流畅的战斗体验。</p><p><a href="https://www.youtube.com/watch?v=P6JyUbkAWgQ&amp;list=PLCZ0WflcfPv4ETvNj4IsnDJU9Aws04cUn&amp;index=4" target="_blank" rel="noreferrer">https://www.youtube.com/watch?v=P6JyUbkAWgQ&amp;list=PLCZ0WflcfPv4ETvNj4IsnDJU9Aws04cUn&amp;index=4</a></p>',15)])])}const p=t(o,[["render",s]]);export{d as __pageData,p as default};
