import{_ as a,o as e,c as r,aa as s}from"./chunks/framework.D4-DbJe5.js";const i="/Art_web_public/assets/image-2.aU-YnDlo.png",o="/Art_web_public/assets/image.Tmj6JYfN.png",l="/Art_web_public/assets/image-1.BjudW2AO.png",n="/Art_web_public/assets/image-3.sUlmDzDi.png",b="/Art_web_public/assets/image-4.DfhXyIMy.png",_="/Art_web_public/assets/image-5.QhR9Nt7s.png",c="/Art_web_public/assets/image-6.BDVSGGUY.png",p="/Art_web_public/assets/image-7.B20mT6lD.png",m="/Art_web_public/assets/image-8.BBnaGTTx.png",g="/Art_web_public/assets/image-9.Bp0fag0d.png",d="/Art_web_public/assets/image-10.DvJ4MwHR.png",h="/Art_web_public/assets/image-11.DK6hGoOM.png",u="/Art_web_public/assets/image-12.CtOIZHeF.png",x="/Art_web_public/assets/image-13.LPhRmsuB.png",A="/Art_web_public/assets/image-14.dPmDGmNM.png",f="/Art_web_public/assets/image-15.h5Btd2NV.png",w="/Art_web_public/assets/image-16.BP2XoA_v.png",q="/Art_web_public/assets/image-17.CTJ6v20s.png",P="/Art_web_public/assets/image-18.D-eIuTG8.png",T="/Art_web_public/assets/image-19.D1iwDfdQ.png",k="/Art_web_public/assets/image-20.BJECHoAu.png",D="/Art_web_public/assets/image-21.N1-FfTPE.png",E="/Art_web_public/assets/image-22.P6pfo9Cd.png",B="/Art_web_public/assets/image-23.DwgONobd.png",G=JSON.parse('{"title":"本地化翻译","description":"","frontmatter":{"readingTime":"4 min read"},"headers":[],"relativePath":"游戏引擎/Unreal_Engine/本地化翻译/本地化翻译.md","filePath":"游戏引擎/Unreal_Engine/本地化翻译/本地化翻译.md"}'),U={name:"游戏引擎/Unreal_Engine/本地化翻译/本地化翻译.md"};function N(S,t,C,I,v,z){return e(),r("div",null,[...t[0]||(t[0]=[s('<h1 id="本地化翻译" tabindex="-1">本地化翻译 <a class="header-anchor" href="#本地化翻译" aria-label="Permalink to &quot;本地化翻译&quot;">​</a></h1><h2 id="一些说明和设置" tabindex="-1">一些说明和设置 <a class="header-anchor" href="#一些说明和设置" aria-label="Permalink to &quot;一些说明和设置&quot;">​</a></h2><p><img src="'+i+'" alt="alt text"><br> 无法在引擎预览，需要通过打包，一个简易方式是standardone game<br><img src="'+o+'" alt="alt text"><br> 只有文本类型的<code>对象objects</code>或<code>变量varsiables</code>才可以被翻译<br> localization dashboard只从特定文件夹收集 <strong>Text文本类型</strong> 的变量进行翻译<br><strong>string</strong> 类型是不会被翻译的<br><img src="'+l+'" alt="alt text"><br> 可以控制哪些text变量不被翻译<br><img src="'+n+'" alt="alt text"><br> UI界面，字幕等确保是可以被翻译的</p><h2 id="文本类型值" tabindex="-1">文本类型值 <a class="header-anchor" href="#文本类型值" aria-label="Permalink to &quot;文本类型值&quot;">​</a></h2><p>Make Literal Text 是蓝图专属的文本构造节点，核心作用：创建一个固定不变的文本常量，也就是我们常说的字符串文本。<br><img src="'+b+'" alt="alt text"><br> Make Literal Text 输出的是Text 类型，不是 String 类型<br> 这是 UE 官方强推荐的文本类型，因为 Text 原生支持 多语言本地化翻译，是 UE 为「游戏内展示给玩家的文字」专门设计的类型。<br> UE 的蓝图中，文本相关有两个核心类型：Text（本地化文本，推荐使用） 和 String（普通字符串，无本地化）</p><h3 id="字符的转换和拼接" tabindex="-1">字符的转换和拼接 <a class="header-anchor" href="#字符的转换和拼接" aria-label="Permalink to &quot;字符的转换和拼接&quot;">​</a></h3><p>to string→append<br><img src="'+_+'" alt="alt text"></p><h3 id="文本翻译的显示长短" tabindex="-1">文本翻译的显示长短 <a class="header-anchor" href="#文本翻译的显示长短" aria-label="Permalink to &quot;文本翻译的显示长短&quot;">​</a></h3><p>不同语言显示同意涵的长短是不同的<br> 勾选自动包装文本,确保有个最差的保底<br><img src="'+c+'" alt="alt text"></p><h2 id="设置" tabindex="-1">设置 <a class="header-anchor" href="#设置" aria-label="Permalink to &quot;设置&quot;">​</a></h2><h3 id="项目设置" tabindex="-1">项目设置 <a class="header-anchor" href="#项目设置" aria-label="Permalink to &quot;项目设置&quot;">​</a></h3><p>项目设置里选择要翻译的目标语言<br> 可以看到同一个语言有不同子类别，子类别有俚语的差异<br> 一般来说选择父类的<br><img src="'+p+'" alt="alt text"><br> 国际化支持可以选择全部<br><img src="'+m+'" alt="alt text"></p><h3 id="localization-dashboard设置" tabindex="-1">localization dashboard设置 <a class="header-anchor" href="#localization-dashboard设置" aria-label="Permalink to &quot;localization dashboard设置&quot;">​</a></h3><p>目录的选择,也可以添加排除<br> 大多数游戏可以不勾选从文本获取<br> 但所有放在游戏包里的需要<br><img src="'+g+'" alt="alt text"><br> 勾选跳过源检查<br> 这里要查一下，没理解到<br><img src="'+d+'" alt="alt text"><br> 设置完成后，在最下面添加语言，就可以开始收集文本<br><img src="'+h+'" alt="alt text"></p><h2 id="翻译" tabindex="-1">翻译 <a class="header-anchor" href="#翻译" aria-label="Permalink to &quot;翻译&quot;">​</a></h2><p>通过编辑并存储<br><img src="'+u+'" alt="alt text"><br> 但这样比较慢<br> 可以选择所有文字，复制到Excel<br><img src="'+x+'" alt="alt text"><br> 翻译完成可以粘贴到另一列。可以全选翻译的粘贴回。<br><strong>如果有单元格没有内容，会导致引擎崩溃</strong><br> 可以在已完成里看到完成的翻译<br><img src="'+A+'" alt="alt text"><br> 如果是第三方翻译公司，可以用PO文件交互<br><img src="'+f+'" alt="alt text"><br> 打开PO文件，只需要把翻译添加最后一行里的字符串就可以<br><img src="'+w+'" alt="alt text"><br><img src="'+q+'" alt="alt text"></p><h2 id="编译翻译" tabindex="-1">编译翻译 <a class="header-anchor" href="#编译翻译" aria-label="Permalink to &quot;编译翻译&quot;">​</a></h2><p><img src="'+P+'" alt="alt text"></p><h2 id="蓝图激活翻译" tabindex="-1">蓝图激活翻译 <a class="header-anchor" href="#蓝图激活翻译" aria-label="Permalink to &quot;蓝图激活翻译&quot;">​</a></h2><p>在关卡蓝图里。设置IETF语言标签 比如中文的标签是 <code>zh</code><br><img src="'+T+'" alt="alt text"><br><strong>Set Current Culture</strong> 是核心节点<br> 测试可以设置1,2,3按键来切换显示<br> culture里设置对应的IETF语言标签<br><img src="'+k+'" alt="alt text"><br> 启动查看<br><img src="'+D+'" alt="alt text"><br> 勾选 <strong>save to fig</strong> 的话，一旦切换语言，下一次还是会从这个语言加载。建议都勾上<br><img src="'+E+'" alt="alt text"><br> 实际开发中，不可能每个语言设置一个按键。实际上只需要当前选择语言为默认语言就可以<br> 核心节点是 <code>get default locale</code><br><img src="'+B+'" alt="alt text"></p>',20)])])}const M=a(U,[["render",N]]);export{G as __pageData,M as default};
