import{_ as d,o as r,c as s,ag as i}from"./chunks/framework.Bx1bucHt.js";const l="/Art_web_public/assets/image.BZ5YQkUQ.png",a="/Art_web_public/assets/image-1.Cf-h0qex.png",o="/Art_web_public/assets/image-2.CgzsLR_H.png",e="/Art_web_public/assets/image-3.obd4l52h.png",n="/Art_web_public/assets/image-4.DcJw1L9t.png",g="/Art_web_public/assets/image-5.BMj4cdYI.png",_="/Art_web_public/assets/image-6.CYHulSko.png",c="/Art_web_public/assets/image-7.Cff3Sd4E.png",p="/Art_web_public/assets/image-8.D2Dv7MV7.png",m="/Art_web_public/assets/image-9.BpBiNbA0.png",h="/Art_web_public/assets/image-10.Bx4E70Hv.png",b="/Art_web_public/assets/image-11.C2xO_dXD.png",u="/Art_web_public/assets/image-12.CXhxy5h8.png",x="/Art_web_public/assets/image-13.VSNr-2zT.png",w="/Art_web_public/assets/image-14.c9NHKE4I.png",A="/Art_web_public/assets/image-15.DIphCMqD.png",S="/Art_web_public/assets/image-16.BrEZCSSO.png",f="/Art_web_public/assets/image-17.8MPbGSMt.png",E=JSON.parse('{"title":"火焰之纹章系列作品总览","description":"","frontmatter":{},"headers":[],"relativePath":"游戏设计/系列游戏/火焰之纹章/火焰之纹章.md","filePath":"游戏设计/系列游戏/火焰之纹章/火焰之纹章.md"}'),C={name:"游戏设计/系列游戏/火焰之纹章/火焰之纹章.md"};function D(q,t,k,P,B,G){return r(),s("div",null,[...t[0]||(t[0]=[i('<h1 id="火焰之纹章系列作品总览" tabindex="-1">火焰之纹章系列作品总览 <a class="header-anchor" href="#火焰之纹章系列作品总览" aria-label="Permalink to &quot;火焰之纹章系列作品总览&quot;">​</a></h1><table tabindex="0"><thead><tr><th>序号</th><th>游戏名称</th><th>游戏封面</th><th>发售时间</th><th>平台</th><th>剧情概要</th><th>玩法特色</th></tr></thead><tbody><tr><td>1</td><td>火焰之纹章 暗黑龙与光之剑</td><td><img src="'+l+'" alt="alt text"></td><td>1990年4月20日</td><td>FC</td><td>马尔斯王子讨伐暗黑龙梅迪乌斯，拯救国家。</td><td>首创SRPG系统，角色死亡永久消失。</td></tr><tr><td>2</td><td>火焰之纹章 外传</td><td><img src="'+a+'" alt="alt text"></td><td>1992年3月14日</td><td>FC</td><td>阿雷武与赛莉卡在瓦伦西亚大陆对抗堕落神明。</td><td>加入地下城、自由移动，强化RPG要素。</td></tr><tr><td>3</td><td>火焰之纹章 纹章之谜</td><td><img src="'+o+'" alt="alt text"></td><td>1994年1月21日</td><td>SFC</td><td>《暗黑龙》重制+新剧情，马尔斯续战。</td><td>双部构成，平衡优化，多职业系统。</td></tr><tr><td>4</td><td>火焰之纹章 圣战之系谱</td><td><img src="'+e+'" alt="alt text"></td><td>1996年5月14日</td><td>SFC</td><td>西格尔特父子跨时代战争，揭示神血秘密。</td><td>恋爱/子代系统，武器三角，超大地图。</td></tr><tr><td>5</td><td>火焰之纹章 多拉基亚776</td><td><img src="'+n+'" alt="alt text"></td><td>1999年9月1日</td><td>SFC</td><td>利夫在黑暗统治下奋战，救民于水火。</td><td>雾视野、捕获、极高难度与策略性。</td></tr><tr><td>6</td><td>火焰之纹章 封印之剑</td><td><img src="'+g+'" alt="alt text"></td><td>2002年3月29日</td><td>GBA</td><td>罗伊王子率军平定战乱，掌握封印之剑。</td><td>加入支援系统，剧情影响角色成长。</td></tr><tr><td>7</td><td>火焰之纹章 烈火之剑</td><td><img src="'+_+'" alt="alt text"></td><td>2003年4月25日</td><td>GBA</td><td>艾利乌德等三人旅途，探寻龙族与政变。</td><td>首作全球发行，含完整新手教程。</td></tr><tr><td>8</td><td>火焰之纹章 圣魔之光石</td><td><img src="'+c+'" alt="alt text"></td><td>2004年10月7日</td><td>GBA</td><td>皇族双胞胎为复国抗敌，探寻圣石真相。</td><td>分支职业、地图探索、自由练级。</td></tr><tr><td>9</td><td>火焰之纹章 苍炎之轨迹</td><td><img src="'+p+'" alt="alt text"></td><td>2005年4月20日</td><td>GameCube</td><td>艾克领导佣兵团，跨种族与女神之战。</td><td>全3D战斗，技能系统与体格机制。</td></tr><tr><td>10</td><td>火焰之纹章 暗黑之门（曙光之女神）</td><td><img src="'+m+'" alt="alt text"></td><td>2007年2月22日</td><td>Wii</td><td>曙光军与艾克联手对抗女神亚斯拉。</td><td>多主角、章节分组、继承前作进度。</td></tr><tr><td>11</td><td>火焰之纹章 新·暗黑龙与光之剑</td><td><img src="'+h+'" alt="alt text"></td><td>2008年8月7日</td><td>NDS</td><td>初代重制作，讲述马尔斯王子的冒险之旅。</td><td>新增自创角色系统、难度选择，多分支章节。</td></tr><tr><td>12</td><td>火焰之纹章 新·纹章之谜</td><td><img src="'+b+'" alt="alt text"></td><td>2010年7月15日</td><td>NDS</td><td>马尔斯继续面对背叛与暗流，展开新篇章。</td><td>引入“我的单位”系统，自定义主角设定，经典与新元素融合。</td></tr><tr><td>13</td><td>火焰之纹章 觉醒</td><td><img src="'+u+'" alt="alt text"></td><td>2012年4月19日</td><td>3DS</td><td>主角克罗姆与战术家罗宾一起对抗未来的毁灭。</td><td>结婚生子系统复活，支援对话丰富，首次引入“休闲模式”。</td></tr><tr><td>14</td><td>火焰之纹章 if（白夜王国/暗夜王国/隐秘王国）</td><td><img src="'+x+'" alt="alt text"></td><td>2015年6月25日</td><td>3DS</td><td>主角卡姆依在两个王国之间做出抉择，影响世界命运。</td><td>三版本三条路线，加入在线城堡系统，强化角色自定义。</td></tr><tr><td>15</td><td>火焰之纹章 Echoes：另一位英雄王</td><td><img src="'+w+'" alt="alt text"></td><td>2017年4月20日</td><td>3DS</td><td>重制《外传》，阿雷武与赛莉卡双主角揭示神明真相。</td><td>完整语音演出，地下城探索、经典与现代融合的尝试。</td></tr><tr><td>16</td><td>火焰之纹章 风花雪月</td><td><img src="'+A+'" alt="alt text"></td><td>2019年7月26日</td><td>Switch</td><td>主角贝雷特担任军校教师，三国学生之间的命运交织。</td><td>学园生活+战斗教学系统，多线剧情、分支结局，高自由度成长。</td></tr><tr><td>17</td><td>火焰之纹章 无双 风花雪月</td><td><img src="'+S+'" alt="alt text"></td><td>2022年6月24日</td><td>Switch</td><td>动作游戏风格再现《风花雪月》角色与战争。</td><td>结合无双玩法与策略成长系统，多周目分支剧情。</td></tr><tr><td>18</td><td>火焰之纹章 Engage</td><td><img src="'+f+'" alt="alt text"></td><td>2023年1月20日</td><td>Switch</td><td>主角琉尔与历代纹章士联手，击退邪龙威胁世界和平。</td><td>“Engage”系统召唤系列角色，强化战斗演出，经典系统回归。</td></tr><tr><td>详细对照</td><td></td><td></td><td></td><td></td><td></td><td></td></tr><tr><td><a href="https://www.gamersky.com/handbook/202212/1550879.shtml" target="_blank" rel="noreferrer">https://www.gamersky.com/handbook/202212/1550879.shtml</a></td><td></td><td></td><td></td><td></td><td></td><td></td></tr></tbody></table><h3 id="火焰之纹章系列关键转折与创新迭代脉络" tabindex="-1">火焰之纹章系列关键转折与创新迭代脉络 <a class="header-anchor" href="#火焰之纹章系列关键转折与创新迭代脉络" aria-label="Permalink to &quot;火焰之纹章系列关键转折与创新迭代脉络&quot;">​</a></h3><h4 id="一、生死存亡的转折点" tabindex="-1">一、<strong>生死存亡的转折点</strong> <a class="header-anchor" href="#一、生死存亡的转折点" aria-label="Permalink to &quot;一、**生死存亡的转折点**&quot;">​</a></h4><ol><li><p><strong>《觉醒》的背水一战（2012）</strong></p><ul><li><strong>背景</strong>：2005-2010年系列因NGC《苍炎之轨迹》（54万销量）、Wii《晓之女神》（49万销量）及NDS重制版（如《新·纹章之谜》仅27万销量）连续失利，任天堂下达&quot;最终作通牒&quot;。</li><li><strong>创新</strong>： <ul><li><strong>休闲模式</strong>：取消角色永久死亡机制，降低门槛；</li><li><strong>结婚生子系统</strong>：子代继承技能与剧情联动，增强角色培养深度；</li><li><strong>DLC扩展</strong>：新增挑战地图与角色养成内容。</li></ul></li><li><strong>成果</strong>：销量228万创系列新高，拯救IP并开启新纪元。</li></ul></li><li><p><strong>GBA三部曲的出海成功（2002-2004）</strong></p><ul><li><strong>简化系统</strong>：《封印之剑》《烈火之剑》《圣魔之光石》引入新手教程与支援对话，全球销量突破百万（《烈火之剑》97万，《圣魔之光石》89万），首次打入欧美市场；</li><li><strong>角色塑造</strong>：支援对话解锁隐藏剧情，角色形象立体化。</li></ul></li></ol><h4 id="二、销量与口碑的巅峰与争议" tabindex="-1">二、<strong>销量与口碑的巅峰与争议</strong> <a class="header-anchor" href="#二、销量与口碑的巅峰与争议" aria-label="Permalink to &quot;二、**销量与口碑的巅峰与争议**&quot;">​</a></h4><ol><li><p><strong>《风花雪月》（2019）的破圈效应</strong></p><ul><li><strong>融合玩法</strong>：战棋+学院养成，角色好感度与多线叙事（四条路线）吸引轻度玩家；</li><li><strong>销量突破</strong>：382万销量问鼎系列之最，TGA 2019最佳策略游戏奖；</li><li><strong>争议</strong>：老玩家批评策略性削弱，养成占比过高。</li></ul></li><li><p><strong>《Engage》（2023）的回归与困境</strong></p><ul><li><strong>玩法革新</strong>： <ul><li>&quot;Engage&quot;系统召唤历代角色，地形破坏与自由技能搭配强化策略深度；</li><li>关卡设计被誉为系列巅峰，高难度需步步为营。</li></ul></li><li><strong>短板</strong>：剧情平庸、材料肝度过高、多周目缺失，销量仅161万（不足《风花雪月》一半）。</li></ul></li></ol><h4 id="三、玩法迭代的核心脉络" tabindex="-1">三、<strong>玩法迭代的核心脉络</strong> <a class="header-anchor" href="#三、玩法迭代的核心脉络" aria-label="Permalink to &quot;三、**玩法迭代的核心脉络**&quot;">​</a></h4><ol><li><p><strong>核心机制传承与革新</strong></p><ul><li><strong>武器三角与职业系统</strong>：从初代延续至今（剑→斧→枪），职业差异化奠定策略基础；</li><li><strong>子代系统</strong>：《圣战之系谱》（1996）首创婚配遗传，《觉醒》《if》发展为技能继承与剧情联动。</li></ul></li><li><p><strong>平台与技术升级</strong></p><ul><li><strong>3D化尝试</strong>：NGC《苍炎之轨迹》首次全3D化（2005），Switch《风花雪月》实现高清建模与动态演出平衡；</li><li><strong>掌机优势</strong>：GBA与3DS作品通过便携性扩大用户，《烈火之剑》成欧美玩家入坑作。</li></ul></li><li><p><strong>难度与受众平衡</strong></p><ul><li><strong>硬核到大众化</strong>：早期SFC作品（如《多拉基亚776》）以高难度著称，NDS重制版加入中途存档，《觉醒》休闲模式降低门槛；</li><li><strong>衍生拓展</strong>：手游《火焰之纹章：英雄》以抽卡+精简战棋延续IP热度，《风花雪月无双》尝试动作化但口碑两极。</li></ul></li></ol>',9)])])}const T=d(C,[["render",D]]);export{E as __pageData,T as default};
