import{_ as t,o as r,c as a,a6 as o}from"./chunks/framework.BB0md0jN.js";const p=JSON.parse('{"title":"思维的精进","description":"","frontmatter":{"layout":"doc","title":"思维的精进","readingTime":"3 min read"},"headers":[],"relativePath":"电影和阅读/微信读书/个人成长_人生哲学/思维的精进.md","filePath":"电影和阅读/微信读书/个人成长_人生哲学/思维的精进.md"}'),n={name:"电影和阅读/微信读书/个人成长_人生哲学/思维的精进.md"};function l(s,e,i,_,d,c){return r(),a("div",null,[...e[0]||(e[0]=[o('<h1 id="思维的精进" tabindex="-1">思维的精进 <a class="header-anchor" href="#思维的精进" aria-label="Permalink to &quot;思维的精进&quot;">​</a></h1><p><img src="https://cdn.weread.qq.com/weread/cover/25/YueWen_37414202/t6_YueWen_37414202.jpg" alt=" 思维的精进"></p><ul><li><strong>书名</strong>： 思维的精进</li><li><strong>作者</strong>： 大卫·罗布森</li><li><strong>简介</strong>： 聪明人更擅于学习，更容易成功？更容易获得幸福？NO！某位得过两次诺奖的化学家真诚地相信维生素补充剂可以治疗癌症；柯南?道尔白天写福尔摩斯，晚上却沉迷于通灵术；NASA工程师忘了把英制换算成公制，致使卫星坠毁。本书汇集心理学、神经科学、哲学和人类学领域最尖端研究，提出聪明人也许更擅于记忆和推理，但在做判断和解决实际问题时，常比一般人还容易犯傻错误。事实上，聪明人偏见盲点更多。丰富的经验和专业知识也常导致他们过于依赖固有的思维模式，难以应对变化。这一切，都是因为陷入了不同形式的智力陷阱。为了突破思维局限，避开这些陷阱，作者大卫~罗布森给出了训练思维和智慧的一系列可行方法。借由这些认知反思模式，本书将帮 你在当下这个复杂世界里，建立起避免偏见和谬误的认知框架，进而更有效地学习，更精准地思考和决策。读完它，你的思维方式将会刷新升级！</li><li><strong>出版时间</strong>： 2021-01-01 00:00:00</li><li><strong>ISBN</strong>： 9787571008604</li><li><strong>分类</strong>： 个人成长-人生哲学</li><li><strong>出版社</strong>： 湖南科学技术出版社</li><li><strong>PC地址</strong>： <a href="https://weread.qq.com/web/reader/017328e0723ae53a0178a2d" target="_blank" rel="noreferrer">https://weread.qq.com/web/reader/017328e0723ae53a0178a2d</a></li></ul><h2 id="引言" tabindex="-1">引言 <a class="header-anchor" href="#引言" aria-label="Permalink to &quot;引言&quot;">​</a></h2><blockquote><p>📌 一般意义上的智力和学院教育不仅不能令我们免于犯各种认知方面的错误，聪明人甚至更容易被一些特定类型的愚蠢想法坑害。 ⏱ 2021-05-20 12:35:49</p></blockquote>',5)])])}const m=t(n,[["render",l]]);export{p as __pageData,m as default};
