import{_ as i,o as a,c as t,a6 as e}from"./chunks/framework.BB0md0jN.js";const p=JSON.parse('{"title":"快速阅读：速读是人生最好的投资","description":"","frontmatter":{"layout":"doc","title":"快速阅读：速读是人生最好的投资","readingTime":"5 min read"},"headers":[],"relativePath":"电影和阅读/微信读书/个人成长/快速阅读_速读是人生最好的投资.md","filePath":"电影和阅读/微信读书/个人成长/快速阅读_速读是人生最好的投资.md"}'),r={name:"电影和阅读/微信读书/个人成长/快速阅读_速读是人生最好的投资.md"};function o(u,l,n,s,_,d){return a(),t("div",null,[...l[0]||(l[0]=[e('<h1 id="快速阅读-速读是人生最好的投资" tabindex="-1">快速阅读：速读是人生最好的投资 <a class="header-anchor" href="#快速阅读-速读是人生最好的投资" aria-label="Permalink to &quot;快速阅读：速读是人生最好的投资&quot;">​</a></h1><p><img src="https://wfqqreader-1252317822.image.myqcloud.com/cover/771/26297771/t7_26297771.jpg" alt=" 快速阅读：速读是人生最好的投资"></p><ul><li><strong>书名</strong>： 快速阅读：速读是人生最好的投资</li><li><strong>作者</strong>： 龚华钢</li><li><strong>简介</strong>： 《快速阅读：速读是人生最好的投资》是一本浓缩了速读培训界大咖20年经验的书，是一本让你在10天变身速读达人的书。本书首先介绍了作者与快速阅读的结缘，以及快速阅读带给作者的人生成长，让读者感受到速读确实是人生最好的投资。本书大篇幅详实介绍了速读的训练步骤和方法，只要读者按照书中的指导进行训练，就能在短短的十天内明显提升阅读速度、理解率、回忆率、总结能力。还介绍了速读与思维导图、快速记忆法结合构成的高效学习法，以及快速阅读在专业书籍和考试中的应用。最后，作者还集中解答读者对快速阅读困惑的问题，让读者对快速阅读有更清晰更理性的认识。</li><li><strong>出版时间 2019-05-01 00</strong>: 00:00</li><li><strong>ISBN</strong>： 9787518061501</li><li><strong>分类</strong>： 个人成长-认知思维</li><li><strong>出版社</strong>： 中国纺织出版社</li></ul><h2 id="我的快速阅读之路" tabindex="-1">我的快速阅读之路 <a class="header-anchor" href="#我的快速阅读之路" aria-label="Permalink to &quot;我的快速阅读之路&quot;">​</a></h2><ul><li>📌 所以就是1～2天很快把一本书考点画下来这个过程中，让我对于专业书籍重点的把握变得快而且准确。 <ul><li>⏱ 2019-12-23 01:04:05</li></ul></li></ul><h2 id="理论与训练流程" tabindex="-1">理论与训练流程 <a class="header-anchor" href="#理论与训练流程" aria-label="Permalink to &quot;理论与训练流程&quot;">​</a></h2><ul><li>📌 增加视幅 <ul><li>⏱ 2019-12-23 01:09:21</li></ul></li></ul><h2 id="快速阅读的作用" tabindex="-1">快速阅读的作用 <a class="header-anchor" href="#快速阅读的作用" aria-label="Permalink to &quot;快速阅读的作用&quot;">​</a></h2><ul><li><p>📌 阅读的目标并不是看完之后就把一本书的知识点背诵下来。</p><ul><li>⏱ 2019-12-23 01:10:42</li></ul></li><li><p>📌 这其实就是快速阅读最核心的作用：快速阅读是对信息和知识的初步加工。帮助读者快速的获取有用的信息和知识，为后面的精加工做准备。</p><ul><li>⏱ 2019-12-23 01:11:26</li></ul></li></ul><h2 id="为什么阅读速度慢" tabindex="-1">为什么阅读速度慢 <a class="header-anchor" href="#为什么阅读速度慢" aria-label="Permalink to &quot;为什么阅读速度慢&quot;">​</a></h2><ul><li><p>📌 很多人在看书时，是一个字一个字看书。</p><ul><li>⏱ 2019-12-23 01:12:08</li></ul></li><li><p>📌 吸收书籍知识的三个主要感觉通道：视觉（看）；听觉（嘴巴念）；动觉（拿笔抄）。看是最快的，嘴巴念次之，拿笔抄最慢。慢的就会拖延速度。如果一边看书一边念，一般是念到哪里看到哪里；如果一边看一边抄写，那更慢，一般是抄到哪里看到哪里。</p><ul><li>⏱ 2019-12-23 01:12:43</li></ul></li><li><p>📌 发出声音和默读之间有一点区别，但是区别不是很大，简单来说只是有没发出声音的区别。</p><ul><li>⏱ 2019-12-23 01:13:14</li></ul></li><li><p>📌 人们从小识字是从一个字一个字开始学习的。潜移默化的就养成了这个习惯。另外人的心理都是求全的，在阅读过程中想抓住百分之百的信息、怕漏信息、怕丢信息，所以造成阅读过程中不敢快。</p><ul><li>⏱ 2019-12-23 02:03:12</li></ul></li></ul><h2 id="快速阅读的基本原理" tabindex="-1">快速阅读的基本原理 <a class="header-anchor" href="#快速阅读的基本原理" aria-label="Permalink to &quot;快速阅读的基本原理&quot;">​</a></h2><ul><li><p>📌 阅读慢造成注意力容易涣散。</p><ul><li>⏱ 2019-12-23 02:03:35</li></ul></li><li><p>📌 当能训练到可以一次快速浏览两行的时候基本上已经够实际的应用了。</p><ul><li>⏱ 2019-12-23 02:04:02</li></ul></li><li><p>📌 放下求全心理，自然敢于加快阅读的速度。</p><ul><li>⏱ 2020-01-03 19:39:16</li></ul></li></ul>',13)])])}const c=i(r,[["render",o]]);export{p as __pageData,c as default};
