import{_ as t,o as r,c as a,a6 as o}from"./chunks/framework.BB0md0jN.js";const p=JSON.parse('{"title":"宋朝原来是这样","description":"","frontmatter":{"layout":"doc","title":"宋朝原来是这样","readingTime":"3 min read"},"headers":[],"relativePath":"电影和阅读/微信读书/历史_中国古代/宋朝原来是这样.md","filePath":"电影和阅读/微信读书/历史_中国古代/宋朝原来是这样.md"}'),n={name:"电影和阅读/微信读书/历史_中国古代/宋朝原来是这样.md"};function l(s,e,i,_,d,c){return r(),a("div",null,[...e[0]||(e[0]=[o('<h1 id="宋朝原来是这样" tabindex="-1">宋朝原来是这样 <a class="header-anchor" href="#宋朝原来是这样" aria-label="Permalink to &quot;宋朝原来是这样&quot;">​</a></h1><p><img src="https://cdn.weread.qq.com/weread/cover/76/yuewen_710002/t6_yuewen_7100021687675177.jpg" alt=" 宋朝原来是这样"></p><ul><li><strong>书名</strong>： 宋朝原来是这样</li><li><strong>作者</strong>： 醉罢君山</li><li><strong>简介</strong>： 陈桥兵变，宋太祖黄袍加身，定三百年帝国基业。大宋帝国在进取与保守、图强与积弱的反复中踽踽而行。这是中国历史上最长命的王朝之一，却从未实现真正意义的大一统；这是一个文化、科技、经济繁荣的时代，却未能掩饰对外战争被动挨打的尴尬。 强干弱枝、内重外轻的政策在避免政治动荡的同时，却不可避免地埋下衰亡的种子；轰轰烈烈的变法运动本是富国强兵的锐意改革，最终却沦为无休止的党争与政治投机家的博弈；联金灭辽本是主动进取的战略，最终却引狼入室，导致北宋灭亡；当南宋中兴的曙光初现时，不作为的朝廷却轻而易举地将希望的火苗掐灭。 本书以纵横捭阖的笔法，书写了宋朝三百年波澜壮阔而又充满矛盾与纠结的历史。有对宋朝高度文明的政治、文化生态的褒扬，也有对其军事、外交败笔的解读、抨击，更有对大宋民富国弱的历史吊诡的深思。</li><li><strong>出版时间</strong>： 2014-06-01 00:00:00</li><li><strong>ISBN</strong>： 9787514327496</li><li><strong>分类</strong>： 历史-中国古代</li><li><strong>出版社</strong>： 现代出版社</li><li><strong>PC地址</strong>： <a href="https://weread.qq.com/web/reader/64432e605ad5726444f730c" target="_blank" rel="noreferrer">https://weread.qq.com/web/reader/64432e605ad5726444f730c</a></li></ul><h2 id="四八-钢铁堡垒-鏖战钓鱼城" tabindex="-1">四八 钢铁堡垒：鏖战钓鱼城 <a class="header-anchor" href="#四八-钢铁堡垒-鏖战钓鱼城" aria-label="Permalink to &quot;四八 钢铁堡垒：鏖战钓鱼城&quot;">​</a></h2><blockquote><p>📌 四川制置使蒲择之派军在遂宁江箭滩阻击，经过一天激战，宋军战败。蒙古人长驱直入，占领成都。 ⏱ 2020-08-16 15:03:47</p></blockquote>',5)])])}const m=t(n,[["render",l]]);export{p as __pageData,m as default};
