import{_ as a,o as t,c as o,a6 as r}from"./chunks/framework.BB0md0jN.js";const _=JSON.parse('{"title":"小岛经济学","description":"","frontmatter":{"layout":"doc","title":"小岛经济学","readingTime":"4 min read"},"headers":[],"relativePath":"电影和阅读/微信读书/小岛经济学.md","filePath":"电影和阅读/微信读书/小岛经济学.md"}'),l={name:"电影和阅读/微信读书/小岛经济学.md"};function n(i,e,s,c,d,h){return t(),o("div",null,[...e[0]||(e[0]=[r('<h1 id="小岛经济学" tabindex="-1">小岛经济学 <a class="header-anchor" href="#小岛经济学" aria-label="Permalink to &quot;小岛经济学&quot;">​</a></h1><p><img src="https://cdn.weread.qq.com/weread/cover/76/YueWen_912417/t6_YueWen_912417.jpg" alt=" 小岛经济学"></p><ul><li><strong>书名</strong>： 小岛经济学</li><li><strong>作者</strong>： 彼得·D.希夫 安德鲁·J.希夫</li><li><strong>简介</strong>： 如何对经济领域的各种现象进行专业而又生动的分析，是一项艰巨的任务。如何让从9岁到90岁的读者都能通过一本书洞悉日常生活现象背后的经济规律，更是一项几乎不可能完成的任务。《小岛经济学》就是这样一本书，它通过插图、幽默的措辞以及讲故事的平实手法，将经济学从高不可攀的架子上取下来，放回到厨房的餐桌上。它本就该属于那个地方。这个关于鱼、渔网、存钱、借钱的故事揭示了经济是如何运行的，映射出当今经济体制与政策暗藏的漏洞。希夫兄弟以机智幽默的手法阐释了经济增长的根源，贸易、储蓄及风险三者的重要性，滞胀的根源，利率的影响，政府的刺激机制、消费信贷的破坏性本质等问题。这些问题经常被讨论，却常常被误解。对于那些想走出误区，理解经济学基本理论与常识的读者，这本书会带来惊喜。</li><li><strong>出版时间</strong>： 2016-09-01 00:00:00</li><li><strong>ISBN</strong>： 9787508649221</li><li><strong>分类</strong>： 经济理财-财经</li><li><strong>出版社</strong>： 中信出版集团</li><li><strong>PC地址</strong>： <a href="https://weread.qq.com/web/reader/a38326805dec21a389a21c8" target="_blank" rel="noreferrer">https://weread.qq.com/web/reader/a38326805dec21a389a21c8</a></li></ul><h2 id="推荐序" tabindex="-1">推荐序 <a class="header-anchor" href="#推荐序" aria-label="Permalink to &quot;推荐序&quot;">​</a></h2><blockquote><p>📌 政府通过通货膨胀稀释民众的财富，通过悄悄盗用民众的财富满足所谓的经济干预计划，而这些计划无不是被种种动听的承诺包装着，尽管这些动听的承诺很少真正兑现。 ⏱ 2019-05-05 23:09:39</p></blockquote><h2 id="珍藏版序言" tabindex="-1">珍藏版序言 <a class="header-anchor" href="#珍藏版序言" aria-label="Permalink to &quot;珍藏版序言&quot;">​</a></h2><blockquote><p>📌 量化宽松不过是“印钞票”的一种花哨的委婉说法而已。自2010年起，美联储凭空印发了数万亿美元用来购买资产，包括政府支持的债券和抵押贷款债券。 ⏱ 2019-05-05 23:14:12</p></blockquote><h2 id="序言" tabindex="-1">序言 <a class="header-anchor" href="#序言" aria-label="Permalink to &quot;序言&quot;">​</a></h2><blockquote><p>📌 几乎没有经济学家明白他们的学科如何发挥作用。 ⏱ 2019-05-05 23:17:09</p></blockquote><blockquote><p>📌 也许是觉得经济学理论太枯燥，需要加点儿新鲜手法，于是凯恩斯提出了一个类似量子力学的经济学研究视角，创造了两套经济规律，一套作用于微观层面（与个人及家庭生活相关），另一套作用于宏观层面（与国家和政府相关）。 ⏱ 2019-05-05 23:17:49</p></blockquote><h2 id="推荐序-1" tabindex="-1">推荐序 <a class="header-anchor" href="#推荐序-1" aria-label="Permalink to &quot;推荐序&quot;">​</a></h2><h3 id="划线评论" tabindex="-1">划线评论 <a class="header-anchor" href="#划线评论" aria-label="Permalink to &quot;划线评论&quot;">​</a></h3><blockquote><p>📌 虚幻的GDP（国内生产总值）数字<br> - 💭 名与实不等同，GDP是有实际增量意义的 - ⏱ 2019-05-05 23:11:37</p></blockquote>',13)])])}const q=a(l,[["render",n]]);export{_ as __pageData,q as default};
