import{_ as t,o as r,c as a,a6 as o}from"./chunks/framework.BB0md0jN.js";const p=JSON.parse('{"title":"工作、消费主义和新穷人","description":"","frontmatter":{"layout":"doc","title":"工作、消费主义和新穷人","readingTime":"2 min read"},"headers":[],"relativePath":"电影和阅读/微信读书/工作、消费主义和新穷人.md","filePath":"电影和阅读/微信读书/工作、消费主义和新穷人.md"}'),l={name:"电影和阅读/微信读书/工作、消费主义和新穷人.md"};function n(i,e,s,c,_,d){return r(),a("div",null,[...e[0]||(e[0]=[o('<h1 id="工作、消费主义和新穷人" tabindex="-1">工作、消费主义和新穷人 <a class="header-anchor" href="#工作、消费主义和新穷人" aria-label="Permalink to &quot;工作、消费主义和新穷人&quot;">​</a></h1><p><img src="https://wfqqreader-1252317822.image.myqcloud.com/cover/972/41598972/t6_41598972.jpg" alt=" 工作、消费主义和新穷人"></p><ul><li><strong>书名</strong>： 工作、消费主义和新穷人</li><li><strong>作者</strong>： 齐格蒙特·鲍曼</li><li><strong>简介</strong>： 在生产者和普遍就业的社会中，贫穷是一回事。在消费者社会中，穷人是另一回事。在后者社会中，生活项目围绕消费者的选择而建立，而不是围绕工作、专业技能而建立。“贫穷”曾经与失业联系在一起，如今，它主要指向有缺陷消费者的困境。这种差异改变了贫穷的体验方式，对于拯救苦难产生重大影响。著名社会学家和思想家鲍曼的这部作品，对于消费者社会及其影响进行了反思和论述。在本书中，鲍曼书追溯现代历史上发生的这种变化，对其社会后果进行盘点，并考虑了与贫困作斗争和减轻困苦的各种方式的有效性。</li><li><strong>出版时间</strong>： 2021-09-10 00:00:00</li><li><strong>ISBN</strong>： 9787552036107</li><li><strong>分类</strong>： 社会文化-社科</li><li><strong>出版社</strong>： 上海社会科学院出版社有限公司</li><li><strong>PC地址</strong>： <a href="https://weread.qq.com/web/reader/c9d327e0727abffcc9d74ab" target="_blank" rel="noreferrer">https://weread.qq.com/web/reader/c9d327e0727abffcc9d74ab</a></li></ul><h2 id="导言" tabindex="-1">导言 <a class="header-anchor" href="#导言" aria-label="Permalink to &quot;导言&quot;">​</a></h2><blockquote><p>📌 如果说“贫穷”曾经来自失业，那么今天它主要来自有缺陷的消费者的困境。 ⏱ 2022-05-25 16:21:50</p></blockquote><blockquote><p>📌 本书试图追溯现代历史上发生的这种变化，并对其后果进行盘点。 ⏱ 2022-05-25 16:23:39</p></blockquote>',6)])])}const m=t(l,[["render",n]]);export{p as __pageData,m as default};
