import{_ as t,o as r,c as a,a6 as o}from"./chunks/framework.BB0md0jN.js";const m=JSON.parse('{"title":"巨人的工具","description":"","frontmatter":{"layout":"doc","title":"巨人的工具","readingTime":"3 min read"},"headers":[],"relativePath":"电影和阅读/微信读书/巨人的工具.md","filePath":"电影和阅读/微信读书/巨人的工具.md"}'),i={name:"电影和阅读/微信读书/巨人的工具.md"};function l(n,e,s,d,_,c){return r(),a("div",null,[...e[0]||(e[0]=[o('<h1 id="巨人的工具" tabindex="-1">巨人的工具 <a class="header-anchor" href="#巨人的工具" aria-label="Permalink to &quot;巨人的工具&quot;">​</a></h1><p><img src="https://wfqqreader-1252317822.image.myqcloud.com/cover/830/23934830/t6_23934830.jpg" alt=" 巨人的工具"></p><ul><li><strong>书名</strong>： 巨人的工具</li><li><strong>作者</strong>： 蒂姆·费里斯</li><li><strong>简介</strong>： 美国知名作家蒂姆·费里斯制作的“蒂姆·费里斯秀”（TheTimFerrisShow）是美国成功的播客节目之一。在每一集节目中，蒂姆都会采访一位美国当前某个领域的精英人士，包括我们熟悉的阿诺德·施瓦辛格、赛斯·高汀、托尼·罗宾斯、彼得·蒂尔、约克·威林克、凯文·科斯特纳、马尔科姆·格拉德威尔、凯文·凯利、保罗·柯艾略、布琳·布朗等。但这本书并非简单的采访合集。作者蒂姆·费里斯讲自己看作一个实验者，收录进本书的智慧都是经过他个人亲自检验的。基于这些采访内容，蒂姆将所有人的成功秘诀进行了分类与提炼，并归纳出“健康”、“财富”与“智慧”这三大工具，而且这三个部分是相辅相成的。这三大工具可以被看作一个三脚架，而成功、幸福的生活正是建立在这三元素相互平衡的基础之上的。读者可以根据自己的需求点，找到适合自己的方法，进行灵活整合，制订专属于自己的人生规划。</li><li><strong>出版时间</strong>： 2018-12-01 00:00:00</li><li><strong>ISBN</strong>： 9787508693248</li><li><strong>分类</strong>： 社会文化-社科</li><li><strong>出版社</strong>： 中信出版集团</li><li><strong>PC地址</strong>： <a href="https://weread.qq.com/web/reader/4b4327a0716d376e4b489f0" target="_blank" rel="noreferrer">https://weread.qq.com/web/reader/4b4327a0716d376e4b489f0</a></li></ul><h3 id="一日之计在于晨" tabindex="-1">一日之计在于晨 <a class="header-anchor" href="#一日之计在于晨" aria-label="Permalink to &quot;一日之计在于晨&quot;">​</a></h3><blockquote><p>📌 如果你每天早晨都整理床铺，那就意味着你每天都至少完成了一项工作。这会给你带来一丝自豪感，会鼓励你接下去做一件又一件工作。 ⏱ 2023-03-07 08:36:17</p></blockquote>',5)])])}const p=t(i,[["render",l]]);export{m as __pageData,p as default};
