import{_ as i,o as t,c as r,a6 as a}from"./chunks/framework.BB0md0jN.js";const d=JSON.parse('{"title":"洞察人性","description":"","frontmatter":{"layout":"doc","title":"洞察人性","readingTime":"3 min read"},"headers":[],"relativePath":"电影和阅读/微信读书/心理/洞察人性.md","filePath":"电影和阅读/微信读书/心理/洞察人性.md"}'),e={name:"电影和阅读/微信读书/心理/洞察人性.md"};function o(p,l,n,s,_,u){return t(),r("div",null,[...l[0]||(l[0]=[a('<h1 id="洞察人性" tabindex="-1">洞察人性 <a class="header-anchor" href="#洞察人性" aria-label="Permalink to &quot;洞察人性&quot;">​</a></h1><p><img src="https://wfqqreader-1252317822.image.myqcloud.com/cover/888/918888/t7_918888.jpg" alt=" 洞察人性"></p><ul><li><p><strong>书名</strong>： 洞察人性</p></li><li><p><strong>作者</strong>： 阿尔弗雷德·阿德勒</p></li><li><p><strong>简介</strong>： “洞察人性”是一个很大的课题，自古以来就是我们的研究目标，但它并不是一门只允许少数专家研究的科学，其本质决定了人性必须由全人类共同探讨，协力挖掘其蕴含的秘密。《洞察人性》一书旨在普及心理学的基本原理，通过这些原理解释我们与生活的世界、人类活动的关系，并阐释了这些原理在群体生活中的应用，对人的性格进行了科学的剖析。阿德勒曾说，创作本书有三个目的：指出错误的个体行为会破坏社会和群居生活；向普通大众介绍辨别自身错误的方法；告知人们应对方法，以实现人与社会的和谐。</p></li><li><p><strong>出版时间 2017-07-01 00</strong>: 00:00</p></li><li><p><strong>ISBN</strong>： 9787568240345</p></li><li><p><strong>分类</strong>： 心理-心理学应用</p></li><li><p><strong>出版社</strong>： 北京理工大学出版社</p></li></ul><h2 id="引言" tabindex="-1">引言 <a class="header-anchor" href="#引言" aria-label="Permalink to &quot;引言&quot;">​</a></h2><ul><li><p>📌 我们对他人的态度取决于我们与他人的熟悉程度，因此这种熟悉程度构成了社会交往的基础。</p><ul><li>⏱ 2018-08-19 02:08:46</li></ul></li><li><p>📌 我们深刻地意识到，相互理解的缺乏正是造成大多数困难和分歧的根源，它使我们易于误解他人或被他人的表面行为所欺骗。</p><ul><li>⏱ 2018-08-19 02:09:04</li></ul></li><li><p>📌 我们的第一个发现是：人类灵魂根源在于童年早期。</p><ul><li>⏱ 2018-08-19 02:18:16</li></ul></li><li><p>📌 隐蔽的童年想法决定了成人的生活态度。</p><ul><li>⏱ 2018-08-19 02:27:54</li></ul></li><li><p>📌 一个惊人的事实是：从心理发展的角度来讲，儿童和成人的思维并没有差别，尽管有些想法的外在行为和语言发生了变化，但童年的根本目标和动机一直都指向相同的心理学目标，并未发生变化。</p><ul><li>⏱ 2018-08-19 02:28:21</li></ul></li></ul>',5)])])}const g=i(e,[["render",o]]);export{d as __pageData,g as default};
