import{_ as t,o as r,c as a,a6 as o}from"./chunks/framework.BB0md0jN.js";const p=JSON.parse('{"title":"我们为什么爱宋朝：重新发现造极之世","description":"","frontmatter":{"layout":"doc","title":"我们为什么爱宋朝：重新发现造极之世","readingTime":"3 min read"},"headers":[],"relativePath":"电影和阅读/微信读书/我们为什么爱宋朝_重新发现造极之世.md","filePath":"电影和阅读/微信读书/我们为什么爱宋朝_重新发现造极之世.md"}'),n={name:"电影和阅读/微信读书/我们为什么爱宋朝_重新发现造极之世.md"};function _(l,e,s,i,d,c){return r(),a("div",null,[...e[0]||(e[0]=[o('<h1 id="我们为什么爱宋朝-重新发现造极之世" tabindex="-1">我们为什么爱宋朝：重新发现造极之世 <a class="header-anchor" href="#我们为什么爱宋朝-重新发现造极之世" aria-label="Permalink to &quot;我们为什么爱宋朝：重新发现造极之世&quot;">​</a></h1><p><img src="https://cdn.weread.qq.com/weread/cover/9/YueWen_23874306/t6_YueWen_23874306.jpg" alt=" 我们为什么爱宋朝：重新发现造极之世"></p><ul><li><strong>书名</strong>： 我们为什么爱宋朝：重新发现造极之世</li><li><strong>作者</strong>： 贾冬婷 杨璐</li><li><strong>简介</strong>： 谈及宋朝，现代人的态度很明显地分为两大类。一类人认为，宋朝积贫积弱；另一类人则大爱宋朝，最想穿越回去的朝代就是宋朝。而汉学家们普遍认为，尽管从军事威力和势力范围来衡量，宋朝是个虚弱的朝代，但就经济和社会繁荣程度而言，宋朝确是中国历史上最具人文精神、最有教养、最有思想的朝代之一。在宋朝，取代宵禁，中国历史上开始出现通宵达旦的夜市，都市经济发展，市民阶层兴起并大放异彩。同时，士大夫阶层出现，“寒俊”崛起，成为文明传播扩散的重要一环。那些在乡不在仕的人也成为文化的骑手，使风雅成为宋代的一时之风。正如陈寅恪先生所言：“华夏民族之文化，历数千载之演进，造极于赵宋之世。”当我们向自身文化寻找，无论是中国传统文化和审美的高峰，还是艺术与生活通融的生活美学源头，都当推宋朝。特别是宋代士人的修养和美学趣味，通过儒家思想的一脉相承，一直为后世所追慕。《我们为什么爱宋朝》一书，致力于用最好的文笔，最严谨的态度，讲述宋朝的人文风物，还原宋朝的真实面目于大众。真的宋朝，超乎你的想象。</li><li><strong>出版时间</strong>： 2018-07-01 00:00:00</li><li><strong>ISBN</strong>： 9787508688077</li><li><strong>分类</strong>： 历史-中国古代</li><li><strong>出版社</strong>： 中信出版集团</li><li><strong>PC地址</strong>： <a href="https://weread.qq.com/web/reader/2d132f90716c4b022d1e66e" target="_blank" rel="noreferrer">https://weread.qq.com/web/reader/2d132f90716c4b022d1e66e</a></li></ul><h3 id="金瓯缺-两宋王朝的真实与想象" tabindex="-1">金瓯缺：两宋王朝的真实与想象 <a class="header-anchor" href="#金瓯缺-两宋王朝的真实与想象" aria-label="Permalink to &quot;金瓯缺：两宋王朝的真实与想象&quot;">​</a></h3><blockquote><p>📌 其时，赵匡胤尚有一项兼职“归德军节度使”，治所在宋州（今河南商丘），系春秋时代宋国故地。是故由他开创的新王朝，便以“宋”命名。 ⏱ 2024-10-03 02:19:00</p></blockquote>',5)])])}const m=t(n,[["render",_]]);export{p as __pageData,m as default};
