import{_ as e,o as r,c as a,a6 as o}from"./chunks/framework.BB0md0jN.js";const p=JSON.parse('{"title":"控糖革命","description":"","frontmatter":{"layout":"doc","title":"控糖革命","readingTime":"3 min read"},"headers":[],"relativePath":"电影和阅读/微信读书/控糖革命.md","filePath":"电影和阅读/微信读书/控糖革命.md"}'),l={name:"电影和阅读/微信读书/控糖革命.md"};function n(s,t,i,c,_,d){return r(),a("div",null,[...t[0]||(t[0]=[o('<h1 id="控糖革命" tabindex="-1">控糖革命 <a class="header-anchor" href="#控糖革命" aria-label="Permalink to &quot;控糖革命&quot;">​</a></h1><p><img src="https://cdn.weread.qq.com/weread/cover/64/cpplatform_qpqefbrrjjgqq7t62wt1ca/t6_cpplatform_qpqefbrrjjgqq7t62wt1ca1706772686.jpg" alt=" 控糖革命"></p><ul><li><strong>书名</strong>： 控糖革命</li><li><strong>作者</strong>： 【法】杰西·安佐斯佩</li><li><strong>简介</strong>： 对于现代人来说，忙着减肥、忙着美肤、忙着健身，但同时失眠、焦虑、痤疮、糖尿病等亚健康问题又如影随形。法国“葡萄糖女神”、生物化学家杰西·安佐斯佩告诉你，这些亚健康问题往往都是由我们体内的血糖水平变化引起的，只是我们不自知而已。若置之不理，皮肤问题、情绪问题往往就会接踵而至，情况严重的话，更会引发2型糖尿病、多囊卵巢综合征等身体问题。杰西·安佐斯佩在《控糖革命》一书中，提出了三个关键性的、颠覆常识的观点：1.不是只有糖尿病人才需要关心血糖，80%的现代人都有血糖方面的问题；2.我们不只要控制血糖，更重要的是要控制血糖峰值的变化；3.比节食更有效的控糖方法是改变吃的顺序。全书分为三大部分——为什么要控糖、出现葡萄糖峰值有哪些危害、轻松控糖的10个小窍门，通过8个章节，系统阐释了如何在不节食、不放弃我们喜爱的食物的情况下，用10个简单而令人惊讶的方法平衡我们的血糖水平，提升和改善我们的健康状况，包括睡眠、情绪、健康、皮肤、体重，甚至减缓衰老。</li><li><strong>出版时间</strong>： 2024-01-28 00:00:00</li><li><strong>ISBN</strong>： 9787573908896</li><li><strong>分类</strong>： 医学健康-健康</li><li><strong>出版社</strong>： 浙江科学技术出版社</li><li><strong>PC地址</strong>： <a href="https://weread.qq.com/web/reader/819321e0813ab880ag01960c" target="_blank" rel="noreferrer">https://weread.qq.com/web/reader/819321e0813ab880ag01960c</a></li></ul><h2 id="前言-聆听来自身体的声音" tabindex="-1">前言 聆听来自身体的声音 <a class="header-anchor" href="#前言-聆听来自身体的声音" aria-label="Permalink to &quot;前言 聆听来自身体的声音&quot;">​</a></h2><blockquote><p>📌 对于大多数人来说，身体就像一个黑匣子：我们知道它的功能，但又说不清楚它是怎样工作的。我们经常会根据自己的意愿和周边信息来决定中午吃什么，而不是根据身体的真正所需。 ⏱ 2024-04-16 20:44:48</p></blockquote>',5)])])}const q=e(l,[["render",n]]);export{p as __pageData,q as default};
