import{_ as t,o as r,c as a,a6 as o}from"./chunks/framework.BB0md0jN.js";const p=JSON.parse('{"title":"理想国","description":"","frontmatter":{"layout":"doc","title":"理想国","readingTime":"3 min read"},"headers":[],"relativePath":"电影和阅读/微信读书/理想国.md","filePath":"电影和阅读/微信读书/理想国.md"}'),n={name:"电影和阅读/微信读书/理想国.md"};function l(s,e,i,_,d,c){return r(),a("div",null,[...e[0]||(e[0]=[o('<h1 id="理想国" tabindex="-1">理想国 <a class="header-anchor" href="#理想国" aria-label="Permalink to &quot;理想国&quot;">​</a></h1><p><img src="https://cdn.weread.qq.com/weread/cover/23/yuewen_41507093/t6_yuewen_415070931718692881.jpg" alt=" 理想国"></p><ul><li><strong>书名</strong>： 理想国</li><li><strong>作者</strong>： 【古希腊】柏拉图</li><li><strong>简介</strong>： 没有人怀疑《理想国》是西方哲学最伟大的作品之一。它把哲学上和文学上的资源用最高形式结合起来，试图回答所有问题当中最重要的问题——如果我们想要满意幸福地生活，那么我们究竟应该怎样生活——达到了可以说是前无古人、鲜有来者的高度。应当正义地生活或是非正义地生活？道德地生活或是不道德地生活？另外，本书提出的答案是以异常丰富的叙述为基础，这些叙述是关于我们的本质以及客观存在的本质。在本书中伦理学、政治学、美学、宗教哲学、精神哲学、科学哲学、认识论以及形而上学交织在一起，本书对以上学科分别做出了贡献，并对这些学科以后的发展形式形成了决定性的影响。当代的哲学家像他们的前辈一样阅读《理想国》，他们这样做不是因为对于柏拉图的虔诚，而是因为该书继续引起挑战、不安和带来启发。可以确定地说，西方哲学并不仅仅是对本书那了不起文字的一系列注释，但是可以说它们很多故事都是从这本书开始的。</li><li><strong>出版时间</strong>： 2024-06-18 00:00:00</li><li><strong>ISBN</strong>： &gt; - 分类： 哲学宗教-西方哲学</li><li><strong>出版社</strong>： &gt; - PC地址：<a href="https://weread.qq.com/web/reader/78832a0072795915788b394" target="_blank" rel="noreferrer">https://weread.qq.com/web/reader/78832a0072795915788b394</a></li></ul><h2 id="前言" tabindex="-1">前言 <a class="header-anchor" href="#前言" aria-label="Permalink to &quot;前言&quot;">​</a></h2><blockquote><p>📌 把哲学上和文学上的资源用最高形式结合起来，试图回答所有问题当中最重要的问题——如果我们想要满意幸福地生活，那么我们究竟应该怎样生活——达到了可以说是前无古人、鲜有来者的高度。 ⏱ 2021-12-30 00:21:45</p></blockquote>',5)])])}const m=t(n,[["render",l]]);export{p as __pageData,m as default};
