import{_ as t,o as r,c as a,a6 as o}from"./chunks/framework.BB0md0jN.js";const p=JSON.parse('{"title":"恶俗：或现代文明的种种愚蠢","description":"","frontmatter":{"layout":"doc","title":"恶俗：或现代文明的种种愚蠢","readingTime":"3 min read"},"headers":[],"relativePath":"电影和阅读/微信读书/社会文化_社科/恶俗_或现代文明的种种愚蠢.md","filePath":"电影和阅读/微信读书/社会文化_社科/恶俗_或现代文明的种种愚蠢.md"}'),n={name:"电影和阅读/微信读书/社会文化_社科/恶俗_或现代文明的种种愚蠢.md"};function _(l,e,s,i,d,c){return r(),a("div",null,[...e[0]||(e[0]=[o('<h1 id="恶俗-或现代文明的种种愚蠢" tabindex="-1">恶俗：或现代文明的种种愚蠢 <a class="header-anchor" href="#恶俗-或现代文明的种种愚蠢" aria-label="Permalink to &quot;恶俗：或现代文明的种种愚蠢&quot;">​</a></h1><p><img src="https://cdn.weread.qq.com/weread/cover/74/YueWen_24191005/t6_YueWen_24191005.jpg" alt=" 恶俗：或现代文明的种种愚蠢"></p><ul><li><strong>书名</strong>： 恶俗：或现代文明的种种愚蠢</li><li><strong>作者</strong>： 保罗·福塞尔</li><li><strong>简介</strong>： 广告恶俗？追随名流恶俗？带手机参加聚会也恶俗？到底什么是恶俗？恶俗与糟糕有何区别？跟愚蠢又有什么关系？如本书所说，恶俗就是弄虚作假、装腔作势却恬不知耻；是餐馆、酒店、电影、电视、大学等各个领域充斥着的虚伪、俗艳和无知；是以丑为美、以假为真、以浅薄为深刻、以愚昧为智慧。本书延续了《格调》的毒舌写法，通过无情的揭露和入骨的批判，展现出现代文明社会种种光怪陆离的现象，敏锐地捕捉到了这个商业欺诈时代最大的特点——恶俗，并剖析了恶俗的本质、根源和未来，尖锐刺耳又不失幽默有趣。虽然极尽嘲讽之能事，作者之意却不在于讽刺挖苦，而是希望借此唤醒大众、反省大众文化，本书因此称得上是一部警世诤言。</li><li><strong>出版时间</strong>： 2017-02-01 00:00:00</li><li><strong>ISBN</strong>： 9787550292901</li><li><strong>分类</strong>： 社会文化-社科</li><li><strong>出版社</strong>： 北京联合出版公司</li><li><strong>PC地址</strong>： <a href="https://weread.qq.com/web/reader/3ee326007171201d3eefa0b" target="_blank" rel="noreferrer">https://weread.qq.com/web/reader/3ee326007171201d3eefa0b</a></li></ul><h2 id="推荐序" tabindex="-1">推荐序 <a class="header-anchor" href="#推荐序" aria-label="Permalink to &quot;推荐序&quot;">​</a></h2><blockquote><p>📌 一个古老的国家几乎与一个新兴的国家一样，在激烈的变革之后都失去了传统，在没有传统规范可循的时代里，一切人类天性中未经磨炼的欲望便会沿着粗俗突兀的路径生长。其结果，必然是随处可见的恶俗事物。 ⏱ 2019-07-25 09:05:42</p></blockquote>',5)])])}const m=t(n,[["render",_]]);export{p as __pageData,m as default};
