import{_ as e,o as a,c as r,a6 as o}from"./chunks/framework.BB0md0jN.js";const g=JSON.parse('{"title":"别说你会刷朋友圈 微信营销这件小事","description":"","frontmatter":{"layout":"doc","title":"别说你会刷朋友圈 微信营销这件小事","readingTime":"5 min read"},"headers":[],"relativePath":"电影和阅读/微信读书/经济理财_商业/别说你会刷朋友圈_微信营销这件小事.md","filePath":"电影和阅读/微信读书/经济理财_商业/别说你会刷朋友圈_微信营销这件小事.md"}'),l={name:"电影和阅读/微信读书/经济理财_商业/别说你会刷朋友圈_微信营销这件小事.md"};function _(i,t,n,s,d,c){return a(),r("div",null,[...t[0]||(t[0]=[o('<h1 id="别说你会刷朋友圈-微信营销这件小事" tabindex="-1">别说你会刷朋友圈 微信营销这件小事 <a class="header-anchor" href="#别说你会刷朋友圈-微信营销这件小事" aria-label="Permalink to &quot;别说你会刷朋友圈 微信营销这件小事&quot;">​</a></h1><p><img src="https://wfqqreader-1252317822.image.myqcloud.com/cover/293/640293/t6_640293.jpg" alt=" 别说你会刷朋友圈 微信营销这件小事"></p><ul><li><strong>书名</strong>： 别说你会刷朋友圈 微信营销这件小事</li><li><strong>作者</strong>： 曹斯明</li><li><strong>简介</strong>： 在移动互联网冲击各行业的大背景下，微信如同黑马一般闯入了社会化媒体营销的大营，备受关注。微信超大的用户群体以及超高的用户粘性使其具有非凡的营销潜力，其平台功能的不断增加也可以承载愈加丰富的营销形式。《别说你会刷朋友圈 微信营销这件小事》就是网络营销专家曹斯明讲解如何利用新媒体、新平台，在O2O时代产生最大的营销效果，致胜“微营销”。书本书是最新版本的微信营销攻略，作者凭借多年营销经验以及微信营销开荒者的阅历，通过微信实用指南与热门案例权威讲解，以及独家操作技巧向读者讲解微信营销的道与术。为那一场，数字营销的狼烟逐鹿。数字营销的方法论进化历程，永远和互联网产品密不可分，每每有跨时代的互联网产品横空出世，也同步暗示着可以被颠覆与可以被重新书写的史诗卷轴的延展而在微信逐步走向世人的岁月。</li><li><strong>出版时间</strong>： 2014-12-01 00:00:00</li><li><strong>ISBN</strong>： 9787547033630</li><li><strong>分类</strong>： 经济理财-商业</li><li><strong>出版社</strong>： 万卷出版公司</li><li><strong>PC地址</strong>： <a href="https://weread.qq.com/web/reader/d393217059c525d39daa579" target="_blank" rel="noreferrer">https://weread.qq.com/web/reader/d393217059c525d39daa579</a></li></ul><blockquote><p>📌 你所营销的目标群体对于微信公众平台未必有你想象得那么感冒，订阅号推送的消息一直积攒着没人看，服务号的消息距离太久又丢失了媒体黏性，并且如何获取用户也成为了一个大难题，毕竟微信公众平台无法主动添加。 ⏱ 2018-05-30 15:02:10</p></blockquote><h2 id="朋友圈" tabindex="-1">朋友圈 <a class="header-anchor" href="#朋友圈" aria-label="Permalink to &quot;朋友圈&quot;">​</a></h2><h3 id="划线评论" tabindex="-1">划线评论 <a class="header-anchor" href="#划线评论" aria-label="Permalink to &quot;划线评论&quot;">​</a></h3><blockquote><p>📌 你觉得这还能算得上是营销吗？营销起码要有一个媒介作为载体才能够实现销售，我告诉你，这完全可以实现销售，所用的载体只有一个——微信朋友圈。 你还在迷信地以为不花钱就做不到宣传和扩散？你还在央求着电视台、报社的记者去给你发通稿？改掉你那种营销习惯吧，让我给你讲一个故事。 2013年9月5日，蓝港在线董事长、土曼联合创始人王峰创下了微信朋友圈卖货的纪录，他在短短的11个小时里通过微信朋友圈卖出总计73698只土曼T-Watch智能手表，预售订单额36775302元！按10条微信计算，平均每条微信等于367万元！ 事件的起因是，王峰在吐槽了三星智能手表之后，在朋友圈内晒出了土曼智能手表的设计图，土曼手表以其时尚的造型得到了大众的关注。在看到大家询问后，王峰决定在朋友圈内尝试预售，于是当天土曼T-Watch智能手表以499元超低价预售，结果这一条朋友圈消息发出去之后一发而不可收拾，引发微信朋友圈的巨大轰动，本打算预售的999只土曼智能手表被一扫而空，另外应朋友们“强烈控诉”<br> - 💭 他的朋友圈是中国最优质最成功的一群人，用特例来佐证论点是不自觉的蠢和坏 - ⏱ 2018-05-30 15:11:35</p></blockquote>',7)])])}const p=e(l,[["render",_]]);export{g as __pageData,p as default};
