import{_ as t,o as r,c as a,a6 as o}from"./chunks/framework.BB0md0jN.js";const p=JSON.parse('{"title":"中国城市大洗牌","description":"","frontmatter":{"layout":"doc","title":"中国城市大洗牌","readingTime":"3 min read"},"headers":[],"relativePath":"电影和阅读/微信读书/经济理财_财经/中国城市大洗牌.md","filePath":"电影和阅读/微信读书/经济理财_财经/中国城市大洗牌.md"}'),n={name:"电影和阅读/微信读书/经济理财_财经/中国城市大洗牌.md"};function l(s,e,i,_,d,c){return r(),a("div",null,[...e[0]||(e[0]=[o('<h1 id="中国城市大洗牌" tabindex="-1">中国城市大洗牌 <a class="header-anchor" href="#中国城市大洗牌" aria-label="Permalink to &quot;中国城市大洗牌&quot;">​</a></h1><p><img src="https://cdn.weread.qq.com/weread/cover/26/YueWen_33396343/t6_YueWen_33396343.jpg" alt=" 中国城市大洗牌"></p><ul><li><strong>书名</strong>： 中国城市大洗牌</li><li><strong>作者</strong>： 黄汉城 史哲 林小琬</li><li><strong>简介</strong>： 当下的中国区域版图，正在面临数十年未有之巨变。北京上海两个超级城市开始做减量发展、雄安新区横空出世、海南自贸港水落石出、粤港澳大湾区翻开“一国两制”的新篇章……过去二十年楼市狂飙突进，地方政府与房企联袂出演，造就了一批批暴富的食利阶层。如今的房地产被踩了刹车，正式告别黄金时代，进入极为分化的新时期。在大都市圈时代下，人口变迁、资本流动的规律宣告了中小城市的大败局。面对国内外形势的风云突变，一二线城市转型升级的使命感尤为突出。国家意志与市场力量相互交织，深刻重塑着今天的中国。城与国，可谓辅车相依。过去四十年来，中国上下一心，筚路蓝缕，开辟了一个又一个经济奇迹，也缔造了一个又一个光荣之城。如今，在这个充满不确定性的时代里，我们更加有必要审视中国的微观镜像，重新梳理和推演区域经济的演变路径。或许只有这样，才能更好的“以启山林”，直面挑战。本书采用严格的理论与数据分析，为我们揭示了城市大洗牌的内在逻辑，堪称未来三十年国人的生存指南。</li><li><strong>出版时间</strong>： 2020-01-01 00:00:00</li><li><strong>ISBN</strong>： 9787520712101</li><li><strong>分类</strong>： 经济理财-财经</li><li><strong>出版社</strong>： 东方出版社</li><li><strong>PC地址</strong>： <a href="https://weread.qq.com/web/reader/2f532de071fd96772f58025" target="_blank" rel="noreferrer">https://weread.qq.com/web/reader/2f532de071fd96772f58025</a></li></ul><h3 id="一份超级文件出世-变相捅破-限购" tabindex="-1">一份超级文件出世，变相捅破“限购” <a class="header-anchor" href="#一份超级文件出世-变相捅破-限购" aria-label="Permalink to &quot;一份超级文件出世，变相捅破“限购”&quot;">​</a></h3><blockquote><p>📌 中小城市已死，未来是大都市圈的天下。 ⏱ 2020-11-23 09:36:29</p></blockquote>',5)])])}const f=t(n,[["render",l]]);export{p as __pageData,f as default};
