import{_ as t,o,c as r,a6 as a}from"./chunks/framework.BB0md0jN.js";const u=JSON.parse('{"title":"脑与阅读","description":"","frontmatter":{"layout":"doc","title":"脑与阅读","readingTime":"4 min read"},"headers":[],"relativePath":"电影和阅读/微信读书/脑与阅读.md","filePath":"电影和阅读/微信读书/脑与阅读.md"}'),l={name:"电影和阅读/微信读书/脑与阅读.md"};function n(i,e,s,c,d,_){return o(),r("div",null,[...e[0]||(e[0]=[a('<h1 id="脑与阅读" tabindex="-1">脑与阅读 <a class="header-anchor" href="#脑与阅读" aria-label="Permalink to &quot;脑与阅读&quot;">​</a></h1><p><img src="https://cdn.weread.qq.com/weread/cover/67/YueWen_25023797/t6_YueWen_25023797.jpg" alt=" 脑与阅读"></p><ul><li><strong>书名</strong>： 脑与阅读</li><li><strong>作者</strong>： 斯坦尼斯拉斯·迪昂</li><li><strong>简介</strong>： 《脑与阅读》是有“神经科学界诺贝尔奖”之称的大脑奖获得者、脑科学领域的大神级专家斯坦尼斯拉斯·迪昂为读者带来一场阅读、教育和终身学习的知识盛宴。本书先为读者展现了人脑神奇的阅读能力，像拆解钟表的精密结构一样，揭示了阅读在脑中的认知齿轮，回答了“我们是如何阅读的？”这一问题。然后通过考察儿童是如何习得阅读的，向我们明示什么样的方法是科学有效的，对目前教育实践中错误的阅读学习方法进行了抨击，就“应该如何学习阅读？”给出了答案。同时，作者用科学研究成果论证了阅读的价值，揭示了“阅读是如何塑造我们的大脑的”。书中还讨论了拼写的隐藏逻辑、文字的发现、文化的诞生等话题，试图探究这样一个有趣的问题——“为什么只有人类创造出了如此精细而复杂的文化？”在这个信息快速运转的时代，每一种事物、每一个行业，迟早会迎来无法预测的变化，终身学习成为时代的要求。而一个人一生如果想要获得过人的成就，注定与读书和终身学习形影不离。想要真正了解阅读，真正实践终身学习，不妨拿起这本书，发现阅读的方法、价值与乐趣，享受阅读带来的改变，成为新世纪的优秀人才！</li><li><strong>出版时间</strong>： 2018-11-01 00:00:00</li><li><strong>ISBN</strong>： 9787553677316</li><li><strong>分类</strong>： 科学技术-科学科普</li><li><strong>出版社</strong>： 浙江教育出版社</li><li><strong>PC地址</strong>： <a href="https://weread.qq.com/web/reader/534328e0717dd535534a74e" target="_blank" rel="noreferrer">https://weread.qq.com/web/reader/534328e0717dd535534a74e</a></li></ul><h2 id="推荐序-开启阅读认知的新篇章" tabindex="-1">推荐序 开启阅读认知的新篇章 <a class="header-anchor" href="#推荐序-开启阅读认知的新篇章" aria-label="Permalink to &quot;推荐序 开启阅读认知的新篇章&quot;">​</a></h2><blockquote><p>📌 迪昂在《脑与阅读》这本书中，运用大量的实证研究证据，雄辩地提出并论证了他的阅读是“神经元再利用”的假说。 ⏱ 2021-09-13 22:22:21</p></blockquote><blockquote><p>📌 认为人脑的结构会受到遗传的限制，同时神经回路又具有一定可塑性。 ⏱ 2021-09-13 22:28:08</p></blockquote><blockquote><p>📌 而阅读能力的形成则需要经过后天的教育。良好的阅读教育能够使人拥有这种跨越时空、获得他人智慧的技能。 ⏱ 2021-09-13 22:28:25</p></blockquote><h2 id="导论-关于阅读的科学" tabindex="-1">导论 关于阅读的科学 <a class="header-anchor" href="#导论-关于阅读的科学" aria-label="Permalink to &quot;导论 关于阅读的科学&quot;">​</a></h2><blockquote><p>📌 10%的成人甚至不能掌握最基本的语篇理解技能。阅读能力背后的大脑运作机制如时钟般精巧，我们几乎意识不到它的存在，但是要达到自动化的熟练阅读水平，却需要多年的努力。 ⏱ 2021-09-27 09:07:52</p></blockquote>',9)])])}const g=t(l,[["render",n]]);export{u as __pageData,g as default};
