import{_ as t,o as a,c as r,a6 as o}from"./chunks/framework.BB0md0jN.js";const M=JSON.parse('{"title":"深入浅出MySQL：数据库开发、优化与管理维护（第2版）","description":"","frontmatter":{"layout":"doc","title":"深入浅出MySQL：数据库开发、优化与管理维护（第2版）","readingTime":"5 min read"},"headers":[],"relativePath":"电影和阅读/微信读书/计算机_数据库/深入浅出MySQL_数据库开发、优化与管理维护第2版.md","filePath":"电影和阅读/微信读书/计算机_数据库/深入浅出MySQL_数据库开发、优化与管理维护第2版.md"}'),n={name:"电影和阅读/微信读书/计算机_数据库/深入浅出MySQL_数据库开发、优化与管理维护第2版.md"};function l(_,e,s,i,L,S){return a(),r("div",null,[...e[0]||(e[0]=[o('<h1 id="深入浅出mysql-数据库开发、优化与管理维护-第2版" tabindex="-1">深入浅出MySQL：数据库开发、优化与管理维护（第2版） <a class="header-anchor" href="#深入浅出mysql-数据库开发、优化与管理维护-第2版" aria-label="Permalink to &quot;深入浅出MySQL：数据库开发、优化与管理维护（第2版）&quot;">​</a></h1><p><img src="https://cdn.weread.qq.com/weread/cover/72/YueWen_730561/t6_YueWen_730561.jpg" alt=" 深入浅出MySQL：数据库开发、优化与管理维护（第2版）"></p><ul><li><strong>书名</strong>： 深入浅出MySQL：数据库开发、优化与管理维护（第2版）</li><li><strong>作者</strong>： 唐汉明 翟振兴 关宝军 王洪权 黄潇</li><li><strong>简介</strong>： 《深入浅出MySQL：数据库开发、优化与管理维护（第2版）》从数据库的基础、开发、优化、管理维护和架构5个方面对MySQL进行了详细的介绍，每一部分都独立成篇。基础篇主要适合于MySQL的初学者阅读，包括MySQL的安装与配置、SQL基础、MySQL支持的数据类型、MySQL中的运算符、常用函数、图形化工具的使用等内容。开发篇主要适合于MySQL的设计和开发人员阅读，内容包括表类型（存储引擎）的选择、选择合适的数据类型、字符集、索引的设计和使用、视图、存储过程和函数、触发器、事务控制和锁定语句、SQL中的安全问题、SQL Mode及相关问题、分区等。优化篇主要适合于开发人员和数据库管理员阅读，内容包括SQL优化、优化数据库对象、锁问题、优化MySQL Server、磁盘I/O问题、应用优化等。管理维护篇主要适合于数据库管理员阅读，内容包括MySQL高级安装和升级、MySQL中的常用工具、MySQL日志、备份与恢复、MySQL权限与安全、MySQL监控、MySQL常见问题和应用技巧等。架构篇主要适合高级数据库管理人员和数据库架构设计师阅读，包括MySQL复制、MySQL Cluster、高可用架构等内容。《深入浅出MySQL：数据库开发、优化与管理维护（第2版）》的作者都是MySQL方面的资深DBA。本书不但融入了他们丰富的工作经验和多年的使用心得，还提供了大量来自工作现场的实例，具有很强的实战性和可操作性。《深入浅出MySQL：数据库开发、优化与管理维护（第2版）》内容实用，覆盖广泛，讲解由浅入深，适合数据库管理人员、数据库开发人员、系统维护人员、数据库初学者及其他数据库从业人员阅读，也适合用作大中专院校相关专业师生的参考用书和相关培训机构的培训教材。</li><li><strong>出版时间</strong>： 2014-01-01 00:00:00</li><li><strong>ISBN</strong>： 9787115335494</li><li><strong>分类</strong>： 计算机-数据库</li><li><strong>出版社</strong>： 人民邮电出版社</li><li><strong>PC地址</strong>： <a href="https://weread.qq.com/web/reader/f3e327e05b25c1f3e6d5ee2" target="_blank" rel="noreferrer">https://weread.qq.com/web/reader/f3e327e05b25c1f3e6d5ee2</a></li></ul><h3 id="第2章-sql基础" tabindex="-1">第2章 SQL基础 <a class="header-anchor" href="#第2章-sql基础" aria-label="Permalink to &quot;第2章 SQL基础&quot;">​</a></h3><blockquote><p>📌 SQL语句主要可以划分为以下3个类别。DDL（Data Definition Languages）语句：数据定义语言，这些语句定义了不同的数据段、数据库、表、列、索引等数据库对象。常用的语句关键字主要包括create、drop、alter等。DML（Data Manipulation Language）语句：数据操纵语句，用于添加、删除、更新和查询数据库记录，并检查数据完整性。常用的语句关键字主要包括 insert、delete、update和select等。DCL（Data Control Language）语句：数据控制语句，用于控制不同数据段直接的许可和访问级别的语句。这些语句定义了数据库、表、字段、用户的访问权限和安全级别。主要的语句关键字包括grant、revoke等。 ⏱ 2020-08-25 18:02:42</p></blockquote><blockquote><p>📌 它和DML语句的最大区别是DML只是对表内部数据操作，而不涉及表的定义、结构的修改，更不会涉及其他对象。DDL 语句更多地由数据库管理员（DBA）使用，开发人员一般很少使用。 ⏱ 2020-08-25 18:03:12</p></blockquote>',6)])])}const d=t(n,[["render",l]]);export{M as __pageData,d as default};
