import{_ as t,o as r,c as a,a6 as o}from"./chunks/framework.BB0md0jN.js";const p=JSON.parse('{"title":"饥饿的盛世：乾隆时代的得与失","description":"","frontmatter":{"layout":"doc","title":"饥饿的盛世：乾隆时代的得与失","readingTime":"3 min read"},"headers":[],"relativePath":"电影和阅读/微信读书/饥饿的盛世_乾隆时代的得与失.md","filePath":"电影和阅读/微信读书/饥饿的盛世_乾隆时代的得与失.md"}'),n={name:"电影和阅读/微信读书/饥饿的盛世_乾隆时代的得与失.md"};function l(_,e,s,i,d,c){return r(),a("div",null,[...e[0]||(e[0]=[o('<h1 id="饥饿的盛世-乾隆时代的得与失" tabindex="-1">饥饿的盛世：乾隆时代的得与失 <a class="header-anchor" href="#饥饿的盛世-乾隆时代的得与失" aria-label="Permalink to &quot;饥饿的盛世：乾隆时代的得与失&quot;">​</a></h1><p><img src="https://cdn.weread.qq.com/weread/cover/72/yuewen_44752228/t6_yuewen_447522281679305568.jpg" alt=" 饥饿的盛世：乾隆时代的得与失"></p><ul><li><strong>书名</strong>： 饥饿的盛世：乾隆时代的得与失</li><li><strong>作者</strong>： 张宏杰</li><li><strong>简介</strong>： 乾隆的统治占据了18世纪的大部分时间，将康乾盛世推向巅峰。除了康熙、雍正为其打下的良好基础外，自制力强、勤政、处理问题果断迅速、善于吸收历代统治得失、感情生活比较专一、健康长寿等才是乾隆能够成功缔造盛世的重要因素。乾隆统治下的中国，纵向比，是中国几千年历史中人口最多、国力最盛的时期。横向比，是当时世界上最强大、最富庶的国家。然而，乾隆身后却是盛世的崩塌和一个逐渐衰败的大清王朝。乾隆晚年声势浩大的白莲教起义爆发；他死后不到半个世纪，鸦片战争爆发。乾隆是成功的，其成就足以让他跻身中国历史上最伟大的帝王之列；乾隆也是失败的，18世纪世界历史的发展让他的统治显得没有任何新意。从马戛尔尼使团访华事件可以看到，乾隆时期中西历史发展正经历另一种“大分流”。</li><li><strong>出版时间</strong>： 2022-08-01 00:00:00</li><li><strong>ISBN</strong>： 9787229167127</li><li><strong>分类</strong>： 历史-历史读物</li><li><strong>出版社</strong>： 重庆出版社</li><li><strong>PC地址</strong>： <a href="https://weread.qq.com/web/reader/32c322d072aadd6432c59a9" target="_blank" rel="noreferrer">https://weread.qq.com/web/reader/32c322d072aadd6432c59a9</a></li></ul><h2 id="彩插" tabindex="-1">彩插 <a class="header-anchor" href="#彩插" aria-label="Permalink to &quot;彩插&quot;">​</a></h2><blockquote><p>📌 即使在历史上的全部中国人中，历代有文字记载者也只有唐朝钱朗到明代文徵明等6人见到过玄孙。 ⏱ 2023-06-21 11:50:52</p></blockquote>',5)])])}const m=t(n,[["render",l]]);export{p as __pageData,m as default};
