import{_ as r,o as l,c as o,ag as i}from"./chunks/framework.Bx1bucHt.js";const a="/Art_web_public/assets/image-1.bGgJh2Ul.png",s="/Art_web_public/assets/comparison_HP.C-2V1gyO.png",n="/Art_web_public/assets/comparison_MP.D_y2DgiF.png",e="/Art_web_public/assets/comparison_ATK.lWFXQdZU.png",g="/Art_web_public/assets/comparison_DEF.BkbC5JlI.png",h="/Art_web_public/assets/comparison_MATK._BpL4RG7.png",u="/Art_web_public/assets/comparison_MDEF.DWOlAqpF.png",d="/Art_web_public/assets/comparison_SPD.BoqAhRrl.png",_="/Art_web_public/assets/comparison_Agility.Bguf6BR5.png",T=JSON.parse('{"title":"职业数值升级","description":"","frontmatter":{},"headers":[],"relativePath":"项目实践/JRPG/B_角色成长/角色升级/角色数值升级.md","filePath":"项目实践/JRPG/B_角色成长/角色升级/角色数值升级.md"}'),c={name:"项目实践/JRPG/B_角色成长/角色升级/角色数值升级.md"};function p(P,t,m,b,A,q){return l(),o("div",null,[...t[0]||(t[0]=[i('<h1 id="职业数值升级" tabindex="-1">职业数值升级 <a class="header-anchor" href="#职业数值升级" aria-label="Permalink to &quot;职业数值升级&quot;">​</a></h1><h2 id="总体规律" tabindex="-1">总体规律 <a class="header-anchor" href="#总体规律" aria-label="Permalink to &quot;总体规律&quot;">​</a></h2><ul><li><strong>成长速度</strong>：每个职业的属性有不同的成长速度，影响了不同职业在战斗中的角色定位。成长速度较高的属性会在后期增幅更为显著，而成长较低的属性可能在后期增幅较小。通过对比不同职业的属性曲线，可以更好地理解各职业的优势与弱点。</li></ul><h2 id="各职业之间的差异" tabindex="-1">各职业之间的差异 <a class="header-anchor" href="#各职业之间的差异" aria-label="Permalink to &quot;各职业之间的差异&quot;">​</a></h2><p><img src="'+a+'" alt="alt text"></p><h3 id="少侠-light-green" tabindex="-1">少侠 (Light Green) <a class="header-anchor" href="#少侠-light-green" aria-label="Permalink to &quot;少侠 (Light Green)&quot;">​</a></h3><ul><li><strong>高成长属性</strong>：HP、MP、ATK 和 MATK 都表现出较好的成长，特别是 MP（1.15）和 ATK（1.08），可以持续提供稳定的输出和支援。</li><li><strong>低成长属性</strong>：SPD 和 Agility 较低，适合偏向防御或输出的角色，但较难在高速战斗中表现突出。</li></ul><h3 id="刀客-light-pink" tabindex="-1">刀客 (Light Pink) <a class="header-anchor" href="#刀客-light-pink" aria-label="Permalink to &quot;刀客 (Light Pink)&quot;">​</a></h3><ul><li><strong>高成长属性</strong>：ATK 和 SPD 都有不错的成长，特别是 ATK（1.11），能在中后期展现出强大的物理输出能力。</li><li><strong>低成长属性</strong>：MATK 和 MDEF 相对较低，刀客偏向物理输出而非魔法输出，且其魔法防御较弱。</li></ul><h3 id="拳师-burlywood" tabindex="-1">拳师 (Burlywood) <a class="header-anchor" href="#拳师-burlywood" aria-label="Permalink to &quot;拳师 (Burlywood)&quot;">​</a></h3><ul><li><strong>高成长属性</strong>：DEF 和 HP 是拳师最突出的属性，符合其坦克型角色定位。其防御力高，使其能够承受大量伤害。</li><li><strong>低成长属性</strong>：SPD 和 Agility 较低，反映出拳师的机动性较差，更侧重于耐久性和防御。</li></ul><h3 id="武僧-khaki" tabindex="-1">武僧 (Khaki) <a class="header-anchor" href="#武僧-khaki" aria-label="Permalink to &quot;武僧 (Khaki)&quot;">​</a></h3><ul><li><strong>高成长属性</strong>：HP、MP 和 MATK 成长较好，适合兼具生存能力和魔法输出的角色。</li><li><strong>低成长属性</strong>：ATK 和 SPD 较低，武僧并不注重攻击输出或速度，适合耐久型和支援型战斗。</li></ul><h3 id="术士-powder-blue" tabindex="-1">术士 (Powder Blue) <a class="header-anchor" href="#术士-powder-blue" aria-label="Permalink to &quot;术士 (Powder Blue)&quot;">​</a></h3><ul><li><strong>高成长属性</strong>：MP 和 MATK 的成长非常突出，尤其是 MP（1.18），使其成为主要依赖魔法输出的角色。</li><li><strong>低成长属性</strong>：HP 和 ATK 成长较低，术士较弱的防御和物理输出让其在近战时容易受到压制。</li></ul><h3 id="隐士-thistle" tabindex="-1">隐士 (Thistle) <a class="header-anchor" href="#隐士-thistle" aria-label="Permalink to &quot;隐士 (Thistle)&quot;">​</a></h3><ul><li><strong>高成长属性</strong>：SPD 和 Agility 在隐士中表现突出，反映出其较强的机动性和攻击频率。</li><li><strong>低成长属性</strong>：MATK 和 HP 成长一般，隐士更多依赖机动性和敏捷性，而非强大的魔法或生命值。</li></ul><h3 id="商人-sandy-brown" tabindex="-1">商人 (Sandy Brown) <a class="header-anchor" href="#商人-sandy-brown" aria-label="Permalink to &quot;商人 (Sandy Brown)&quot;">​</a></h3><ul><li><strong>高成长属性</strong>：MP 和 MATK 的成长较好，MP（1.13）有助于提供支援技能和魔法输出。</li><li><strong>低成长属性</strong>：ATK 和 SPD 较低，商人更多依赖辅助能力而非攻击力或速度。</li></ul><h3 id="游侠-light-blue" tabindex="-1">游侠 (Light Blue) <a class="header-anchor" href="#游侠-light-blue" aria-label="Permalink to &quot;游侠 (Light Blue)&quot;">​</a></h3><ul><li><strong>高成长属性</strong>：ATK 和 Agility 表现突出，尤其是 Agility（1.10），使其具备较强的机动性和攻击频率，能够迅速做出反应并进行多次攻击。</li><li><strong>低成长属性</strong>：HP 和 DEF 较低，虽然游侠具备强大的攻击能力，但其生存能力相对较弱，需依赖队友支援。</li></ul><h2 id="各属性的规律分析" tabindex="-1">各属性的规律分析 <a class="header-anchor" href="#各属性的规律分析" aria-label="Permalink to &quot;各属性的规律分析&quot;">​</a></h2><h3 id="_1-hp-生命值" tabindex="-1">1. HP (生命值) <a class="header-anchor" href="#_1-hp-生命值" aria-label="Permalink to &quot;1. HP (生命值)&quot;">​</a></h3><ul><li><strong>图表说明：HP 对比</strong><ul><li>从图表可以看出，<strong>少侠</strong>和<strong>武僧</strong>的 HP 成长较快，特别是武僧（1.13）的 HP 成长在所有职业中领先，表现出其作为坦克型职业的特征。</li><li><strong>刀客</strong>的 HP 成长相对较慢，但依然维持了基本的生存能力。</li><li><strong>术士</strong>的 HP 成长较低，反映出其脆弱的生命值和相对较强的魔法输出能力。</li></ul></li></ul><p><img src="'+s+'" alt="HP Comparison"></p><h3 id="_2-mp-魔法值" tabindex="-1">2. MP (魔法值) <a class="header-anchor" href="#_2-mp-魔法值" aria-label="Permalink to &quot;2. MP (魔法值)&quot;">​</a></h3><ul><li><strong>图表说明：MP 对比</strong><ul><li><strong>术士</strong>的 MP 成长在所有职业中是最高的（1.18），表明其主要依赖魔法输出。</li><li><strong>少侠</strong>、<strong>隐士</strong>和<strong>武僧</strong>的 MP 成长也表现较好，适合有一定法术支援需求的职业。</li><li><strong>刀客</strong>和<strong>拳师</strong>的 MP 成长较慢，偏向物理输出，魔法值需求较少。</li></ul></li></ul><p><img src="'+n+'" alt="MP Comparison"></p><h3 id="_3-atk-攻击力" tabindex="-1">3. ATK (攻击力) <a class="header-anchor" href="#_3-atk-攻击力" aria-label="Permalink to &quot;3. ATK (攻击力)&quot;">​</a></h3><ul><li><strong>图表说明：ATK 对比</strong><ul><li><strong>刀客</strong>的 ATK 成长非常突出（1.11），其攻击输出能力在后期将大幅提升。</li><li><strong>少侠</strong>和<strong>拳师</strong>的 ATK 也有不错的成长，适合需要持续输出的角色。</li><li><strong>术士</strong>的 ATK 成长较低，更多依赖魔法输出。</li></ul></li></ul><p><img src="'+e+'" alt="ATK Comparison"></p><h3 id="_4-def-防御力" tabindex="-1">4. DEF (防御力) <a class="header-anchor" href="#_4-def-防御力" aria-label="Permalink to &quot;4. DEF (防御力)&quot;">​</a></h3><ul><li><strong>图表说明：DEF 对比</strong><ul><li><strong>拳师</strong>和<strong>武僧</strong>的 DEF 成长非常突出，拳师的防御在所有职业中排名第一，适合以高耐久性为主的战斗风格。</li><li><strong>刀客</strong>和<strong>少侠</strong>的防御相对较低，更多依赖攻击和机动性。</li><li><strong>术士</strong>的 DEF 成长极低，缺乏防御力，需要依赖队友保护。</li></ul></li></ul><p><img src="'+g+'" alt="DEF Comparison"></p><h3 id="_5-matk-魔法攻击力" tabindex="-1">5. MATK (魔法攻击力) <a class="header-anchor" href="#_5-matk-魔法攻击力" aria-label="Permalink to &quot;5. MATK (魔法攻击力)&quot;">​</a></h3><ul><li><strong>图表说明：MATK 对比</strong><ul><li><strong>术士</strong>的 MATK 成长极为突出（1.12），表明其为魔法输出职业。</li><li><strong>隐士</strong>的 MATK 成长也相对较高，表明其具备一定的魔法攻击能力。</li><li><strong>刀客</strong>和<strong>拳师</strong>的 MATK 成长非常低，几乎没有魔法攻击能力。</li></ul></li></ul><p><img src="'+h+'" alt="MATK Comparison"></p><h3 id="_6-mdef-魔法防御力" tabindex="-1">6. MDEF (魔法防御力) <a class="header-anchor" href="#_6-mdef-魔法防御力" aria-label="Permalink to &quot;6. MDEF (魔法防御力)&quot;">​</a></h3><ul><li><strong>图表说明：MDEF 对比</strong><ul><li><strong>拳师</strong>和<strong>武僧</strong>的 MDEF 成长较好，适合抵御魔法攻击。</li><li><strong>刀客</strong>和<strong>少侠</strong>的 MDEF 较低，可能需要依靠队友进行魔法防护。</li><li><strong>术士</strong>的 MDEF 较低，魔法防御较弱。</li></ul></li></ul><p><img src="'+u+'" alt="MDEF Comparison"></p><h3 id="_7-spd-速度" tabindex="-1">7. SPD (速度) <a class="header-anchor" href="#_7-spd-速度" aria-label="Permalink to &quot;7. SPD (速度)&quot;">​</a></h3><ul><li><strong>图表说明：SPD 对比</strong><ul><li><strong>刀客</strong>和<strong>隐士</strong>的 SPD 成长较好，特别是隐士，表现出其机动性和攻击频率。</li><li><strong>拳师</strong>和<strong>武僧</strong>的 SPD 成长相对较低，速度较慢，适合偏向防御和耐久的战斗方式。</li></ul></li></ul><p><img src="'+d+'" alt="SPD Comparison"></p><h3 id="_8-agility-敏捷" tabindex="-1">8. Agility (敏捷) <a class="header-anchor" href="#_8-agility-敏捷" aria-label="Permalink to &quot;8. Agility (敏捷)&quot;">​</a></h3><ul><li><strong>图表说明：Agility 对比</strong><ul><li><strong>隐士</strong>和<strong>刀客</strong>的 Agility 成长较为突出，适合进行快速反应和频繁攻击的职业。</li><li><strong>拳师</strong>和<strong>武僧</strong>的 Agility 成长较低，反映出其较为迟缓的行动速度。</li></ul></li></ul><p><img src="'+_+'" alt="Agility Comparison"></p><h2 id="总结" tabindex="-1">总结 <a class="header-anchor" href="#总结" aria-label="Permalink to &quot;总结&quot;">​</a></h2><ul><li><strong>防御类职业</strong>（如拳师、武僧）侧重于 HP 和 DEF 的增长，重视生存能力。</li><li><strong>物理输出类职业</strong>（如刀客和少侠）注重 ATK 和 SPD 的成长，强调攻击和机动性。</li><li><strong>魔法类职业</strong>（如术士、隐士）注重 MP 和 MATK 的成长，偏重法术输出和魔法支援。</li><li><strong>综合性职业</strong>（如商人）通常各属性之间的成长较为均衡，没有单一的突出点，适合多角色发展。</li></ul><p>这些规律有助于根据职业的成长特性制定合理的玩法策略，比如选择适合的职业配合游戏中的战斗方式。</p>',49)])])}const k=r(c,[["render",p]]);export{T as __pageData,k as default};
