import{_ as r,o,c as t,ag as e}from"./chunks/framework.Bx1bucHt.js";const c=JSON.parse('{"title":"角色技能设定","description":"","frontmatter":{},"headers":[],"relativePath":"项目实践/JRPG/B_角色成长/角色技能/技能/技能.md","filePath":"项目实践/JRPG/B_角色成长/角色技能/技能/技能.md"}'),l={name:"项目实践/JRPG/B_角色成长/角色技能/技能/技能.md"};function i(n,a,s,h,p,d){return o(),t("div",null,[...a[0]||(a[0]=[e('<h1 id="角色技能设定" tabindex="-1">角色技能设定 <a class="header-anchor" href="#角色技能设定" aria-label="Permalink to &quot;角色技能设定&quot;">​</a></h1><hr><h2 id="陈少秋-平衡型输出-团队核心" tabindex="-1">陈少秋（平衡型输出 / 团队核心） <a class="header-anchor" href="#陈少秋-平衡型输出-团队核心" aria-label="Permalink to &quot;陈少秋（平衡型输出 / 团队核心）&quot;">​</a></h2><h3 id="普攻-「灵枢速射」" tabindex="-1">普攻：「灵枢速射」 <a class="header-anchor" href="#普攻-「灵枢速射」" aria-label="Permalink to &quot;普攻：「灵枢速射」&quot;">​</a></h3><p>挥动「灵枢冲击者」切换至霰弹枪形态，对前方扇形区域敌人造成一次物理伤害，附带概率击退效果。</p><h3 id="技能" tabindex="-1">技能 <a class="header-anchor" href="#技能" aria-label="Permalink to &quot;技能&quot;">​</a></h3><ol><li><p><strong>电磁爆裂（单体爆发）</strong><br> 切换电击枪形态，蓄力后对单个敌人释放高伤害电击，附加 2 回合麻痹状态。若敌人处于潮湿状态，伤害提升 50%。</p></li><li><p><strong>娲石共振（群体增益）</strong><br> 激活腰带娲石能量，为全体队友提升 20% 攻击力与暴击率，持续 3 回合，同时清除 1 个负面状态。</p></li><li><p><strong>蒸汽乱舞（武术爆发）</strong><br> 收起枪械施展近身连击，对周围敌人造成 3 次物理伤害，每次攻击有 15% 概率触发「破甲」效果（降低目标防御）。</p></li><li><p><strong>终焉回响（超必杀）</strong><br> 融合蒸汽科技与娲术，发射贯穿全场的能量光束，对所有敌人造成巨额伤害，并附带「湮灭」标记（死亡后无法复活，持续 1 回合）。</p></li></ol><hr><h2 id="伊万诺维奇-探险家-肉盾-火力支援" tabindex="-1">伊万诺维奇（探险家 / 肉盾 / 火力支援） <a class="header-anchor" href="#伊万诺维奇-探险家-肉盾-火力支援" aria-label="Permalink to &quot;伊万诺维奇（探险家 / 肉盾 / 火力支援）&quot;">​</a></h2><h3 id="普攻-「霰弹威慑」" tabindex="-1">普攻：「霰弹威慑」 <a class="header-anchor" href="#普攻-「霰弹威慑」" aria-label="Permalink to &quot;普攻：「霰弹威慑」&quot;">​</a></h3><p>挥动改装猎枪对前方敌人造成物理伤害，降低目标 10% 命中率，持续 2 回合。</p><h3 id="技能-1" tabindex="-1">技能 <a class="header-anchor" href="#技能-1" aria-label="Permalink to &quot;技能&quot;">​</a></h3><ol><li><p><strong>极地壁垒（防御强化）</strong><br> 展开皮毛大衣形成冰盾，提升自身 50% 防御力与格挡率，持续 4 回合。期间受到攻击有概率反击。</p></li><li><p><strong>银弹狙击（单体破防）</strong><br> 瞄准敌人弱点发射特制银弹，无视目标 20% 防御并造成真实伤害。对血族敌人伤害翻倍。</p></li><li><p><strong>蒸汽弹幕（群体压制）</strong><br> 启动猎枪蒸汽装置，向天空发射信号弹召唤 10 秒持续炮击，对全体敌人造成多段物理伤害。</p></li><li><p><strong>巨兽咆哮（援护技）</strong><br> 召唤极地巨兽虚影震慑全场，清除全体队友控制效果，并嘲讽敌方全体强制攻击自己 2 回合。</p></li></ol><hr><h2 id="朱利安-工程-干扰" tabindex="-1">朱利安（工程 / 干扰） <a class="header-anchor" href="#朱利安-工程-干扰" aria-label="Permalink to &quot;朱利安（工程 / 干扰）&quot;">​</a></h2><h3 id="普攻-「陷阱枪击」" tabindex="-1">普攻：「陷阱枪击」 <a class="header-anchor" href="#普攻-「陷阱枪击」" aria-label="Permalink to &quot;普攻：「陷阱枪击」&quot;">​</a></h3><p>发射带有电磁陷阱的子弹，对单个敌人造成伤害，并在原地留下持续 2 回合的减速陷阱。</p><h3 id="技能-2" tabindex="-1">技能 <a class="header-anchor" href="#技能-2" aria-label="Permalink to &quot;技能&quot;">​</a></h3><ol><li><p><strong>引力囚笼（控制技）</strong><br> 投掷特斯拉装置制造反重力场，将范围内敌人牵引至中心并禁锢 1 回合，期间受到伤害提升 30%。</p></li><li><p><strong>过载脉冲（群体削弱）</strong><br> 释放电磁脉冲，降低全体敌人攻击力与速度 20%，并瘫痪敌方机械类敌人 2 回合。</p></li><li><p><strong>时空锚点（战术辅助）</strong><br> 在战场标记 3 个锚点，下次使用技能时可瞬移至任意锚点，并重置该技能冷却时间。</p></li><li><p><strong>永动悖论（领域技）</strong><br> 展开能量立场，使我方全体技能消耗降低 50%，敌方全体技能消耗翻倍，持续 3 回合。</p></li></ol><hr><h2 id="基因萝莉-娲术天才-技术辅助" tabindex="-1">基因萝莉（娲术天才 / 技术辅助） <a class="header-anchor" href="#基因萝莉-娲术天才-技术辅助" aria-label="Permalink to &quot;基因萝莉（娲术天才 / 技术辅助）&quot;">​</a></h2><h3 id="普攻-「基因校准」" tabindex="-1">普攻：「基因校准」 <a class="header-anchor" href="#普攻-「基因校准」" aria-label="Permalink to &quot;普攻：「基因校准」&quot;">​</a></h3><p>注射基础营养液，恢复单个队友 5% 生命值，并清除 1 个持续性负面状态。</p><h3 id="技能-3" tabindex="-1">技能 <a class="header-anchor" href="#技能-3" aria-label="Permalink to &quot;技能&quot;">​</a></h3><ol><li><p><strong>突变催化（异常施加）</strong><br> 对敌人注射变异血清，造成持续伤害并随机附加「中毒」「混乱」「变形」中的 1 种异常状态。</p></li><li><p><strong>圣树之佑（群体治疗）</strong><br> 召唤基因改良植物，为全体队友恢复大量生命值，并附加「再生」状态（每回合回血）。</p></li><li><p><strong>女娲重构（复活技）</strong><br> 消耗自身当前 50% 生命值，复活一名队友并恢复其 80% 生命值与所有技能。</p></li><li><p><strong>熵寂之蚀（战略技）</strong><br> 释放基因分解射线，对全体敌人造成真实伤害，同时降低敌方全体 30% 最大生命值上限，持续至战斗结束。</p></li></ol><hr><h2 id="机械正太-蒸汽机械师-速攻手" tabindex="-1">机械正太（蒸汽机械师 / 速攻手） <a class="header-anchor" href="#机械正太-蒸汽机械师-速攻手" aria-label="Permalink to &quot;机械正太（蒸汽机械师 / 速攻手）&quot;">​</a></h2><h3 id="普攻-「齿轮连斩」" tabindex="-1">普攻：「齿轮连斩」 <a class="header-anchor" href="#普攻-「齿轮连斩」" aria-label="Permalink to &quot;普攻：「齿轮连斩」&quot;">​</a></h3><p>切换义肢短剑形态，对单个敌人发动 3 次快速斩击，每次攻击有概率触发「撕裂」效果。</p><h3 id="技能-4" tabindex="-1">技能 <a class="header-anchor" href="#技能-4" aria-label="Permalink to &quot;技能&quot;">​</a></h3><ol><li><p><strong>电磁风暴（范围控制）</strong><br> 挥舞特斯拉齿轮剑制造环形电磁刃，对周围敌人造成伤害并附加 1 回合麻痹。</p></li><li><p><strong>超速超载（爆发强化）</strong><br> 过载蒸汽背包，提升自身 100% 攻击速度与暴击率，持续 2 回合，但结束后进入 2 回合虚弱状态。</p></li><li><p><strong>纳米拆解（破防特化）</strong><br> 释放纳米机器人分解敌人护甲，降低目标 50% 防御力，并使后续 3 次攻击无视护盾。</p></li><li><p><strong>永动剑舞（连续追击）</strong><br> 以蒸汽推进器高速移动，对全体敌人发动 6 次随机斩击，每次攻击有 20% 概率触发技能连击。</p></li></ol><hr>',32)])])}const g=r(l,[["render",i]]);export{c as __pageData,g as default};
