import{_ as r,o as i,c as o,ag as t}from"./chunks/framework.Bx1bucHt.js";const d=JSON.parse('{"title":"🌆《神遗：蒸汽妖都》主角小队职业设计","description":"","frontmatter":{},"headers":[],"relativePath":"项目实践/JRPG/B_角色成长/角色技能/技能/技能2.md","filePath":"项目实践/JRPG/B_角色成长/角色技能/技能/技能2.md"}'),n={name:"项目实践/JRPG/B_角色成长/角色技能/技能/技能2.md"};function s(a,l,g,e,u,h){return i(),o("div",null,[...l[0]||(l[0]=[t('<h1 id="🌆《神遗-蒸汽妖都》主角小队职业设计" tabindex="-1">🌆《神遗：蒸汽妖都》主角小队职业设计 <a class="header-anchor" href="#🌆《神遗-蒸汽妖都》主角小队职业设计" aria-label="Permalink to &quot;🌆《神遗：蒸汽妖都》主角小队职业设计&quot;">​</a></h1><blockquote><p>✨ 世界观关键词：娲术：上古女娲创世生物基因技术、蒸汽时代、电气发明、血族、山海经神兽 ✨「娲术」作为​​反派专属的禁忌技术​​不被主角滥用，同时强化主角团以​​蒸汽科技、电气科技、武道气术、神兽共鸣​​等合法手段对抗娲术污染的对抗逻辑。​ ​主角的力量来自对正统技艺的磨砺或对自身悲剧的克制，而非驾驭禁忌 🎮 战术配置：经典7人队伍。同时最多4人出战</p></blockquote><hr><h2 id="🛡️【前线位】稳定战线-扛伤打断" tabindex="-1">🛡️【前线位】稳定战线 / 扛伤打断 <a class="header-anchor" href="#🛡️【前线位】稳定战线-扛伤打断" aria-label="Permalink to &quot;🛡️【前线位】稳定战线 / 扛伤打断&quot;">​</a></h2><h3 id="_1-陈少秋-chen-shaoqiu" tabindex="-1">1. 陈少秋（Chen Shaoqiu） <a class="header-anchor" href="#_1-陈少秋-chen-shaoqiu" aria-label="Permalink to &quot;1. 陈少秋（Chen Shaoqiu）&quot;">​</a></h3><p>“一人之身，承古今之道；一念之间，破乱世之局。”<br> 武术世家子弟`帝国学院归国少年陈少秋，在回国后的不久，被卷入娲术灾变中。既熟练传统气术，也掌握现代战斗战术，是队伍中的领导者与攻守枢纽。<br> 他的人生观坚守“护人先护道”，因此他在战斗中也承担平衡队形、分析局势、干预敌态的多功能角色。</p><ul><li><strong>职业类型</strong>：天衡之刃（平衡型剑士）</li><li><strong>定位</strong>：近战/指挥/多功能型，兼具输出与辅助，核心为战场节奏控制</li><li><strong>风格灵感</strong>：苍炎之轨迹的艾克 + 克劳德（FF7）+ 八方旅人的剑士</li><li><strong>武器</strong>：中国剑，灵枢冲击者,特斯拉电枪／压缩霰弹</li><li><strong>普通攻击</strong>： <ul><li><strong>衡剑斩</strong>：单体物理攻击，冲锋斩击目标并使其瘫痪，有中等几率破防（降低目标护甲1回合）</li></ul></li><li><strong>技能</strong>： <ul><li><strong>脉冲护体</strong>：开启电场护盾，短时间内减伤 + 嘲讽敌人</li><li><strong>云势·崩</strong>：单体高威力物理技，额外削减敌方行动条</li><li><strong>破曦·斩</strong>：觉醒技，满气力释放，对敌方全体造成大范围斩击</li></ul></li></ul><hr><h2 id="✨【支援位】治疗恢复-状态净化-增益增伤" tabindex="-1">✨【支援位】治疗恢复 / 状态净化 / 增益增伤 <a class="header-anchor" href="#✨【支援位】治疗恢复-状态净化-增益增伤" aria-label="Permalink to &quot;✨【支援位】治疗恢复 / 状态净化 / 增益增伤&quot;">​</a></h2><h3 id="_5-林晚晴-lin-wanqing" tabindex="-1">5. 林晚晴（Lin Wanqing） <a class="header-anchor" href="#_5-林晚晴-lin-wanqing" aria-label="Permalink to &quot;5. 林晚晴（Lin Wanqing）&quot;">​</a></h3><ul><li><strong>职业类型</strong>：气络医者（Qi-Flow Healer）</li><li><strong>定位</strong>：群体治疗 / 异常驱散 / 附加增益</li><li><strong>风格灵感</strong>：塞拉（FF13）+ 神圣法师 + 和风巫医</li><li><strong>武器</strong>：银针阵 / 火药炸弹 / 气脉传导环</li><li><strong>普通攻击</strong>： <ul><li><strong>毒针</strong>：单体物理攻击，并带3回合毒性伤害</li><li><strong>净血注射</strong>：治疗量最低队友恢复生命</li></ul></li><li><strong>技能</strong>： <ul><li><strong>百草清瘴</strong>：驱散单体异常状态</li><li><strong>银针渡穴</strong>：快速为全队恢复生命</li><li><strong>龙息灸法</strong>：3回合内持续为全体恢复生命，并提升抗性</li><li><strong>金针定脉</strong>（极技）：禁锢敌人气流，使其瘫痪数回合</li></ul></li></ul><hr><h2 id="⚔️【输出位】远程压制-爆发暗杀" tabindex="-1">⚔️【输出位】远程压制 / 爆发暗杀 <a class="header-anchor" href="#⚔️【输出位】远程压制-爆发暗杀" aria-label="Permalink to &quot;⚔️【输出位】远程压制 / 爆发暗杀&quot;">​</a></h2><h3 id="_3-阿伊莎-aisha" tabindex="-1">3. 阿伊莎（Aisha） <a class="header-anchor" href="#_3-阿伊莎-aisha" aria-label="Permalink to &quot;3. 阿伊莎（Aisha）&quot;">​</a></h3><p>情报与潜入专家</p><ul><li><strong>职业类型</strong>：隐毒刃客（Veilblade Assassin）</li><li><strong>定位</strong>：高机动杀手 /毒状态叠加、暴击率高</li><li><strong>风格灵感</strong>：影牢女王 + 潜行型的P5小丑</li><li><strong>武器</strong>：炼毒短刃/ 飞镖</li><li><strong>普通攻击</strong>： <ul><li><strong>刃鸣</strong>：单体物理攻击，并带3回合毒性伤害</li></ul></li><li><strong>技能</strong>： <ul><li><strong>幻影潜行</strong>：后背攻击，高暴击率</li><li><strong>蛇骨镖</strong>：中毒投掷物，附加麻痹效果</li><li><strong>毒谶契印</strong>：多段攻击次敌人，若目标中毒，则暴击率翻倍</li></ul></li></ul><hr><h3 id="_4-朱利安-julian" tabindex="-1">4. 朱利安（Julian） <a class="header-anchor" href="#_4-朱利安-julian" aria-label="Permalink to &quot;4. 朱利安（Julian）&quot;">​</a></h3><ul><li><strong>职业类型</strong>：灵枢工铳师（Gear-Arms Engineer）</li><li><strong>定位</strong>：范围输出 + 控制陷阱 + 装备维保</li><li><strong>风格灵感</strong>：卢克（TOA）+ 维克托（幻想水滸）+ FF的红魔导士</li><li><strong>武装</strong>：可折叠臂铳 / 齿轮雷 / 骑士细剑</li><li><strong>普通攻击</strong>： <ul><li><strong>风华突</strong>：高命中率单体穿刺，击中后降低目标闪避率1回合</li></ul></li><li><strong>技能</strong>： <ul><li><strong>特斯拉电涌</strong>：发射电网，造成群体电击</li><li><strong>蒸汽阱缚</strong>：布置地面陷阱，2回合禁锢目标，并持续2回合伤害</li><li><strong>智识</strong>：为队友下回合攻击叠加伤害</li></ul></li></ul><hr><h3 id="_5-伊万诺维奇" tabindex="-1">5. 伊万诺维奇 <a class="header-anchor" href="#_5-伊万诺维奇" aria-label="Permalink to &quot;5. 伊万诺维奇&quot;">​</a></h3><ul><li><strong>职业类型</strong>：冰钢猎魂（火力+陷阱）</li><li><strong>定位</strong>：中远程攻击、设陷为主，具元素控制与爆破支援</li><li><strong>风格灵感</strong>：风格灵感：工程兵 + 物理控制 + 少年猎人类型</li><li><strong>武装</strong>：陷阱、犴狩狙击（寒冰追击）、焰爆（爆破附加）</li><li><strong>普通攻击</strong>： <ul><li><strong>犴狩狙击</strong>：单体远程射击</li></ul></li><li><strong>技能</strong>： <ul><li><strong>冰雷</strong>：冰雷陷阱，命中则冻结+高伤</li><li><strong>裂地轰鸣</strong>：对敌方全体造成小范围地系冲击，并降低速度</li><li><strong>北境猎魂</strong>：群体冰爆</li></ul></li></ul><hr><h3 id="_2-巴鲁克-baruch-⭐新增异变角色" tabindex="-1">2. 巴鲁克（Baruch）⭐新增异变角色 <a class="header-anchor" href="#_2-巴鲁克-baruch-⭐新增异变角色" aria-label="Permalink to &quot;2. 巴鲁克（Baruch）⭐新增异变角色&quot;">​</a></h3><ul><li><strong>职业类型</strong>：血怒狂兵（Crimson Berserker）</li><li><strong>定位</strong>：狂暴输出 / 爆发</li><li><strong>武器</strong>：利爪 + 变异生物</li><li><strong>普通攻击</strong>： <ul><li><strong>风华突</strong>：高命中率单体穿刺，击中后降低目标闪避率1回合</li></ul></li><li><strong>技能</strong>： <ul><li><strong>狂血激潮</strong>：对自身造成小幅伤害，换取攻击力暴增</li><li><strong>撕咬裂击</strong>：单体破甲伤害，附带出血效果</li><li><strong>血族根须</strong>（终结技）：释放体内寄生体群体攻击</li></ul></li><li><strong>设定融合说明</strong>： <ul><li>前血族混血斗士，反叛族群后投靠人类阵营</li><li>依靠自我克制压制体内“兽性病毒”</li></ul></li></ul><hr><h3 id="_6-维克多-victor-⭐新增小正太角色" tabindex="-1">6. 维克多（Victor）⭐新增小正太角色 <a class="header-anchor" href="#_6-维克多-victor-⭐新增小正太角色" aria-label="Permalink to &quot;6. 维克多（Victor）⭐新增小正太角色&quot;">​</a></h3><p>14岁音乐神童，用音乐声波的神秘力量</p><ul><li><strong>职业类型</strong>：谐律操纵者（Resonance Conductor）</li><li><strong>定位</strong>：全队增益 / 节奏控制 / 音波攻击</li><li><strong>武器</strong>：音叉权杖 + 八音盒（背包装置）</li><li><strong>普通攻击</strong>： <ul><li><strong>律动打击</strong>：单体音波伤害，20%概率使目标行动条倒退10%</li></ul></li><li><strong>技能</strong>： <ul><li><strong>震魂调</strong>：发出高频谐波，使受娲术控制的敌人有概率陷入混乱，无差别攻击同类</li><li><strong>加速谐波</strong>：全队行动条推进15%，下次伤害+20%</li><li><strong>圣咏屏障</strong>：3回合音波护盾，吸收伤害的30%转化为治疗</li><li><strong>​​终末共鸣（极技）</strong>：对异常状态目标进行7段音爆连击</li></ul></li></ul><hr><h3 id="_7-芙蕾雅-freya-⭐新增兽耳小萝莉角色" tabindex="-1">7. 芙蕾雅（Freya）⭐新增兽耳小萝莉角色 <a class="header-anchor" href="#_7-芙蕾雅-freya-⭐新增兽耳小萝莉角色" aria-label="Permalink to &quot;7. 芙蕾雅（Freya）⭐新增兽耳小萝莉角色&quot;">​</a></h3><p>被植入「娲灵」神兽基因的沉默实验体。外表是天真无邪的萝莉，内心封印着神兽的低语。<br> 能与古神兽产生短暂共鸣。她是战场的异数：不依赖娲术，却因其副产物而变强。</p><ul><li><strong>职业类型</strong>：谐律操纵者（Resonance Conductor）</li><li><strong>定位</strong>：高爆发AOE / 神兽召唤 / 心智抗性 / 异常反击</li><li><strong>风格灵感</strong>：九尾、玉藻前 + 神隐萝莉 + 神兽共鸣器</li><li><strong>武器</strong>：神兽羽晶 / 音律浮翎 / 灵脉断指甲</li><li><strong>普通攻击</strong>： <ul><li><strong>律灵爪轻击</strong>：用爪状灵力劈击单体，造成法术伤害，有小概率施加1回合的「扰神」效果（使敌人技能冷却+1回合）</li></ul></li><li><strong>技能</strong>： <ul><li><strong>神语共鸣</strong>：与远古神兽短暂同步，为全队施加“灵兽庇佑”（2回合内全属性抗性+20%，敌人命中-10%）</li><li><strong>九尾炎煌</strong>：召唤九尾神兽幻影，对敌方全体造成火属性AOE伤害，并灼烧3回合（每回合固定扣血）</li><li><strong>祸斗破魂</strong>：召唤祸斗灵影冲锋敌方单体，造成高威力精神伤害，若敌方正处异常状态，则附加1回合“惧意”（无法使用主动技能）</li><li><strong>​禁忌回响（极技）</strong>：神兽基因短暂完全激活，变身3回合，提升自身50%攻击、免疫所有控制效果；技能替换为“灵爆连击”，每回合自动释放高爆发AOE攻击一次（不耗技能点），并自动驱散1个自身不良状态。</li></ul></li></ul><hr>',34)])])}const _=r(n,[["render",s]]);export{d as __pageData,_ as default};
