import{_ as e,o as d,c as r,ag as o}from"./chunks/framework.Bx1bucHt.js";const b=JSON.parse('{"title":"特效","description":"","frontmatter":{},"headers":[],"relativePath":"项目实践/JRPG/B_角色成长/角色技能/特效/特效.md","filePath":"项目实践/JRPG/B_角色成长/角色技能/特效/特效.md"}'),a={name:"项目实践/JRPG/B_角色成长/角色技能/特效/特效.md"};function c(l,t,s,i,n,h){return d(),r("div",null,[...t[0]||(t[0]=[o('<h1 id="特效" tabindex="-1">特效 <a class="header-anchor" href="#特效" aria-label="Permalink to &quot;特效&quot;">​</a></h1><p>30+ Visual Effects</p><h2 id="战斗特效-combat-effects" tabindex="-1">战斗特效（Combat Effects） <a class="header-anchor" href="#战斗特效-combat-effects" aria-label="Permalink to &quot;战斗特效（Combat Effects）&quot;">​</a></h2><table tabindex="0"><thead><tr><th>名称</th><th>场景/用途</th><th>描述</th></tr></thead><tbody><tr><td>剑击特效<br>Sword Slash Effect</td><td>战斗</td><td>剑挥动时的动态光影轨迹</td></tr><tr><td>剑碰撞火花<br>Clash Sparks</td><td>战斗</td><td>剑与剑碰撞时迸发的火花效果</td></tr><tr><td>箭射轨迹<br>Arrow Trail</td><td>战斗</td><td>箭矢飞行时的尾部轨迹光效</td></tr><tr><td>火焰特效<br>Flame Effect</td><td>战斗</td><td>火焰剑攻击时的炽热火焰效果</td></tr><tr><td>冰冻特效<br>Frost Effect</td><td>战斗</td><td>冰霜盾格挡或技能释放时的冰晶效果</td></tr><tr><td>爆炸特效<br>Explosion Effect</td><td>战斗</td><td>魔法炸弹或技能引发的爆炸效果</td></tr><tr><td>闪电特效<br>Lightning Effect</td><td>战斗</td><td>魔法技能召唤的闪电击中效果</td></tr><tr><td>怪物死亡<br>Monster Death Effect</td><td>战斗</td><td>怪物死亡时的烟雾与碎裂消失效果</td></tr><tr><td>Boss登场特效<br>Boss Entry Effect</td><td>战斗</td><td>Boss出现时的黑暗烟雾与震动特效</td></tr><tr><td>警告标记<br>Warning Marker</td><td>战斗</td><td>Boss攻击前的红色区域提示效果</td></tr><tr><td>闪避特效<br>Dodge Effect</td><td>战斗</td><td>闪避成功时的动态模糊特效</td></tr></tbody></table><hr><h2 id="状态与技能特效-status-skill-effects" tabindex="-1">状态与技能特效（Status &amp; Skill Effects） <a class="header-anchor" href="#状态与技能特效-status-skill-effects" aria-label="Permalink to &quot;状态与技能特效（Status &amp; Skill Effects）&quot;">​</a></h2><table tabindex="0"><thead><tr><th>名称</th><th>场景/用途</th><th>描述</th></tr></thead><tbody><tr><td>中毒特效<br>Poison Effect</td><td>状态效果</td><td>角色中毒时的绿色烟雾和浮动数字</td></tr><tr><td>治疗光环<br>Healing Aura</td><td>技能</td><td>使用治疗技能时的绿色光波效果</td></tr><tr><td>升级特效<br>Level-Up Effect</td><td>成长</td><td>角色升级时的金光和星星飞散效果</td></tr><tr><td>魔法盾<br>Magic Shield</td><td>防御技能</td><td>防护技能触发时的蓝色护盾特效</td></tr><tr><td>圣光特效<br>Holy Light Effect</td><td>技能</td><td>圣光戒指释放技能时的金色光环效果</td></tr><tr><td>传送特效<br>Teleportation Effect</td><td>技能</td><td>角色传送时的光环收缩与粒子分散效果</td></tr><tr><td>黑暗吞噬<br>Darkness Devour Effect</td><td>反派技能</td><td>反派释放技能时的黑雾与红光特效</td></tr><tr><td>复活特效<br>Revival Effect</td><td>技能</td><td>使用复活卷轴时的白色光柱与碎片效果</td></tr><tr><td>隐身消失<br>Invisibility Effect</td><td>技能</td><td>隐身斗篷激活时的透明化粒子特效</td></tr></tbody></table><hr><h2 id="环境特效-environment-effects" tabindex="-1">环境特效（Environment Effects） <a class="header-anchor" href="#环境特效-environment-effects" aria-label="Permalink to &quot;环境特效（Environment Effects）&quot;">​</a></h2><table tabindex="0"><thead><tr><th>名称</th><th>场景/用途</th><th>描述</th></tr></thead><tbody><tr><td>火把光效<br>Torch Light Effect</td><td>环境</td><td>火把燃烧时的动态火焰与光影效果</td></tr><tr><td>瀑布水雾<br>Waterfall Mist</td><td>环境</td><td>瀑布周围的水雾弥散与光反射效果</td></tr><tr><td>风吹草动<br>Grass Movement Effect</td><td>环境</td><td>风吹过草原时的动态草丛摆动效果</td></tr><tr><td>星光闪烁<br>Star Twinkle</td><td>环境</td><td>夜晚天空的星光动态闪烁效果</td></tr><tr><td>雨水滴落<br>Rain Effect</td><td>环境</td><td>下雨时的水滴动态和地面涟漪</td></tr><tr><td>雾气弥漫<br>Fog Effect</td><td>环境</td><td>地下迷宫或山顶的低沉雾气效果</td></tr><tr><td>水面反射<br>Water Reflection</td><td>环境</td><td>水面动态反射角色和环境光影</td></tr></tbody></table><hr><h2 id="解谜与机关特效-puzzle-mechanism-effects" tabindex="-1">解谜与机关特效（Puzzle &amp; Mechanism Effects） <a class="header-anchor" href="#解谜与机关特效-puzzle-mechanism-effects" aria-label="Permalink to &quot;解谜与机关特效（Puzzle &amp; Mechanism Effects）&quot;">​</a></h2><table tabindex="0"><thead><tr><th>名称</th><th>场景/用途</th><th>描述</th></tr></thead><tbody><tr><td>宝箱开启特效<br>Chest Open Effect</td><td>解谜</td><td>宝箱开启时的金光与光柱效果</td></tr><tr><td>门开启特效<br>Door Open Effect</td><td>解谜</td><td>特殊门打开时的流光动画效果</td></tr><tr><td>火焰陷阱<br>Flame Trap Effect</td><td>迷宫</td><td>陷阱触发时的火焰喷射动画</td></tr><tr><td>冰冻陷阱<br>Ice Trap Effect</td><td>迷宫</td><td>陷阱触发时的冰晶扩散动画</td></tr><tr><td>解谜符文<br>Rune Activation Effect</td><td>解谜</td><td>符文被激活时的蓝色流光动画</td></tr><tr><td>宝石发光<br>Gem Glow Effect</td><td>特殊道具</td><td>获得宝石时的闪烁光效</td></tr><tr><td>地面裂纹<br>Ground Crack Effect</td><td>战斗/解谜</td><td>Boss攻击地面时的裂纹扩散动画</td></tr></tbody></table><hr><h3 id="分类总结" tabindex="-1">分类总结 <a class="header-anchor" href="#分类总结" aria-label="Permalink to &quot;分类总结&quot;">​</a></h3><ul><li><strong>战斗特效</strong>：用于增强战斗动作的视觉表现，凸显战斗紧张感。</li><li><strong>状态与技能特效</strong>：表现角色状态变化和技能释放的视觉效果。</li><li><strong>环境特效</strong>：为场景营造动态和沉浸感的氛围特效。</li><li><strong>解谜与机关特效</strong>：用于宝箱、陷阱、符文等与解谜和机关相关的视觉反馈。</li></ul><h2 id="特效命名规则与分类" tabindex="-1">特效命名规则与分类 <a class="header-anchor" href="#特效命名规则与分类" aria-label="Permalink to &quot;特效命名规则与分类&quot;">​</a></h2><p>为确保命名规范和易管理，特效按照用途分组，并使用前缀区分功能类别。以下是详细规则和命名建议：</p><h2 id="命名规则" tabindex="-1">命名规则 <a class="header-anchor" href="#命名规则" aria-label="Permalink to &quot;命名规则&quot;">​</a></h2><p><strong>格式</strong>：<code>[分类前缀]_[描述名称]</code></p><ul><li>分类前缀：根据特效用途分组，明确分类。</li><li>描述名称：用简洁的英文描述特效内容，采用首字母大写的驼峰命名法（CamelCase）。</li></ul><hr><h2 id="分类与前缀" tabindex="-1">分类与前缀 <a class="header-anchor" href="#分类与前缀" aria-label="Permalink to &quot;分类与前缀&quot;">​</a></h2><h3 id="_1-战斗特效-combat-effects" tabindex="-1">1. 战斗特效（Combat Effects） <a class="header-anchor" href="#_1-战斗特效-combat-effects" aria-label="Permalink to &quot;1. 战斗特效（Combat Effects）&quot;">​</a></h3><p><strong>前缀</strong>：<code>Combat_</code><br><strong>说明</strong>：用于增强战斗动作表现的动态特效。<br><strong>示例</strong>：</p><ul><li><code>Combat_SwordSlash</code>：剑击特效</li><li><code>Combat_ClashSparks</code>：剑碰撞火花</li><li><code>Combat_ArrowTrail</code>：箭射轨迹</li><li><code>Combat_Flame</code>：火焰特效</li><li><code>Combat_Explosion</code>：爆炸特效</li></ul><hr><h3 id="_2-状态与技能特效-status-skill-effects" tabindex="-1">2. 状态与技能特效（Status &amp; Skill Effects） <a class="header-anchor" href="#_2-状态与技能特效-status-skill-effects" aria-label="Permalink to &quot;2. 状态与技能特效（Status &amp; Skill Effects）&quot;">​</a></h3><p><strong>前缀</strong>：<code>Status_</code>（状态特效），<code>Skill_</code>（技能特效）<br><strong>说明</strong>：表现角色状态变化或技能释放的特效。<br><strong>示例</strong>：</p><ul><li><code>Status_Poison</code>：中毒特效</li><li><code>Status_LevelUp</code>：升级特效</li><li><code>Skill_HealingAura</code>：治疗光环特效</li><li><code>Skill_Teleportation</code>：传送特效</li><li><code>Skill_Revival</code>：复活特效</li></ul><hr><h3 id="_3-环境特效-environment-effects" tabindex="-1">3. 环境特效（Environment Effects） <a class="header-anchor" href="#_3-环境特效-environment-effects" aria-label="Permalink to &quot;3. 环境特效（Environment Effects）&quot;">​</a></h3><p><strong>前缀</strong>：<code>Env_</code><br><strong>说明</strong>：为场景添加动态表现的特效。<br><strong>示例</strong>：</p><ul><li><code>Env_TorchLight</code>：火把光效</li><li><code>Env_WaterfallMist</code>：瀑布水雾</li><li><code>Env_GrassMovement</code>：风吹草动</li><li><code>Env_Rain</code>：雨水滴落</li><li><code>Env_StarTwinkle</code>：星光闪烁</li></ul><hr><h3 id="_4-解谜与机关特效-puzzle-mechanism-effects" tabindex="-1">4. 解谜与机关特效（Puzzle &amp; Mechanism Effects） <a class="header-anchor" href="#_4-解谜与机关特效-puzzle-mechanism-effects" aria-label="Permalink to &quot;4. 解谜与机关特效（Puzzle &amp; Mechanism Effects）&quot;">​</a></h3><p><strong>前缀</strong>：<code>Puzzle_</code>（解谜特效），<code>Mechanism_</code>（机关特效）<br><strong>说明</strong>：解谜和互动相关的视觉反馈。<br><strong>示例</strong>：</p><ul><li><code>Puzzle_RuneActivation</code>：解谜符文特效</li><li><code>Puzzle_GemGlow</code>：宝石发光特效</li><li><code>Mechanism_ChestOpen</code>：宝箱开启特效</li><li><code>Mechanism_DoorOpen</code>：门开启特效</li><li><code>Mechanism_GroundCrack</code>：地面裂纹特效</li></ul><hr><h3 id="_5-boss与反派特效-boss-enemy-effects" tabindex="-1">5. Boss与反派特效（Boss &amp; Enemy Effects） <a class="header-anchor" href="#_5-boss与反派特效-boss-enemy-effects" aria-label="Permalink to &quot;5. Boss与反派特效（Boss &amp; Enemy Effects）&quot;">​</a></h3><p><strong>前缀</strong>：<code>Boss_</code>（Boss相关特效），<code>Enemy_</code>（普通敌人特效）<br><strong>说明</strong>：用于增强敌人或Boss存在感的特效。<br><strong>示例</strong>：</p><ul><li><code>Boss_Entry</code>：Boss登场特效</li><li><code>Boss_AttackWarning</code>：Boss攻击前的警告标记特效</li><li><code>Enemy_Death</code>：敌人死亡特效</li><li><code>Enemy_PoisonCloud</code>：敌人释放毒雾的特效</li></ul><hr><h3 id="_6-通用特效-generic-effects" tabindex="-1">6. 通用特效（Generic Effects） <a class="header-anchor" href="#_6-通用特效-generic-effects" aria-label="Permalink to &quot;6. 通用特效（Generic Effects）&quot;">​</a></h3><p><strong>前缀</strong>：<code>FX_</code><br><strong>说明</strong>：通用或无法明确分类的特效。<br><strong>示例</strong>：</p><ul><li><code>FX_Sparkle</code>：闪光特效</li><li><code>FX_Shockwave</code>：冲击波特效</li><li><code>FX_ExplosionSmall</code>：小型爆炸特效</li><li><code>FX_FogLayer</code>：场景雾气特效</li></ul><hr><h2 id="分类总结-1" tabindex="-1">分类总结 <a class="header-anchor" href="#分类总结-1" aria-label="Permalink to &quot;分类总结&quot;">​</a></h2><table tabindex="0"><thead><tr><th>分类</th><th>前缀</th><th>示例</th></tr></thead><tbody><tr><td>战斗特效</td><td><code>Combat_</code></td><td><code>Combat_SwordSlash</code></td></tr><tr><td>状态特效</td><td><code>Status_</code></td><td><code>Status_LevelUp</code></td></tr><tr><td>技能特效</td><td><code>Skill_</code></td><td><code>Skill_HealingAura</code></td></tr><tr><td>环境特效</td><td><code>Env_</code></td><td><code>Env_TorchLight</code></td></tr><tr><td>解谜特效</td><td><code>Puzzle_</code></td><td><code>Puzzle_RuneActivation</code></td></tr><tr><td>机关特效</td><td><code>Mechanism_</code></td><td><code>Mechanism_ChestOpen</code></td></tr><tr><td>Boss特效</td><td><code>Boss_</code></td><td><code>Boss_Entry</code></td></tr><tr><td>敌人特效</td><td><code>Enemy_</code></td><td><code>Enemy_Death</code></td></tr><tr><td>通用特效</td><td><code>FX_</code></td><td><code>FX_Sparkle</code></td></tr></tbody></table><p>通过该命名规则，可以使特效资源分类清晰，方便开发与管理。</p>',50)])])}const m=e(a,[["render",c]]);export{b as __pageData,m as default};
