import{_ as l,o as r,c as s,ag as d}from"./chunks/framework.Bx1bucHt.js";const _=JSON.parse('{"title":"《神遗：蒸汽妖都》90秒PV 分镜脚本","description":"","frontmatter":{},"headers":[],"relativePath":"项目实践/JRPG/PV/PV.md","filePath":"项目实践/JRPG/PV/PV.md"}'),i={name:"项目实践/JRPG/PV/PV.md"};function o(n,t,g,a,e,u){return r(),s("div",null,[...t[0]||(t[0]=[d('<h1 id="《神遗-蒸汽妖都》90秒pv-分镜脚本" tabindex="-1">《神遗：蒸汽妖都》90秒PV 分镜脚本 <a class="header-anchor" href="#《神遗-蒸汽妖都》90秒pv-分镜脚本" aria-label="Permalink to &quot;《神遗：蒸汽妖都》90秒PV 分镜脚本&quot;">​</a></h1><hr><h2 id="_0-10s-【氛围开篇-·-异样上海】" tabindex="-1">0-10s 【氛围开篇 · 异样上海】 <a class="header-anchor" href="#_0-10s-【氛围开篇-·-异样上海】" aria-label="Permalink to &quot;0-10s 【氛围开篇 · 异样上海】&quot;">​</a></h2><ul><li><strong>镜头1 (0-3s)</strong><ul><li><strong>远景长镜头</strong>：俯拍外滩夜景，乌云翻滚，红月缓缓显现。</li><li><strong>运动</strong>：慢推，压迫感递增。</li></ul></li><li><strong>镜头2 (3-6s)</strong><ul><li><strong>中景</strong>：工人搬运货物，洋人监工呵斥。</li><li><strong>插入快切</strong>：报童挥舞报纸，标题闪过——“少女血液被吸干！”</li></ul></li><li><strong>镜头3 (6-10s)</strong><ul><li><strong>低机位推镜头</strong>：蒸汽阀门轰鸣开启，齿轮填满画面。</li><li><strong>转场</strong>：黑屏 + 低沉旁白：“1910年，旧上海——风云将起。”</li></ul></li></ul><hr><h2 id="_10-25s-【动作切入-·-爆发冲突】" tabindex="-1">10-25s 【动作切入 · 爆发冲突】 <a class="header-anchor" href="#_10-25s-【动作切入-·-爆发冲突】" aria-label="Permalink to &quot;10-25s 【动作切入 · 爆发冲突】&quot;">​</a></h2><ul><li><strong>镜头4 (10-13s)</strong><ul><li><strong>快切组镜</strong>： <ul><li>港口斧头帮嚣张大笑。</li><li>陈少秋抬枪，电光点亮。</li><li><strong>慢动作</strong>：第一枪轰出，火花四散。</li></ul></li></ul></li><li><strong>镜头5 (13-17s)</strong><ul><li><strong>跟拍</strong>：林晚晴飞身，银针落下 → 特写敌人僵直。</li><li><strong>特写快切</strong>：朱利安齿轮爆裂，火花喷出。</li></ul></li><li><strong>镜头6 (17-25s)</strong><ul><li><strong>高速蒙太奇 (0.5-1s一切)</strong>： <ul><li>血族獠牙一闪。</li><li>红酒杯中血液晃动。</li><li>宋先生举杯冷笑：“文明的遗物，只属于强者。”</li><li>铁手重拳砸碎石柱，石屑飞溅。</li></ul></li></ul></li></ul><hr><h2 id="_25-45s-【群战爆发-·-蒸汽暴走】" tabindex="-1">25-45s 【群战爆发 · 蒸汽暴走】 <a class="header-anchor" href="#_25-45s-【群战爆发-·-蒸汽暴走】" aria-label="Permalink to &quot;25-45s 【群战爆发 · 蒸汽暴走】&quot;">​</a></h2><ul><li><strong>镜头7 (25-30s)</strong><ul><li><strong>横摇长镜头</strong>：巨舰“蜃楼舟”低空压境，投下庞大阴影。</li><li><strong>音效</strong>：低频轰鸣，气氛压抑。</li></ul></li><li><strong>镜头8 (30-35s)</strong><ul><li><strong>高速切换 (1s/镜)</strong>： <ul><li>陈少秋双枪扫射。</li><li>林晚晴银针阵法发光。</li><li>朱利安丢下特斯拉线圈 → 电光罩场。</li></ul></li></ul></li><li><strong>镜头9 (35-45s)</strong><ul><li><strong>快切 + 抖动手持感</strong>：血仆群扑，镜头穿插人群。</li><li><strong>慢动作插入</strong>：陈少秋翻滚开火，电光直冲镜头。</li></ul></li></ul><hr><h2 id="_45-65s-【抉择矛盾-·-情感刺激】" tabindex="-1">45-65s 【抉择矛盾 · 情感刺激】 <a class="header-anchor" href="#_45-65s-【抉择矛盾-·-情感刺激】" aria-label="Permalink to &quot;45-65s 【抉择矛盾 · 情感刺激】&quot;">​</a></h2><ul><li><strong>镜头10 (45-50s)</strong><ul><li><strong>静止长镜头</strong>：陈公馆，烛光摇曳，母亲遗像。</li><li><strong>虚化叠影</strong>：母亲低语：“用娲术……让我回来……”</li></ul></li><li><strong>镜头11 (50-55s)</strong><ul><li><strong>近景快切</strong>：林晚晴泪光，声音颤抖：“少秋！若方尊落入他们之手，我们将再无未来！”</li></ul></li><li><strong>镜头12 (55-65s)</strong><ul><li><strong>碎片蒙太奇 (0.5s一切)</strong>： <ul><li><strong>补天诏</strong>：血色洪流吞没混血。</li><li><strong>血族新约</strong>：人类与血族在红月下并肩。</li><li><strong>蜃楼流浪</strong>：巨舰腾空，飞向群星。</li></ul></li></ul></li></ul><hr><h2 id="_65-90s-【高潮收尾-·-记忆点】" tabindex="-1">65-90s 【高潮收尾 · 记忆点】 <a class="header-anchor" href="#_65-90s-【高潮收尾-·-记忆点】" aria-label="Permalink to &quot;65-90s 【高潮收尾 · 记忆点】&quot;">​</a></h2><ul><li><strong>镜头13 (65-75s)</strong><ul><li><strong>高速蒙太奇</strong>：齿轮旋转、红月、血族獠牙、蒸汽爆裂、人群尖叫。</li><li><strong>慢动作插入</strong>：陈少秋怒吼，电光点亮全场。</li></ul></li><li><strong>镜头14 (75-85s)</strong><ul><li><strong>远景长镜头</strong>：轩辕号腾空，蒸汽轰鸣。</li><li><strong>特写</strong>：宋先生半张脸笼罩红光，微笑低语：“你，会如何选择？”</li></ul></li><li><strong>镜头15 (85-90s)</strong><ul><li><strong>黑屏</strong>：Logo 出现——《神遗：蒸汽妖都》</li><li><strong>音效</strong>：一声蒸汽爆鸣 → 戛然而止。</li></ul></li></ul><hr><h2 id="🎯-剪辑节奏总结" tabindex="-1">🎯 剪辑节奏总结 <a class="header-anchor" href="#🎯-剪辑节奏总结" aria-label="Permalink to &quot;🎯 剪辑节奏总结&quot;">​</a></h2><ul><li><strong>0-10s</strong> → 慢节奏氛围，历史感。</li><li><strong>10-25s</strong> → 快切动作，第一次爆发。</li><li><strong>25-45s</strong> → 蒸汽大战，高潮预演。</li><li><strong>45-65s</strong> → 拉静、矛盾抉择，情绪爆点。</li><li><strong>65-90s</strong> → 蒙太奇冲击，LOGO收束。</li></ul><table tabindex="0"><thead><tr><th>时间</th><th>画面</th><th>配乐/音效</th><th>文案/字幕</th><th>说明</th></tr></thead><tbody><tr><td>0~3s</td><td>黑屏渐亮，女娲塑像剪影，天裂山河之光穿透裂缝</td><td>深沉弦乐，远处雷声</td><td>「神遗·蒸汽妖都」</td><td>开场视觉冲击，奠定神话背景</td></tr><tr><td>3~7s</td><td>蒸汽弥漫的上海外滩夜景，霓虹与烟囱交错，远处血月升起</td><td>机械齿轮声与低沉鼓点</td><td>「东方古老神术，隐藏于近代工业都市」</td><td>突出东方神秘与工业化对比</td></tr><tr><td>7~12s</td><td>血族侧脸特写，眼睛闪红，手指触碰青铜器裂缝</td><td>心跳声+金属摩擦</td><td>「古老的血脉，跨越东西方」</td><td>暗示血族起源，引发神秘与紧张感</td></tr><tr><td>12~18s</td><td>陈少秋枪械与娲石结合，霰弹与电光交错</td><td>电击声+枪声</td><td>「文明与科技的碰撞」</td><td>主角能力视觉化</td></tr><tr><td>18~24s</td><td>林晚晴挥动银针，周围敌人被束缚</td><td>银光刺耳音效</td><td>「东方法术，解锁潜能」</td><td>展示中式医术与法术特色</td></tr><tr><td>24~30s</td><td>朱利安操作机械装置，齿轮与蒸汽飞旋</td><td>蒸汽喷发+机械碰撞声</td><td>「科学与炼金，突破常理」</td><td>强化蒸汽朋克科技感</td></tr><tr><td>30~36s</td><td>外滩拍卖会混乱场景，血族与主角团激烈交锋</td><td>快节奏打击音效</td><td>「权力与血脉的较量」</td><td>强化冲突与悬疑感</td></tr><tr><td>36~42s</td><td>血月下，民众惊恐四散，报纸飞舞，血族身影掠过</td><td>响亮尖叫声</td><td>「城市之上，暗流涌动」</td><td>引发民族情绪与都市危机感</td></tr><tr><td>42~48s</td><td>快速闪回：蜃楼舟升空、伊斯坦布尔穹顶、巴黎铁塔、昆仑遗迹</td><td>史诗鼓+弦乐加速</td><td>「东西方文明交汇，神秘力量苏醒」</td><td>展示世界冒险范围，视觉冲击</td></tr><tr><td>48~54s</td><td>各角色特写：陈少秋、林晚晴、朱利安、阿伊莎、陆明夷</td><td>音效停顿+心跳</td><td>「他们的命运，将改变世界」</td><td>人物塑造、激发玩家代入</td></tr><tr><td>54~60s</td><td>急速剪辑：血族袭击、蜃楼舟起飞、娲术闪光、血液瞬间蔓延</td><td>大型爆炸音+低沉合唱</td><td>「你，准备好了吗？」</td><td>高潮收束，呼应PV主题</td></tr><tr><td>60~66s</td><td>黑屏，女娲符号亮起，蒸汽与红光交错</td><td>回音+残响</td><td>「蒸汽妖都，神遗重生」</td><td>标题收尾，强化品牌记忆</td></tr><tr><td>66~72s</td><td>游戏LOGO + 预约/发售信息</td><td>激昂史诗音</td><td>「即刻踏入东方蒸汽传奇」</td><td>转化玩家兴趣为行动</td></tr></tbody></table>',20)])])}const c=l(i,[["render",o]]);export{_ as __pageData,c as default};
