import{_ as d,o as a,c as e,ag as h}from"./chunks/framework.Bx1bucHt.js";const c=JSON.parse('{"title":"《神遗：蒸汽妖都》PV 分镜脚本（完整版）","description":"","frontmatter":{},"headers":[],"relativePath":"项目实践/JRPG/PV动画/《神遗：蒸汽妖都》PV分镜脚本（完整版）.md","filePath":"项目实践/JRPG/PV动画/《神遗：蒸汽妖都》PV分镜脚本（完整版）.md"}'),r={name:"项目实践/JRPG/PV动画/《神遗：蒸汽妖都》PV分镜脚本（完整版）.md"};function o(l,t,i,n,b,s){return a(),e("div",null,[...t[0]||(t[0]=[h('<h1 id="《神遗-蒸汽妖都》pv-分镜脚本-完整版" tabindex="-1">《神遗：蒸汽妖都》PV 分镜脚本（完整版） <a class="header-anchor" href="#《神遗-蒸汽妖都》pv-分镜脚本-完整版" aria-label="Permalink to &quot;《神遗：蒸汽妖都》PV 分镜脚本（完整版）&quot;">​</a></h1><h2 id="核心原则" tabindex="-1">核心原则 <a class="header-anchor" href="#核心原则" aria-label="Permalink to &quot;核心原则&quot;">​</a></h2><p>所有镜头均基于游戏内基础功能（跑图跟随视角、战斗默认视角、立绘对话界面、地图 UI），无额外制作素材；7 个模块按 “世界观→角色→战斗→使命→收尾” 逻辑串联，强化叙事连贯性。</p><h2 id="【模块-1-跑图剪辑-世界观语音】-0-00-0-15" tabindex="-1">【模块 1：跑图剪辑 + 世界观语音】（0:00-0:15） <a class="header-anchor" href="#【模块-1-跑图剪辑-世界观语音】-0-00-0-15" aria-label="Permalink to &quot;【模块 1：跑图剪辑 + 世界观语音】（0:00-0:15）&quot;">​</a></h2><table tabindex="0"><thead><tr><th>镜头</th><th>画面内容（游戏内跑图视角）</th><th>音效 / 台词（世界观介绍）</th><th>资产来源（全游戏内）</th></tr></thead><tbody><tr><td>1</td><td>【跟随跑图】陈少秋从码头跑向陈公馆：镜头跟随主角移动，途经蒸汽路灯（冒白雾）、斧头帮黑市（喽啰巡逻），地面偶尔闪过 “双蛇螺旋” 光斑</td><td>脚步声 + 蒸汽排气声旁白（低沉）：“女娲补天遗留的灵石，藏着‘娲术’的秘密 —— 那是能编撰基因、融合万物的神技”</td><td>码头→外滩→陈公馆连贯地图素材 + 跑图跟随镜头</td></tr><tr><td>2</td><td>【场景切换】快速跑图剪辑（每场景 2 秒）：1. 陈公馆实验室（跑过摆放妖兽骨粉的货架）2. 杨树浦工业区（跑过蒸汽管道，管道泄漏白烟）3. 徐光启墓地下迷宫（跑过刻有娲术纹的石壁）</td><td>货架碰撞声 + 管道泄漏声旁白（急促）：“有人用娲术改造血脉，诞生了以血为食的血族；也有人妄图用它强国，却引发了基因变异的灾难 —— 血月之夜，妖雾已笼罩上海”</td><td>实验室 / 工业区 / 地下迷宫地图素材 + 跑图加速镜头</td></tr></tbody></table><h2 id="【模块-2-立绘混剪-角色展示】-0-16-0-28" tabindex="-1">【模块 2：立绘混剪 + 角色展示】（0:16-0:28） <a class="header-anchor" href="#【模块-2-立绘混剪-角色展示】-0-16-0-28" aria-label="Permalink to &quot;【模块 2：立绘混剪 + 角色展示】（0:16-0:28）&quot;">​</a></h2><table tabindex="0"><thead><tr><th>镜头</th><th>画面内容（立绘 + 实景组合）</th><th>音效 / 台词（角色介绍）</th><th>资产来源（全游戏内）</th></tr></thead><tbody><tr><td>3</td><td>【立绘单切】每个角色立绘依次弹出（配角色专属动作）：1. 陈少秋（举枪瞄准，蓝光环绕）2. 林晚晴（持针抬手，白光闪烁）3. 朱利安（转动齿轮，金光飞溅）4. 阿伊莎（展开古卷，棕光浮现）</td><td>立绘弹出音效（各角色不同）角色台词（依次）：陈少秋：“以科技承娲术，而非滥用”林晚晴：“银针既能救人，亦能御敌”朱利安：“达芬奇的图纸里，藏着反制血族的钥匙”阿伊莎：“中东古文字，记载着娲术的起源”</td><td>角色对话立绘素材 + 技能光效素材（叠加）</td></tr><tr><td>4</td><td>【立绘组合】4 人立绘拼成 “菱形站位”（背景为轩辕号甲板实景），立绘边缘有蒸汽粒子环绕</td><td>蒸汽粒子声旁白：“归国学子、精武传人、天才工程师、古文字学者 —— 为了不同的信念，他们踏上同一条路”</td><td>角色组队立绘模板 + 轩辕号实景地图</td></tr></tbody></table><h2 id="【模块-3-实景战斗混剪-信念展示】-0-29-0-45" tabindex="-1">【模块 3：实景战斗混剪 + 信念展示】（0:29-0:45） <a class="header-anchor" href="#【模块-3-实景战斗混剪-信念展示】-0-29-0-45" aria-label="Permalink to &quot;【模块 3：实景战斗混剪 + 信念展示】（0:29-0:45）&quot;">​</a></h2><table tabindex="0"><thead><tr><th>镜头</th><th>画面内容（游戏内战斗视角）</th><th>音效 / 台词（信念 + 价值观）</th><th>资产来源（全游戏内）</th></tr></thead><tbody><tr><td>5</td><td>【战斗快剪】每个角色 1 段标志性战斗画面（每段 2 秒）：1. 陈少秋：“灵枢冲击者” 电击模式，电流击飞 3 个血仆（战斗默认视角）2. 林晚晴：“百草清瘴” 群体回复，白光覆盖队友（战斗技能视角）3. 朱利安：“特斯拉电涌” 范围麻痹，铁手僵直（BOSS 战视角）4. 阿伊莎：匕首刺杀魅影，身影隐入阴影（刺客技能视角）</td><td>战斗音效（依次增强）角色耍帅台词（同步）：陈少秋：“力量不是为了统治，是为了守护”林晚晴：“医者仁心，但对恶者从不手软”朱利安：“机械是工具，善恶在使用者”阿伊莎：“阴影里的刀刃，只为真相出鞘”</td><td>角色战斗动画片段（截取核心帧）</td></tr><tr><td>6</td><td>【反派魅惑展示】苏菲亚战斗特写：红唇轻笑，声波 “猩红咏叹” 扩散，队友 HP 小幅下降（战斗 UI 显示 “精神攻击”）</td><td>声波震动声 + 苏菲亚低笑苏菲亚（魅惑）：“你们的信念… 在永生面前不堪一击”</td><td>BOSS 战斗动画片段 + 战斗 UI 素材</td></tr></tbody></table><h2 id="【模块-4-游戏地图全景-组队-反派介绍】-0-46-1-00" tabindex="-1">【模块 4：游戏地图全景 + 组队 + 反派介绍】（0:46-1:00） <a class="header-anchor" href="#【模块-4-游戏地图全景-组队-反派介绍】-0-46-1-00" aria-label="Permalink to &quot;【模块 4：游戏地图全景 + 组队 + 反派介绍】（0:46-1:00）&quot;">​</a></h2><table tabindex="0"><thead><tr><th>镜头</th><th>画面内容（地图 UI + 实景）</th><th>音效 / 台词（使命 + 反派）</th><th>资产来源（全游戏内）</th></tr></thead><tbody><tr><td>7</td><td>【地图全景】游戏世界地图从 “上海” 放大到 “伊斯坦布尔→佛罗伦萨→昆仑墟”，每个地点标注关键道具（上海：青铜方尊 / 伊斯坦布尔：圣索菲亚石刻）</td><td>地图缩放音效旁白：“从蒸汽妖都到女娲遗迹，他们的使命，是找到娲术的真相 —— 阻止灾难扩散，还是接受基因的进化？”</td><td>世界地图 UI 素材 + 关键道具图标（叠加）</td></tr><tr><td>8</td><td>【组队过程】实景剪辑角色相遇瞬间：1. 码头陈少秋救林晚晴（复用剧情过场）2. 拍卖行朱利安抛烟雾弹救场（复用战斗过场）3. 地下迷宫阿伊莎解机关（复用解密过场）</td><td>剧情音效（脚步声 / 烟雾弹声）陈少秋（坚定）：“我们或许来路不同，但目标一致”</td><td>剧情过场动画片段（截取）</td></tr><tr><td>9</td><td>【反派介绍】反派立绘组合（宋先生居中，铁手 / 苏菲亚分列两侧，背景为血族据点实景）</td><td>红雾扩散声宋先生（阴冷）：“娲术应归血族掌控，重塑世界秩序 —— 你们，不过是阻碍”</td><td>反派立绘素材 + 血族据点地图</td></tr></tbody></table><h2 id="【模块-5-战斗特色介绍】-1-01-1-10" tabindex="-1">【模块 5：战斗特色介绍】（1:01-1:10） <a class="header-anchor" href="#【模块-5-战斗特色介绍】-1-01-1-10" aria-label="Permalink to &quot;【模块 5：战斗特色介绍】（1:01-1:10）&quot;">​</a></h2><table tabindex="0"><thead><tr><th>镜头</th><th>画面内容（战斗 UI + 实景）</th><th>音效 / 台词（特色说明）</th><th>资产来源（全游戏内）</th></tr></thead><tbody><tr><td>10</td><td>【特色展示】3 个战斗机制快速切换：1. 娲术共鸣：陈少秋激活娲石，队友技能图标发亮（战斗 UI 显示 “伤害 + 20%”）2. 灵枢重构：林晚晴施针，敌人 “魅影” 变 “变异魅影”（战斗模型变化）3. 战术分析：陆明夷调出电报密码，敌人防御条闪烁（战斗 UI 显示 “防 - 15%”）</td><td>技能特效声 + UI 提示音旁白：“娲术共鸣强化队友、灵枢重构反噬敌人、战术分析破解弱点 —— 这是科技与神技结合的战斗体系”</td><td>战斗 UI 素材 + 技能特效动画</td></tr></tbody></table><h2 id="【模块-6-游戏-logo-发售消息】-1-11-1-18" tabindex="-1">【模块 6：游戏 logo + 发售消息】（1:11-1:18） <a class="header-anchor" href="#【模块-6-游戏-logo-发售消息】-1-11-1-18" aria-label="Permalink to &quot;【模块 6：游戏 logo + 发售消息】（1:11-1:18）&quot;">​</a></h2><table tabindex="0"><thead><tr><th>镜头</th><th>画面内容（logo + 文字）</th><th>音效 / 台词（发售信息）</th><th>资产来源（全游戏内）</th></tr></thead><tbody><tr><td>11</td><td>【logo 展示】游戏中英文 logo（《神遗：蒸汽妖都》+ <em>Relics of the Divine: Steam Phantom Metropolis</em>）从蒸汽中浮现，背景为轩辕号升空实景</td><td>蒸汽鸣笛声 + logo 出现音效</td><td>游戏 logo 素材 + 轩辕号飞行动画</td></tr><tr><td>12</td><td>【发售消息】黑屏后弹出白色文字（复用游戏内对话字体）：“202X 年 Q4 启航测试开启”下方小字：“预约通道已开放 登录官网获取资格”</td><td>倒计时音效（3 声 “嘀”）旁白（激昂）：“蒸汽轰鸣，娲石闪耀 —— 这场跨越东西方的冒险，等你来加入！”</td><td>游戏内文字 UI 模板 + 官网预约界面截图（简化）</td></tr></tbody></table><h2 id="关键适配说明" tabindex="-1">关键适配说明 <a class="header-anchor" href="#关键适配说明" aria-label="Permalink to &quot;关键适配说明&quot;">​</a></h2><ol><li><p><strong>跑图镜头</strong>：复用游戏内 “主角跟随视角”，通过加速剪辑和场景切换避免单调，同时用地面光斑、蒸汽泄漏等动态元素强化氛围；</p></li><li><p><strong>战斗画面</strong>：全用游戏内默认战斗视角（俯拍 / 侧拍），截取 “技能释放→命中反馈” 的核心片段，不额外调整镜头角度；</p></li><li><p><strong>文字信息</strong>：发售消息、战斗特色说明均用游戏内已有对话字体，确保风格统一，降低制作成本。</p></li></ol><blockquote><p>（注：文档部分内容可能由 AI 生成）</p></blockquote>',18)])])}const P=d(r,[["render",o]]);export{c as __pageData,P as default};
