import{_ as a,o as i,c as r,ag as t}from"./chunks/framework.Bx1bucHt.js";const _=JSON.parse('{"title":"主城功能建筑设计","description":"","frontmatter":{},"headers":[],"relativePath":"项目实践/共斗游戏/主城/主城.md","filePath":"项目实践/共斗游戏/主城/主城.md"}'),e={name:"项目实践/共斗游戏/主城/主城.md"};function o(n,l,s,h,u,d){return i(),r("div",null,[...l[0]||(l[0]=[t('<h1 id="主城功能建筑设计" tabindex="-1">主城功能建筑设计 <a class="header-anchor" href="#主城功能建筑设计" aria-label="Permalink to &quot;主城功能建筑设计&quot;">​</a></h1><h2 id="核心功能建筑体系" tabindex="-1">核心功能建筑体系 <a class="header-anchor" href="#核心功能建筑体系" aria-label="Permalink to &quot;核心功能建筑体系&quot;">​</a></h2><h3 id="_1-天机阁-任务中枢" tabindex="-1">1. 天机阁（任务中枢） <a class="header-anchor" href="#_1-天机阁-任务中枢" aria-label="Permalink to &quot;1. 天机阁（任务中枢）&quot;">​</a></h3><ul><li>​<strong>​对标机制​</strong>​：怪物猎人集会所 + 讨鬼传鬼府任务板</li><li>​<strong>​建筑特色​</strong>​：九层八角塔楼，每层对应《山海经》一卷地域任务</li><li>​<strong>​交互设计​</strong>​： <ul><li>顶层悬「河图洛书」全息沙盘，触碰星象激活秘境任务</li><li>任务榜单采用甲骨文竹简形态，完成度达80%可解锁「大禹治水」隐藏副本</li></ul></li></ul><h3 id="_2-百工坊-装备打造" tabindex="-1">2. 百工坊（装备打造） <a class="header-anchor" href="#_2-百工坊-装备打造" aria-label="Permalink to &quot;2. 百工坊（装备打造）&quot;">​</a></h3><ul><li>​<strong>​复合功能​</strong>​： <ul><li>铸兵窟：青铜熔炉以「蚩尤旗」星象为能量源，打造时浮现星图特效</li><li>织云室：用文鳐鱼鳞织造防水护甲，帝江羽翼制作滑翔披风</li><li>琢玉轩：将山膏獠牙雕刻成诅咒饰品，附带「口吐恶言」嘲讽技能</li></ul></li><li>​<strong>​特色系统​</strong>​：投入「精卫石」可重铸装备五行属性</li></ul><h3 id="_3-归墟市集-商业中枢" tabindex="-1">3. 归墟市集（商业中枢） <a class="header-anchor" href="#_3-归墟市集-商业中枢" aria-label="Permalink to &quot;3. 归墟市集（商业中枢）&quot;">​</a></h3><ul><li>​<strong>​区域划分​</strong>​： <ul><li>西山坊：售卖丹穴山朱砂（强化符咒）、昆仑玉英（提升灵气恢复）</li><li>东海铺：交易鲛人泪（水下呼吸药剂）、龙绡衣（抗火装备）</li><li>幽冥栈：深夜限时开放，用「鬼车羽」兑换冥器（对灵体特攻）</li></ul></li></ul><h2 id="角色关联建筑" tabindex="-1">角色关联建筑 <a class="header-anchor" href="#角色关联建筑" aria-label="Permalink to &quot;角色关联建筑&quot;">​</a></h2><h3 id="_4-栖云居-自宅系统" tabindex="-1">4. 栖云居（自宅系统） <a class="header-anchor" href="#_4-栖云居-自宅系统" aria-label="Permalink to &quot;4. 栖云居（自宅系统）&quot;">​</a></h3><ul><li>​<strong>​定制化设计​</strong>​： <ul><li>地基可选「玄龟甲」（水宅）、「建木枝」（树屋）、「息壤」（地穴）</li><li>展示台可陈列夔牛皮鼓（提升战意）、九尾狐雕像（魅惑抗性）</li></ul></li><li>​<strong>​特殊机制​</strong>​：布置相柳九首烛台，夜间自动生成驱毒结界</li></ul><h3 id="_5-太卜司-角色成长" tabindex="-1">5. 太卜司（角色成长） <a class="header-anchor" href="#_5-太卜司-角色成长" aria-label="Permalink to &quot;5. 太卜司（角色成长）&quot;">​</a></h3><ul><li>​<strong>​功能融合​</strong>​：讨鬼传御魂系统 + 阴阳五行理论</li><li>​<strong>​核心玩法​</strong>​： <ul><li>星命占盘：将英招之角置于奎宿位，激活「风驰电掣」身法</li><li>魂魄熔炉：献祭「浑敦」残魂可获得「混沌护体」技能（免伤3秒）</li></ul></li></ul><h2 id="社交与生态建筑" tabindex="-1">社交与生态建筑 <a class="header-anchor" href="#社交与生态建筑" aria-label="Permalink to &quot;社交与生态建筑&quot;">​</a></h2><h3 id="_6-鲲鹏渡-跨服枢纽" tabindex="-1">6. 鲲鹏渡（跨服枢纽） <a class="header-anchor" href="#_6-鲲鹏渡-跨服枢纽" aria-label="Permalink to &quot;6. 鲲鹏渡（跨服枢纽）&quot;">​</a></h3><ul><li>​<strong>​视觉表现​</strong>​：码头停泊「巨鳌背岛」，龟甲刻有各服务器名称</li><li>​<strong>​交互设计​</strong>​： <ul><li>缴纳「鹏鸟金羽」可发起跨服远征</li><li>垂钓触发「北冥有鱼」事件，概率钓起其他玩家漂流瓶</li></ul></li></ul><h3 id="_7-万灵苑-异兽交互" tabindex="-1">7. 万灵苑（异兽交互） <a class="header-anchor" href="#_7-万灵苑-异兽交互" aria-label="Permalink to &quot;7. 万灵苑（异兽交互）&quot;">​</a></h3><ul><li>​<strong>​生态设计​</strong>​： <ul><li>雷池：圈养夔牛，挑战成功可获取「雷兽缰绳」坐骑</li><li>炎谷：投喂祸斗需使用「祝融符」，否则引发火灾事件</li></ul></li><li>​<strong>​繁殖系统​</strong>​：当康（祥瑞）与朱厌（凶兆）杂交生成「吉凶兽」，每日运势随机</li></ul><h2 id="特色功能建筑" tabindex="-1">特色功能建筑 <a class="header-anchor" href="#特色功能建筑" aria-label="Permalink to &quot;特色功能建筑&quot;">​</a></h2><h3 id="_8-观星台-环境控制" tabindex="-1">8. 观星台（环境控制） <a class="header-anchor" href="#_8-观星台-环境控制" aria-label="Permalink to &quot;8. 观星台（环境控制）&quot;">​</a></h3><ul><li>​<strong>​动态系统​</strong>​： <ul><li>转动二十八宿浑天仪改变昼夜长短（夜长提升鬼系素材掉落）</li><li>观测「荧惑守心」天象时，全城武器自动附加火属性</li></ul></li></ul><h3 id="_9-饕餮鼎-社交宴饮" tabindex="-1">9. 饕餮鼎（社交宴饮） <a class="header-anchor" href="#_9-饕餮鼎-社交宴饮" aria-label="Permalink to &quot;9. 饕餮鼎（社交宴饮）&quot;">​</a></h3><ul><li>​<strong>​共斗机制​</strong>​： <ul><li>投入异兽食材触发「钟鸣鼎食」BUFF（全属性+15%）</li><li>烹饪「巴蛇羹」需4人协作：切脍/控火/调味/镇压怨魂</li></ul></li><li>​<strong>​黑暗料理​</strong>​：错误配方可能召唤「饕餮残影」突袭宴会</li></ul><h2 id="防御工事体系" tabindex="-1">防御工事体系 <a class="header-anchor" href="#防御工事体系" aria-label="Permalink to &quot;防御工事体系&quot;">​</a></h2><h3 id="_10-不周柱-主城防护" tabindex="-1">10. 不周柱（主城防护） <a class="header-anchor" href="#_10-不周柱-主城防护" aria-label="Permalink to &quot;10. 不周柱（主城防护）&quot;">​</a></h3><ul><li>​<strong>​动态防御​</strong>​： <ul><li>共工残魂每周攻城，击退可获取「天柱碎片」</li><li>镶嵌碎片激活「四极结界」：青龙门（东）抗物理，白虎门（西）破妖法</li></ul></li><li>​<strong>​破城事件​</strong>​：结界破碎时主城地形转为「天倾西北」状态，需要修补地脉</li></ul><blockquote><p>设计要诀：<br> ① 将《山海经》生物特性转化为交互元素（如文鳐鱼铺路形成「夜光道」）<br> ② 重要NPC设定为上古神人异化形态（西王母保留虎齿豹尾特征）<br> ③ 建筑功能与古籍记载形成暗合（「天机阁」高度对应「绝地天通」传说）<br> ④ 保留怪物猎人的装备驱动内核，但素材获取需结合星象/生态知识</p></blockquote>',27)])])}const g=a(e,[["render",o]]);export{_ as __pageData,g as default};
