import{_ as l,o as i,c as a,ag as o}from"./chunks/framework.Bx1bucHt.js";const g=JSON.parse('{"title":"《玄罡录》长篇剧情架构设计","description":"","frontmatter":{},"headers":[],"relativePath":"项目实践/漫画/新大纲.md","filePath":"项目实践/漫画/新大纲.md"}'),r={name:"项目实践/漫画/新大纲.md"};function e(d,t,n,s,u,h){return i(),a("div",null,[...t[0]||(t[0]=[o('<h1 id="《玄罡录》长篇剧情架构设计" tabindex="-1">《玄罡录》长篇剧情架构设计 <a class="header-anchor" href="#《玄罡录》长篇剧情架构设计" aria-label="Permalink to &quot;《玄罡录》长篇剧情架构设计&quot;">​</a></h1><h2 id="世界观设定" tabindex="-1">世界观设定 <a class="header-anchor" href="#世界观设定" aria-label="Permalink to &quot;世界观设定&quot;">​</a></h2><ul><li><p>​<strong>核心背景</strong>​</p><ul><li>上古女娲补天遗留秘术&quot;娲术&quot;，可造人改命，亦可扭曲生灵</li><li>生物改造组织&quot;九婴&quot;操控《山海经》异兽基因，制造十二凶煞精英</li><li>玄罡卫（五行职阶）VS 九婴（生物科技）的千年对抗</li></ul></li><li><p>​<strong>核心冲突</strong>​</p><ul><li>补天石碎片：封印混沌的关键道具，九婴企图集齐复活灭世凶兽</li><li>娲术代价：使用禁术需折寿/失忆/肢体异化</li></ul></li></ul><h2 id="分章结构与话数规划" tabindex="-1">分章结构与话数规划 <a class="header-anchor" href="#分章结构与话数规划" aria-label="Permalink to &quot;分章结构与话数规划&quot;">​</a></h2><h3 id="第一阶段-起源篇-1-50话" tabindex="-1">第一阶段：起源篇（1-50话） <a class="header-anchor" href="#第一阶段-起源篇-1-50话" aria-label="Permalink to &quot;第一阶段：起源篇（1-50话）&quot;">​</a></h3><ol><li><p>​<strong>开篇事件</strong>​（1-10话）</p><ul><li>边陲村丧尸化事件（娲术失控）</li><li>墨阳继承断岳刀法，师父牺牲遗言&quot;补天石在昆仑墟&quot;</li></ul></li><li><p>​<strong>阵营初现</strong>​（11-30话）</p><ul><li>玄罡卫五行试炼（金木水火土职阶觉醒）</li><li>九婴首次投放改造体&quot;饕餮&quot;（吞噬血肉特性）</li></ul></li><li><p>​<strong>小队成型</strong>​（31-50话）</p><ul><li>核心成员集结：墨阳（金）、凌鸢（火）、白羿（水）、青溟（木）</li><li>揭露朝廷与九婴勾结的&quot;红丸案&quot;</li></ul></li></ol><h3 id="第二阶段-凶煞讨伐篇-51-180话" tabindex="-1">第二阶段：凶煞讨伐篇（51-180话） <a class="header-anchor" href="#第二阶段-凶煞讨伐篇-51-180话" aria-label="Permalink to &quot;第二阶段：凶煞讨伐篇（51-180话）&quot;">​</a></h3><ol><li><p>​<strong>单元剧结构</strong>​（每20话击破一名凶煞）</p><ul><li>穷奇篇（51-70话）：飞行系改造体，引发空城瘟疫事件</li><li>蛊雕篇（71-90话）：声波控制幻境，玄罡卫内鬼暴露</li><li>狰篇（91-110话）：再生能力+岩浆地形，白羿堕入影渊</li></ul></li><li><p>​<strong>技能升级节点</strong></p><ul><li>断岳刀法→天罡九斩（金系最终技）</li><li>影遁术→虚空步（水系空间跳跃）</li><li>星陨箭术→朱雀焚天（火系AOE）</li></ul></li><li><p>​<strong>感情线爆发</strong></p><ul><li>墨阳发现凌鸢是九婴叛逃者后代（101话）</li><li>青溟为救白羿启用木系禁术&quot;枯木逢春&quot;（137话）</li></ul></li></ol><h3 id="第三阶段-终局篇-181-300话" tabindex="-1">第三阶段：终局篇（181-300话） <a class="header-anchor" href="#第三阶段-终局篇-181-300话" aria-label="Permalink to &quot;第三阶段：终局篇（181-300话）&quot;">​</a></h3><ol><li><p>​<strong>终极战场</strong>​（181-250话）</p><ul><li>昆仑墟娲皇遗迹解密（上古妖兽守护试炼）</li><li>九婴首领召唤混沌本体（触手状能量生命体）</li></ul></li><li><p>​<strong>牺牲与封印</strong></p><ul><li>墨阳融合补天石自爆封印（287话）</li><li>凌鸢燃烧娲族血脉重塑结界（292话）</li></ul></li><li><p>​<strong>尾声留白</strong></p><ul><li>幸存的青溟/白羿隐退南海（298话）</li><li>朝廷发布《禁娲术诏》（299话）</li><li>最后一块补天石碎片沉入归墟（300话）</li></ul></li></ol><p>反派架构​ 十二凶煞分三阶：地煞（4体）、天煞（6体）、灭世双煞（混沌载体+九婴首领）<br> 关键道具​ 补天石碎片附带负面效果（佩戴者会听到混沌低语）<br> ​场景设计​ 结合中国地理：昆仑雪山、南海归墟、蜀中毒瘴林</p><h2 id="整体架构规划" tabindex="-1">整体架构规划 <a class="header-anchor" href="#整体架构规划" aria-label="Permalink to &quot;整体架构规划&quot;">​</a></h2><table tabindex="0"><thead><tr><th>篇章阶段</th><th>话数范围</th><th>核心目标</th><th>与后续篇章关联性</th></tr></thead><tbody><tr><td>​<strong>起源篇</strong>​</td><td>1-50话</td><td>世界观奠基/主角团成型/九婴威胁初现</td><td>埋藏十二凶煞、混沌、娲术三大长线伏笔</td></tr><tr><td>​<strong>凶煞讨伐篇</strong>​</td><td>51-150话</td><td>分单元击破九婴势力/补天石碎片收集</td><td>揭露九婴与朝廷深层勾结</td></tr><tr><td>​<strong>阴谋篇</strong>​</td><td>151-220话</td><td>玄罡卫内部分裂/娲术禁地真相</td><td>混沌复苏倒计时启动</td></tr><tr><td>​<strong>终焉篇</strong>​</td><td>221-300话</td><td>混沌灭世战/牺牲与救赎</td><td>闭环世界观并留白续作可能</td></tr></tbody></table>',13)])])}const c=l(r,[["render",e]]);export{g as __pageData,c as default};
